css:
@-moz-document domain(docs.unity3d.com)
{
html,
div.content {
    background-color: #161616;
}

body,
h1,
h2 {
    color: #e6e4e4 !important;
}

h3, div.frontpage-section h3 a {
    color: #6a8199;
}

body {
    color: black;
}

content,
lbl,
ul li,
.tooltiptext {
    color: #eee !important;
}

.content h4 {
    color: #70a5ca !important;
}

#otherVersionsLegend {
    background-color: hsl(220, 24%, 14%);
    bottom: 0;
    position: -webkit-sticky;
    position: -moz-sticky;
    position: -ms-sticky;
    position: -o-sticky;
    position: sticky;
    width: 100%;
    cursor: initial;
}

div.toolbar {
    border-bottom: #000 0px solid;
    border-top: none;
}

div.lang-switcher div.lang-list,
div.otherversionscontent {
    position: absolute;
    background-color: white;
    width: 200px;
    max-height: 400px;
    font-size: medium;
    -webkit-box-shadow: 0 1px 5px rgba(0, 0, 0, 0.3);
    -moz-box-shadow: 0 1px 5px rgba(0, 0, 0, 0.3);
    box-shadow: 0 1px 5px rgba(0, 0, 0, 0.3);
    display: block;
}

div.otherversionscontent {
    border-top: #2c3a48 4px solid;
}

div.lang-switcher div.lang-list a {
    display: block;
    padding: 7px 10px;
    color: #e3e3e3;
    text-decoration: none;
}

div.lang-switcher div.current div.arrow {
    filter: invert(20%);
}

div.versionSwitcherArrow {
filter: invert(20%);
}

div.otherversionscontent a:hover {
    background: #394a5d;
}

div.otherversionscontent a {
    color: #fff;
}

div.sidebar-menu ul li a.current {
    color: #fff;
    font-weight: bold;
    text-decoration: underline;
}

div.sidebar-menu ul li a {
    display: block;
    color: #eee;
    word-wrap: break-word;
    text-decoration: none;
}

div.sidebar-menu ul li div.arrow {
    border: 0;
    background-color: #469b79;
}

::selection {
    background: #c3c3c4;
    color: #fff;
    text-shadow: none;
}

.nextprev,
.toolbar,
div.toolbar div.content {
    background: #1a1f23 !important;
}

.mCSB_scrollTools .mCSB_dragger .mCSB_dragger_bar {
    background: #6d43af !important;
}

.leafnode.expanded {
    background-image: none !important;
}

.mCSB_scrollTools .mCSB_draggerRail {
    background: #222c37;
}

.filler {
    background: #00aeae !important;
}

.feedbackbox,
div.lang-switcher div.lang-list,
.timetoshineitem,
.feedbackbox {
    background: #1a1f23 !important;
}

li:before,
.nl {
    color: #fff !important;
}

li.vsDivHeader {
    border-left: 8px solid #1a1f23;
}

ol {
    color: #e4e4e4 !important;     
}
       
.filler a, 
.header a:link,
.menu {
    color: #fff !important;
}

div.message-error {
    background: #222c37;
}

.content .section table thead th {
    background: #222c37;
}

.tooltip .tooltiptext,
div.otherversionscontent {
    background-color: #222c37;
}

code {
    color: rgb(251, 231, 7);
    background-color: #161616;
}

.codeExampleCS,
.codeExampleJS {
    background-color: #212936;
    color: #fff;
}
table.list tr:hover {
    outline: rgba(40, 200, 200, .4) 1px solid;
}

td,
td.desc {
    border-style: solid;
    border-width: 1px;
    border-color: #9e488d;
    color: #fff;
    background-color: #1b1d22;
}

.content .section table thead th, tr {
    background: #222C37 !important;
    color: #fff;
}

div.sidebar-menu ul li div.leafnode {
    border: 0;
    position: absolute;
    background-color: #16161600;
}

.vsDivHeader {
    background-color: #1a1f23 !important;
}

pre,
pre code {
    color: #fff;
    background-color: #212936 !important;
}

.doc-keyword {
    background: #fff;
    color: #000;
}
div.header div.search-form:before,
div.header .more .filler:before {
    background: none;
}
  
.navbar-default,
.footer,
.containerbody-content hide-when-search {
    background-color: #161616;
    border: 0px solid #000;
    color: #fff;
}

.navbar-default a,
.footer a {
    color: #fff;
}

.level1.nav a {
    color: #c2c0c0;
}

#wrapper {
    background-color: #161616;
}

.sidetoc,
.sidefilter,
.naw,
.toc {
    background-color: #161616 !important;

}
.sidetoc,
.sidefilter {
    border-width: 2px !important;
    border-color: #fff !important;
}

.toc .nav li a {
    color: #00c9ff;
}
.toc .nav li a:hover {
    text-decoration: underline;
}
.toc .nav li a:hover,
.toc .nav > li.active > a,
.affix > ul > li.active > a,
.affix > ul > li.active > a:before {
    color: #ce89c3;
}

.toc-filter {
    outline: none !important;
    border-radius: 3px;
    background: #fff;
    color: #e4e4e4 !important;
    padding: 2px;
    margin: 3px;
}
.toc-filter > input {
    color: #e4e4e4 !important;
}
.hljs {
    color: #fff;
}
.hljs-keyword {
    color: #222c37;
}

thead {
    background-color: #ff00b7;
}

.hljs-title
{
    color: #621dd0 !important;
}

.hljs-number
{
    color: #00c9ff !important;
}

.hljs-string
{
    color: #ff2424 !important;
}
/*[[Color]]*/
}

style_setting_options.value:
input[type="text"] {
    background-color: #242424;
}

div.header div.search-form input.field:focus { 
    background: #242424 !important; 
}

div.header div.search-form input.submit, #mobileSearchBtn {
    background: url(https://docs.unity3d.com/StaticFilesManual/images/sprites.png) 5px -54px no-repeat;
    filter: invert(86%);
}

div.header div.search-form:before {
    content: '';
    width: 18px;
    height: 60px;
    display: block;
    position: absolute;
    top: 0;
    left: -17px;
    background: url(https://docs.unity3d.com/StaticFilesManual/images/shard-left.png) 0 0 no-repeat;
     filter: invert(86%);
}

div.header .more .filler:before {
    content: '';
    width: 18px;
    height: 60px;
    display: block;
    position: absolute;
    top: 0;
    left: -1px;
    background: url(https://docs.unity3d.com/StaticFilesManual/images/shard-right.png) 0 0 no-repeat;
     filter: invert(86%);
}

input[type="text"] {
    background-color: #fff;
}

div.header div.search-form input.field:focus { 
    background: #fff !important; 
}

div.header div.search-form:before {
    content: '';
    width: 18px;
    height: 60px;
    display: block;
    position: absolute;
    top: 0;
    left: -17px;
    background: url(https://docs.unity3d.com/StaticFilesManual/images/shard-left.png) 0 0 no-repeat;
}

div.header .more .filler:before {
    content: '';
    width: 18px;
    height: 60px;
    display: block;
    position: absolute;
    top: 0;
    left: -1px;
    background: url(https://docs.unity3d.com/StaticFilesManual/images/shard-right.png) 0 0 no-repeat;
}
