css:
@-moz-document domain("rimworldwiki.com") {
.mw-body, .parsoid-body {
    padding: 1em;
    background-color: #101010;
    color: #BDBDBD;
    direction: ltr;
}
h1, h2, h3, h4, h5, h6 {
    color: #BDBDBD;
    background: none;
    font-weight: normal;
    margin: 0;
    overflow: hidden;
    padding-top: 0.5em;
    padding-bottom: 0.17em;
    border-bottom: 1px solid #9BAED2;
}
.ambox th.mbox-text, .ambox td.mbox-text {
    padding: 0.25em 0.5em;
    color: #A3A3A3;
    background: #101010;
}
.ambox td.mbox-image {
    background: #101010;
    padding: 2px 0 2px 0.5em;
}
table.ambox {
    margin: 0px 10%;
    border: 1px solid #151515;
    border-left: 10px solid #9BAED2;
    background: #101010;
}
table.wikitable.sortable.jquery-tablesorter, thead, tr, th.headerSort, table.wiki{
    background: #101010;
    color: #A3A3A3;
}
.external a, .external a:link, .external a:active, .external a:visited, a, a:link, a:active, a:visited {
    color: #9BAED2 !important;
    text-decoration: none;
}
div.nav .heading {
    font-family: EasonPro,"Times New Roman",serif !important;
    font-size: 1.417em !important;
    color: #A3A3A3 !important;
    background-color: #151515;
    line-height: 100%;
    margin: -1px -1px 0;
    padding: .5em 0;
    text-shadow: rgba(0,0,0,0.75) 0 0 0.25em;
}
div.nav dt {
    float: left;
    text-align: right;
    width: 125px;
    clear: left;
    border-right: 1px solid #9BAED2;
    background: #101010;
    margin: 0 -2px 0 0;
    padding: 2px 8px;
}
div.nav.wide dt {
    background: #101010;
    border-top: 1px solid #9BAED2;
    border-left: 1px solid #9BAED2;
    border-bottom-left-radius: 150px;
    width: 150px;
}
div.nav .box {
    position: relative;
    font-size: .9230769231em;
    border-spacing: 0;
    overflow: visible;
    min-height: 60px;
    border: 0px solid #fff0;
    background: #101010;
    margin: 5px;
    padding: 0;
}
div.nav dd {
    float: left;
    text-align: left;
    border-left: #9BAED2 solid 2px;
    border-top: 1px solid #9BAED2;
    border-right: 1px solid #9BAED2;
    border-bottom: 1px solid #9BAED2;
    width: 325px;
    margin: 0;
    padding: 2px 8px;
    background: #101010;
}
body {
    background-color: #101010;
}
#mw-head {
    position: absolute;
    top: 0;
    right: 0;
    width: 100%;
    background: #101010;
    border-bottom: 1px solid #9BAED2;
}
.vectorTabs li a {
    display: block;
    height: 1.9em;
    padding-left: 0.615em;
    padding-right: 0.615em;
    color: #9BAED2;
    background: #101010;
    cursor: pointer;
    font-size: 0.8125em;
}
#simpleSearch input {
    background-color: #101010;
    color: #A3A3A3;
    margin: 0;
    padding: 0;
    border: 0;
}
#simpleSearch {
    background-position: top left;
    background-repeat: repeat-x;
    color: #A3A3A3;
    background-color: #101010;
    display: block;
    width: 12.6em;
    width: 20vw;
    min-width: 5em;
    max-width: 20em;
    padding-right: 1.4em;
    height: 1.4em;
    margin-top: 0.65em;
    position: relative;
    min-height: 1px;
    border: 1px solid #9BAED2;
    border-radius: 2px;
    -webkit-transition: border-color 250ms;
    -moz-transition: border-color 250ms;
    transition: border-color 250ms;
}
div.infobox .image_wrapper {
    width: 250px;
    height: 250px;
    display: table-cell;
    vertical-align: middle;
    text-align: center;
    padding: 0;
    margin: 0;
    background: #101010;
    border-bottom: 1px solid #9BAED2;
    border-top: 1px solid #9BAED2;
}
.infobox {
    border: 1px solid #9BAED2;
    background-color: #101010;
    color: #A3A3A3;
    margin: 0.5em 0 0.5em 1em;
    padding: 0.2em;
    float: right;
    clear: right;
}
.mw-body-content p {
    line-height: inherit;
    margin: 0.5em 0;
    background-color: #101010;
    color: #A3A3A3;
}
div.infobox .wrapper, div.version_nav .wrapper {
    clear: both;
    padding: 0;
    background: #101010;
}
div.infobox .heading {
    font-family: EasonPro,"Times New Roman",serif!important;
    font-size: 1.417em!important;
    color: #A3A3A3!important;
    background-color: #101010;
    text-shadow: rgba(0,0,0,0.75) 0 0 0.25em;
    line-height: 1.176em;
    font-size-adjust: .43;
    padding: .5em!important;
}
div.infobox {
    float: right;
    position: relative;
    width: 250px;
    background: #101010;
    font-size: .923em;
    border: 1px solid #9BAED2;
    margin: 0 .5em 1em .5em;
    padding: 0;
}
#catlinks {
    text-align: left;
    background-color: #101010;
}
div.infobox dd, div.version_nav dd {
    float: left;
    width: 145px;
    font-weight: 400;
    border-left: 2px solid #9BAED2;
    margin: 0;
    padding: 2px 7px;
}
hr {
    height: 1px;
    color: #9BAED2;
    background-color: #9BAED2;
    border: 0;
    margin: 0.2em 0;
}
#footer ul li {
    color: #A3A3A3;
    margin: 0;
    padding: 0;
    padding-top: 0.5em;
    padding-bottom: 0.5em;
    font-size: 0.7em;
}
#mw-panel .portal h3 {
    font-size: 0.75em;
    color: #A3A3A3;
    font-weight: normal;
    margin: 0.5em 0 0 0.66666667em;
    padding: 0.25em 0;
    cursor: default;
    border: 0;
}
#editform, #toolbar, #wpTextbox1 {
    clear: both;
    color: #A3A3A3;
    background: #101010;
    border: 1px solid #9BAED2;
}
div.mw-lag-warn-normal, div.noarticletext, div.fmbox-system {
    clear: both;
    margin: 0.2em 0;
    border: 1px solid #9BAED2;
    background: #101010;
    padding: 0.25em 0.9em;
}
.mw-body, #mw-head-base, #left-navigation, #footer {
    margin-left: 11em;
    background: #101010;
}
#mw-page-base {
    height: 5em;
    background-position: bottom left;
    background-repeat: repeat-x;
    background-color: #101010;
    background-image: linear-gradient(#151515 50%,#101010 100%);
}
#bodyContent .mw-number-text h3 {
    color: #A3A3A3;
    margin: 0;
    padding: 0;
    font-family: 'Linux Libertine','Georgia','Times',serif;
    font-weight: normal;
    font-size: 2.2em;
    line-height: 1.2;
    text-align: center;
}
.mw-ui-input {
    background-color: #101010;
    color: #A3A3A3;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    display: block;
    width: 100%;
    border: 1px solid #9BAED2;
    border-radius: 2px;
    padding: 0.57142857em 0.57142857em 0.5em;
    box-shadow: inset 0 0 0 0.1em #9BAED2;
    font-family: inherit;
    font-size: inherit;
    line-height: 1.07142857em;
    vertical-align: middle;
}
#mw-createaccount-join {
    border-radius: 50px;
    background-color: #101010;
    color: #9BAED2;
}
#mw-createaccount-join:hover {
    background-color: #101010;
    border-color: #9BAED2;
    box-shadow: none;
}
.mw-rcfilters-ui-filterTagMultiselectWidget-wrapper-top-queryName {
    flex: 1 1 auto;
    padding: 0.6em 0;
    white-space: nowrap;
    min-width: 0;
    background-color: #101010;
    color: #A3A3A3;
    overflow: hidden;
    text-overflow: ellipsis;
}
.mw-rcfilters-ui-filterTagMultiselectWidget-cell-filters div.oo-ui-tagMultiselectWidget-group {
    margin-top: 0.2em;
    display: block;
    background: #101010;
}
.oo-ui-tagItemWidget.oo-ui-widget-enabled .oo-ui-buttonElement-button .oo-ui-icon-close {
    left: 0.21428571em;
    min-width: 14px;
    width: 1em;
}
.oo-ui-tagItemWidget.oo-ui-labelElement:not(.oo-ui-tagItemWidget-fixed) .oo-ui-labelElement-label {
    cursor: text;
    background: #101010;
    color: #A3A3A3;
}
.oo-ui-tagMultiselectWidget.oo-ui-widget-enabled.oo-ui-tagMultiselectWidget-outlined .oo-ui-tagItemWidget.oo-ui-widget-enabled {
    background-color: #101010;
}
.oo-ui-menuSelectWidget {
    position: absolute;
    width: 100%;
    z-index: 4;
    background-color: #101010;
    margin-top: -1px;
    margin-bottom: -1px;
    border: 1px solid #9BAED2;
    border-radius: 0 0 2px 2px;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.25);
}
.mw-rcfilters-ui-filterMenuHeaderWidget-header-title {
    position: relative;
    width: 100%;
    vertical-align: middle;
    background: #101010;
    color: #A3A3A3;
}
.oo-ui-labelElement .oo-ui-labelElement-label {
    line-height: 1.42857143em;
    color: #A3A3A3;
}
.mw-rcfilters-ui-filterMenuHeaderWidget-header-invert, .mw-rcfilters-ui-filterMenuHeaderWidget-header-highlight {
    min-width: 1em;
    vertical-align: middle;
    padding-right: 0.85714286em;
    background: #101010;
}
.oo-ui-buttonElement.oo-ui-labelElement > input.oo-ui-buttonElement-button, .oo-ui-buttonElement.oo-ui-labelElement > .oo-ui-buttonElement-button > .oo-ui-labelElement-label {
    line-height: 1.28571429em;
    color: #A3A3A3;
}
.oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button {
    background-color: #101010;
    color: #A3A3A3;
    border-color: #9BAED2;
}
.oo-ui-buttonElement-frameless.oo-ui-iconElement > .oo-ui-buttonElement-button > .oo-ui-iconElement-icon {
    left: 0.35714286em;
    color: #A3A3A3;
}
.oo-ui-buttonSelectWidget.oo-ui-widget-enabled .oo-ui-buttonElement.oo-ui-widget-enabled > .oo-ui-buttonElement-button:hover, .oo-ui-buttonSelectWidget.oo-ui-widget-enabled .oo-ui-buttonElement.oo-ui-widget-enabled > .oo-ui-buttonElement-button:active {
    z-index: 1;
    background: #101010;
}
.oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button:active, .oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button:active:focus, .oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-buttonElement-pressed > .oo-ui-buttonElement-button {
    background-color: #101010;
    color: #A3A3A3;
    border-color: #9BAED2;
    box-shadow: none;
}
.oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-buttonElement-active > .oo-ui-buttonElement-button {
    background-color: #101010;
    color: #A3A3A3;
    border-color: #9BAED2;
}
.mw-rcfilters-ui-filterTagMultiselectWidget-views-select-widget.oo-ui-widget {
    display: block;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    height: 2.5em;
    border: 1px solid #9BAED2;
    border-left-width: 0;
    border-radius: 0 0 2px 0;
    padding-right: 0.35714286em;
    text-align: right;
    white-space: nowrap;
    background: #101010;
}
.oo-ui-tagMultiselectWidget.oo-ui-widget-enabled.oo-ui-tagMultiselectWidget-outlined.oo-ui-flaggedElement-invalid .oo-ui-inputWidget-input {
    border-color: #d33;
}
.oo-ui-textInputWidget .oo-ui-inputWidget-input {
    background-color: #101010;
    color: #A3A3A3;
    margin: 0;
    border: 1px solid #9BAED2;
    border-radius: 2px;
    padding: 0.42857143em 0.57142857em;
    font-size: inherit;
    font-family: inherit;
    line-height: 1.28571429em;
}
.mw-rcfilters-ui-filterTagMultiselectWidget.oo-ui-widget-enabled .oo-ui-tagMultiselectWidget-handle {
    border: 1px solid #9BAED2;
    border-bottom: 0;
    background-color: #101010;
    border-radius: 2px 2px 0 0;
    padding: 0 0.6em 0.6em 0.6em;
    margin-top: 1em;
    line-height: normal;
}
.mw-rcfilters-ui-filterTagMultiselectWidget-cell-filters div.oo-ui-tagMultiselectWidget-group {
    margin-top: 0.2em;
    display: block;
    background: #101010;
}
.oo-ui-tagMultiselectWidget.oo-ui-widget-enabled.oo-ui-tagMultiselectWidget-outlined .oo-ui-tagMultiselectWidget-handle {
    background-color: #101010;
    border-bottom: 0;
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
}
.mw-rcfilters-ui-menuSelectWidget-footer-feedback {
    text-align: right;
    background: #101010;
    color: #A3A3A3;
}
.mw-rcfilters-ui-changesListWrapperWidget .mw-changeslist-legend {
    background-color: #101010;
    position: relative;
    z-index: 1;
    border: 1px solid #9BAED2;
}
.sitedir-ltr textarea, .sitedir-ltr input {
    direction: ltr;
    background: #101010;
    color: #A3A3A3;
    border: 1px solid #9BAED2;
}
select {
    -webkit-writing-mode: horizontal-tb !important;
    text-rendering: auto;
    color: #A3A3A3;
    letter-spacing: normal;
    word-spacing: normal;
    text-transform: none;
    text-indent: 0px;
    text-shadow: none;
    display: inline-block;
    text-align: start;
    -webkit-appearance: menulist;
    box-sizing: border-box;
    align-items: center;
    white-space: pre;
    -webkit-rtl-ordering: logical;
    background-color: #101010;
    cursor: default;
    margin: 0em;
    font: 400 13.3333px Arial;
    border-radius: 0px;
    border-width: 1px;
    border-style: solid;
    border-color: rgb(169, 169, 169);
    border-image: initial;
}
#pagehistory li.selected {
    background-color: #101010;
    color: #A3A3A3;
    border: 1px dashed #9BAED2;
}
#pagehistory li {
    border: 1px solid #9BAED2;
}
li {
    margin-bottom: 0.1em;
    background: #101010;
    color: #A3A3A3;
}
.toc .toctitle {
    text-align: center;
    background: #101010;
    color: #A3A3A3;
}
.toc, .mw-warning, .toccolours {
    border: 1px solid #9BAED2;
    background-color: #101010;
    padding: 5px;
    font-size: 95%;
}
.mw-content-ltr .tocnumber {
    padding-left: 0;
    padding-right: 0.5em;
    color: #A3A3A3;
}
.mw-body {
    margin-left: 10em;
    border: 1px solid #9BAED2;
    border-right-width: 0;
    margin-top: -1px;
}
.vectorTabs {
    float: left;
    height: 2.5em;
    background-position: bottom left;
    background-repeat: no-repeat;
    padding-left: 1px;
    border-left: 1px solid #9BAED2;
    border-top: 1px solid #9BAED2;
    border-right: 1px solid #9BAED2
}
.mw-search-profile-tabs {
    background-color: #101010;
    margin-top: 1em;
    border: 1px solid #9BAED2;
    border-radius: 2px;
}
.smwb-theme-light .smwb-propval {
    background-color: #101010;
}
.smwb-theme-light .smwb-propval {
    background-color: #101010;
}
.smwb-propval {
    background-color: #101010;
}
.smwb-theme-light .smwb-title, .smwb-theme-light .smwb-center, .smwb-theme-light .smwb-actions, .smwb-theme-light .smwb-prophead {
    background-color: #101010;
}
.smwb-theme-light .smwb-factbox {
    border-left: 0.5em solid #9BAED2;
    background-color: #101010;
}
.smwb-factbox .smwb-cell {
    border: 0px solid #9BAED2;
    border-top: 2px solid #9BAED2;
}
.smwb-theme-light .smwb-ifactbox {
    border-right: 0.5em solid #9BAED2;
}
.smwb-ifactbox .smwb-cell {
    border: 0px solid #9BAED2;
    border-top: 2px solid #9BAED2;
}
.smwb-bottom {
    border-bottom: 2px solid #9BAED2;
}
}
