css:
@-moz-document domain(audible.com) {
body {
    background-color: #434242;
    color: #fff2ec;
}

.bc-color-link {
    color: #ffab73;
}

.bc-color-price {
    color: #ffab73;
}

.bc-color-base {
    color: #ebe0d9;
}

.bc-input,
textarea.bc-input {
    background-color: #565656;
    border-width: 1px;
    border-style: solid;
    border-radius: 2px;
    display: inline-block;
    height: 40px;
    line-height: 38px;
    font-size: 13px;
    -webkit-transition: all .1s linear;
    transition: all .1s linear;
    padding: 0 16px;
    padding-top: 0px;
    padding-right: 16px;
    padding-bottom: 0px;
    width: 100%;
}

.bc-color-secondary {
    color: rgba(255, 241, 237, .65);
}

.bc-box {
    border: none;
    background-color: #434242;
}

.bc-icon.bc-icon-size-small {
    font-size: 16px;
    width: 16px;
    color: #c1835b;
}

.bc-section {

    clear: both;
    overflow: visible;
    background-color: #434242;

}

.bc-color-secondary {
    color: rgba(255, 241, 237, .65);
    background-color: #434242;
}

.bc-list-horizontal {
    margin-left: 0;
    background-color: #434242;
}
}
