css:
@-moz-document domain("m.youtube.com") {
::-webkit-scrollbar {
    display: none;
}

header {
    height: 18px;
}

html {
    word-wrap: break-word;
    color: #fff;
    background-color: #111;
    -webkit-text-size-adjust: 100%;
}

.slim-video-metadata-info {
    font-size: 1.4rem;
    color: #aaa;
    padding: 12px;
    border-top: 1px solid hsla(0,0%,53.3%,.4);
}

.secondary-text {
    color: #aaa;
}

.button-renderer-icon {
    color: rgb(255, 0, 0);
}

.slim-video-metadata-actions .button-renderer-text {
    color: #ddd;
}

.player-container {
    position: absolute;
    top: 7px;
    right: 0;
    right: env(safe-area-inset-right);
    left: 0;
    left: env(safe-area-inset-left);
    overflow: hidden;
    z-index: 1;
    direction: ltr;
}

.player-container.sticky-player {
	top: 7px;
}

.player-size {
    position: relative;
    width: 100%;
    top: -20px;
    padding-bottom: 55%;
    overflow: hidden;
    background-color: #000;
}

.mobile-topbar-header[data-mode="watch"] {
    background-color: transparent; /* #222 */
}

.mobile-topbar-logo.ringo-logo {
    color: #fff;
}

ytm-pivot-bar-renderer {
    height: 40px;
    border-top:1px solid hsla(0,0%,53.3%,.4);
    background: #666;
    color: hsla(0,0%,6.7%,.6);
    font-size: 1.1rem;
}

.pivot-bar-item-title {
    color: #bbb;
}

ytm-header-bar.sticky-player.in, ytm-mobile-topbar-renderer.sticky-player.in {
    top: 0;
    transition: transform 225ms cubic-bezier(0.0,0.0,0.2,1);
    transition-property: all;
    background-color: transparent;
}

ytm-mobile-topbar-renderer c3-icon {
    display: block;
    color: #fff;
}

.mobile-topbar-header {
    position: relative;
    z-index: 2;
    box-shadow: 0 0 0 0 rgba(0,0,0,0.2);
}

}
