css:
@-moz-document regexp('https://webmail-etu\\.univ-nantes\\.fr.*') {

html, body, #leftBody, frame, #headerTableTrTd {
	background-color: #222;
    color: #eee !important;
}

/* textes */

a, .folderTreeFolderMailboxName > a > font {
    color: #fff !important;
}

.folderTreeFolderMailboxName > b > a > font {
	color: #a33 !important;
}

.attachmentBloc {
    color: #eee !important;
}

.mailboxHeaderTd > b {
	color: #eee !important;
}

.folderTreeFolderUnread {
    color: #000 !important;
    text-decoration: bold;
}

/* mails non lu */
td.mailboxtd > a > label > b {
	color: #3a3 !important;
}
td.mailboxtd > b {
	color: #3a3 !important;
}
td.mailboxtd > b > a {
	color: #3a3 !important;
}

/* mails sélectionnés pour action */
.selectedMailboxtd {
	background-color: #33a !important;
}

/* citations de précédents mails */
pre > font {
	color: #3a3 !important;
}

/* couleurs de fond */

#readBodyMenuBarTable {
	background-color: #222 !important;
}

#readBodyFooterTable > tbody > tr > td {
	background-color: #222 !important;
}    

#mailboxDisplayBottomPagerTable > tbody > tr {
	background-color: #222 !important;
}

#pageTopMenuTr > td {
    background-color: #222 !important;
}

td[height="5"] {
    background-color: #222 !important;
}

/* images */

#leftLogo, img[alt="gordon"] {
	filter: invert(87%);
}

/* couleurs de fond de contenu */

.attachmentBloc, .attachmentInfos, .attachmentInfosSize, .attachmentInfosDownload {
    background-color: #333 !important;
}

#attachmentTable, #readBodyHeaderTable, .bodyclass > table {
	background-color: #444 !important;
}

#readBodyHeaderTable + table > tbody > tr > td > table > tbody > tr {
	background-color: #444 !important;
}

#navContainer > span, .folderTreeFolder {
	background-color: #666;
}

#mailboxDisplayPagerTable > tbody > tr {
    background-color: #333;
}

tr[bgcolor="#d0d22f"] {
	color: black;
}

#innerMailboxTable > tbody > tr > td {
	background-color: #333;
}

form > table > tbody > tr > td {
	background-color: #333;
}

#body {
	background-color: #444;
    color: #eee;
}

}
