css:
@namespace url(http://www.w3.org/1999/xhtml);

/*************************************
* Original version of script by Xamm *
*************************************/

/*********
* Forums *
*********/
@-moz-document url-prefix("http://www.computerandvideogames.com/forums/") {
  .ads_leaderboard,
  .ads_skyscraper,
  .ads_takeover,
  .ads_mpu,
  .ads_mpu_header,
  .vid_overlay,
  img[src="/templates/images/recruit_button_160.gif"],
  .wrap_footer {
    display: none !important;
  }

  .wrap_page {
    background: #fff !important;
    width: 100% !important;
    padding: 0px !important;
  }

  .wrap_content {
    position: absolute !important;
    top: 0px !important;
    left: 0px !important;
    width: /*85%*/auto !important;
    height: 100% !important;
    margin-top: 0px !important;
    margin-left: /*0px*/136px !important;
    margin-right: 4px !important;
  }

  .wrap_sidebar {
    width: /*15% !important;
    min-width: */132px !important;
    margin-top: 0px !important;
    margin-left: 0px !important;
    padding: 0px !important;
  }
  
  .wrap_sidebar * {
    margin-left: auto !important;
    margin-right: auto !important;
  }

  .wrap_forums {
    width: 100% !important;
    height: 100% !important;
    position: relative !important;
    top: 0px !important;
    left: 0px !important;
  }

  .wrap_topbox {
    display: none !important;
  }

  .wrap_forums_post {
    width: 100% !important;
  }

  .pod_light_header_full {
    padding-left: 0px !important;
    background-repeat: no-repeat !important;
    background-color: #fff !important;
    width: 100% !important;
    font-size: 0px !important;
  }

  .pod_light_header_full:before {  
    content: " " !important;
    height: 4px !important;
    width: 100% !important;
    background-color: #7B7B8C !important;
    display: block !important;
  }

  .wrap_forums td[align="left"][valign="top"][width="110"] {
    width: 13% !important;
  }
  .wrap_forums td[valign="top"][width="100%"] {
    width: 87% !important;
  }
  .genmed {color: #FFFFFF;}

/*****************************************
* Bastard Video Player and Kudos Rubbish *
*****************************************/
  table[background="/templates/images/vidthumb2.gif"],
  .wrap_sidebar .wrap_pod_more,
  .wrap_sidebar .vid_overlay/*,
  .pod_dark*/ {
    display: none !important;
  }
  /*.pod_dark .pod_dark_header .pod_header,*/
  .pod_dark .pod_dark_header .vid_overlay,
  .pod_dark .wrap_pod a img {
    display: none !important;
  }
}
