css:
@-moz-document url-prefix("https://codelabs.developers.google.com/codelabs/") {
google-codelab #codelab-title {
    color: #d9d9d9;
    background-color: #333333;
}

.steps ol {
    background-color: #333333;
}

.metadata {
    background-color: #333333;
}

google-codelab-step {
    background-color: #333333;
}

google-codelab #drawer .steps ol li a {
    background-color: #5c5c5c;
    color: #ffffff;
    border-color: #757575;
}

.inner {
    background-color: #454545;
    color: #ffffff;
}

google-codelab-step .instructions table code,
google-codelab-step .instructions h2 code,
.inner code {
    background-color: #333333;
    color: #ffffff;
}

google-codelab-step::-webkit-scrollbar {
    width: 8px;
}

google-codelab-step::-webkit-scrollbar-track {
    background: #666;
}

google-codelab-step::-webkit-scrollbar-thumb {
    background: #ddd;
}
}
