css:
@-moz-document url-prefix("https://wfokafka.digitalesregister.it/"), url-prefix("https://fos-meran.digitalesregister.it/"), url-prefix("https://gymme.digitalesregister.it/"), url-prefix("https://sogym-fotour.digitalesregister.it/") {
@media (prefers-color-scheme: dark) {

    body,
    html,
    .login-flexbox {
        background: #202020;
    }
    input.form-control {
        border: 1px solid #181818;
        background: #181818;
    }
    .student-dashboard-list .add-entry-item .input {
        border-radius: 5px;
    }
    .h1,
    .h2 {
        color: #f7f7f7;
    }
    label {
        color: #b8c7e4;
    }
    .v2-main-navigation {
        box-shadow: 0 2px 12px 0 rgba(0, 0, 0, 0.35);
    }
    .student-dashboard-list .student-dashboard-item {
        background: #2c2c2c;
        box-shadow: 0 0 16px 0 rgba(0, 0, 0, 0.1);
        color: #d5d5d5;
    }
    .student-dashboard-list .add-homework,
    .student-dashboard-list .item-delete {
        color: #80c0f9;
        font-size: 16px;
        cursor: pointer;
    }
    .student-dashboard-list .item-submission-deadline {
        color: #cbddf1;
    }
    .student-dashboard-list .student-dashboard-switch {
        background-color: #313131;
        box-shadow: 0 0 16px 0 rgba(0, 0, 0, 0.1);
        color: #dadada;
    }
    .student-dashboard-list .class-label {
        background: #3e3e3e;
        color: #f4f4f4;
    }
    .student-dashboard-list .item-status-done {
        background: #48b775 !important;
        box-shadow: 0 0 4px 0 #000000;
    }
    .student-dashboard-list .item-status-warning {
        background: #dc3a4b;
        box-shadow: 0 0 6px 0 rgb(167, 0, 18);
    }
    .item-status-checkable {
        background: #303030 !important;
    }
    .student-dashboard-list .item-status {
        box-shadow: 0 0 10px 0 rgba(0, 0, 0, 0.5);
    }
    input.input {
        background: #232323;
        border: 1px solid #232323;
        color: #c7d5f1;
    }
    .student-dashboard-list .timeline {
        background: #3d3d3d;
    }
    /*work in progress*/
    .v2-main-navigation {
        background: #272727;
        color: #C2C2C2;
    }
    /* -- */
    .student-dashboard-list .student-dashboard-switch:hover {
        background-color: #363636;
    }
    .calendar-hour-legend-hour {
        color: #dadada;
    }
    .calendar-hour-white {
        background: #313131;
        color: #dee3ec;
        border-radius: 5px;
    }
    .calendar-hour-white .calendar-hour-subtitle {
        color: #a6b6c7;
    }
    .calendar-hour-white:hover {
        background: #373737;
    }
    .calendar-hour-highlight {
        border-right: 8px solid #d23142;
    }
    body {
        color: #f1f1f1;
    }
    .calendar-day-header {
        color: #d4def1;
    }
    .student-dashboard-list .student-absence-item,
    .student-subject-item {
        background: #303030;
    }
    .student-subject-item .student-name {
        color: #d7e0f5;
    }
    .grade {
        color: #37cc8b;
        margin-right: 10px;
        font-weight: bold;
    }
    .grade-negative {
        color: #ff4032;
        font-weight: bold;
    }
    .student-subject-item .absences,
    .student-subject-item .average {
        color: #d9e1f7;
        background: #272727;
        border: 1px solid #272727;
    }
    .profile-card-inner {
        background: #2d2d2d;
        box-shadow: 0 0 12px 2px rgba(19, 19, 19, 0.5);
    }
    .btn:hover {
        background-color: #4a4a4a;
        color: #FFF;
    }
    .btn {
        border: none;
        background-color: #424242;
        color: #FFF;
    }
    .uib-datepicker-popup.dropdown-menu {
        background: #353535;
    }
    .btn-group > .btn:first-child:not(:last-child):not(.dropdown-toggle),
    .btn-group > .btn:last-child:not(:first-child),
    .btn-group > .dropdown-toggle:not(:first-child) {
        color: #82bcde;
    }
    .uib-datepicker-popup button {
        color: #b9b9b9;
    }
    .uib-datepicker-popup button .text-info {
        color: #ffffff;
    }
    .modal label {
        color: #c5d1e7;
    }
    .lesson-list .lesson-item .lesson-number {
        border: 1px solid #545454;
        color: #d5d5d5;
    }
    .lesson-list .lesson-item .lesson-title {
        color: #e3e3e3;
    }
    textarea.input {
        background: #232323;
        border: 1px solid #232323;
        color: #e1e1e1;
    }
    .ui-select-toggle[disabled],
    .ui-select-toggle[disabled]:hover {
        background: #3d3d3d;
        border: 1px solid #3d3d3d;
        color: #dfdfdf;
    }
    .ui-select-multiple.ui-select-container {
        background: #232323;
        border: 1px solid #232323;
        color: #e3e3e3;
    }
    .ui-select-match-item.btn {
        background: #3d3d3d;
        border: 1px solid #3d3d3d;
        color: #c1d0e6;
    }
    .lesson-list .lesson-item .lesson-online-label {
        color: #cfdef0;
    }
    .tbl td {
        color: #c4cfe5;
    }
    .switch {
        background: #232323;
        border: 1px solid #232323;
        box-shadow: #242424 0 0 0 0 inset;
    }
    .switch small {
        background: center no-repeat #4c4c4c;
        box-shadow: 0 1px 3px rgba(0, 0, 0, .4);
    }
    .ui-select-match-item.btn:hover {
        background: #494949;
        color: #e0ecff;
        border-color: #494949;
    }
    .ui-select-bootstrap .btn {
        background: #232323;
        border: 1px solid #232323;
        color: #e3ecfd;
    }
    .calendar-date-picker-button,
    .calendar-hour-dark,
    .calendar-hour-light,
    .calendar-hour-special,
    .calendar-hour-special2,
    .calendar-hour-white,
    .grade-group-item,
    .grade-group-item-expanded,
    .student-subject-item,
    .student-subject-item-expanded,
    .summary .dropdown-menu,
    .summary-subject-picker-button {
        box-shadow: 0 0 16px 0 rgba(37, 37, 37, 0.06);
    }
    .lesson-list .lesson-item .lesson-expanded {
        background-color: #272727;
    }
    .lesson-list .lesson-item .lesson-room {
        background: #383838;
    }
    .input-group,
    .ui-select-bootstrap .btn,
    .ui-select-multiple.ui-select-container,
    input.input,
    select.input,
    textarea.input {
        background: #232323;
        border: 1px solid #181818;
        color: #aabee4;
    }
    .input-group .form-control:first-child {
        color: #bbbbbb;
    }
    .tbl tr:nth-child(odd) {
        background: rgb(43, 43, 43);
    }
    .tbl {
        box-shadow: 0 0 12px 2px rgba(0, 0, 0, 0.05);
    }
    .calendar-date-picker-button {
        box-shadow: 0 0 16px 0 rgba(255, 255, 255, .06);
        filter: invert(80%);
        color: black;
    }
    .calendar-date-picker-button:hover {
        background-color: #f1f1f1;
    }
    .modal .lesson-list .modal-close,
    .modal-dialog .modal-close {
        display: block;
        filter: invert(88.5%);
    }
    .btn-expand {
        filter: invert(81%);
    }
    .student-subject-item-view-chooser {
        background: #292929;
    }
    .student-subject-item-view-chooser .view-chooser-selected {
        color: #d8d8d8;
    }
    .student-subject-item-expanded {
        background: #252525;
    }
    .course-content-item,
    .message-item {
        background: #2f2f2f;
        color: #d2dae8;
    }
    .lesson-list .lesson-item-now,
    .lesson-list .lesson-item:hover {
        box-shadow: 0 0 26px 16px rgba(0, 0, 0, 0.1);
    }
    .course-content-item,
    .message-item,
    .message-item-expanded,
    .v2-notification {
        box-shadow: 0 0 16px 0 rgba(0, 0, 0, 0.1);
    }
    .lesson-list .lesson-item .lesson-detail-label {
        color: #fefeff;
    }
    a:focus,
    a:hover {
        color: #729dc3;
        text-decoration: underline;
    }
    .course-content-topic-header {
        color: #cecece;
    }
    .message-item .message-date,
    .message-item .message-from {
        color: #d8d8d8;
    }
    .message-item .message-subject {
        color: #a5a5a5;
    }
    .lesson-list .lesson-item .lesson-collapsed {
        background: #272727;
    }
    .login-background {
        filter: brightness(0.5);
    }
    .loading-animation {
        border: 8px solid #151515;
        border-top: 8px solid #7592FB;
    }
    .lesson-list .lesson-item {
        box-shadow: 0 0 16px 0 rgba(0, 0, 0, 0.1);
        transition: box-shadow .1s ease-out;
    }
    .message-item-expanded {
        background: #272727;
    }
    .ql-container blockquote,
    .ql-container li,
    .ql-container p,
    .ql-container ul,
    .ql-editor blockquote,
    .ql-editor li,
    .ql-editor p,
    .ql-editor ul {
        color: #dde9ff;
    }
    a {
        color: #73beff;
    }
    .student-subject-item-table tr:nth-child(odd) {
        background: #272727;
    }
    .student-subject-item-table td .grade {
        background: #232323;
        border: 1px solid #232323;
    }
    .student-subject-item-table tr:hover {
        background: #292929;
    }
    .student-subject-item-table-header {
        color: #6b95e4;
    }
    .col-md-12 {
        width: 100%;
        color: #8c8c8c;
    }
    .modal-header {
        color: #e6e6e6;
    }
    .popover {
        background-color: #383838;
    }
    .popover.top > .arrow:after {
        border-top-color: #383838;
    }
    .v2-notification {
        background: #2c2c2c;
        border-left: 6px solid #7592fb;
        margin-right: 40px;
    }
    .item-status-warning {
        background: #dc3a4b !important;
        box-shadow: 0 0 6px 0 rgb(167, 0, 18) !important;
    }
    .v2-main-navigation a.item-icon-messages:before {
        height: 14px;
        border-radius: 4px;
        margin-top: -8px;
    }
    .v2-main-navigation a.item-icon-logout:before {
        left: 10px;
    }
    a.item.item-icon.item-icon-logout {
        background: #424859;
        margin: 0px 30px;
        width: 170px;
        padding: 10px 0px 10px 42px;
        border-radius: 6px;
        box-shadow: 0 0 5px 0px #00000094;
    }
    .calendar-hour-white {
        background: #2b2b2b;
        color: #dee3ec;
        border-radius: 5px;
    }
    .form-control,
    .ui-select-toggole {
        background: #232323 !important;
    }
    .tbl {
        background: #242424;
    }
    .tbl th,
    .tbl th:hover {
        background: #2d2d2d;
    }
    .tbl tr:hover {
        background: #202020;
    }
    .form-control {
        color: #d4d4d4;
    }
    .login-box p {
        color: #d1d1d1;
    }
    .corona-box-large {
        background: #202020;
    }
    #buttonHidePassword,
    #buttonHidePassword:hover {
        border-color: transparent;
        background-color: transparent;
        filter: brightness(5.5);
    }
    .modal-content {
        background-color: #272727;
    }
    select#inputLanguage,
    input[type=file] {
        background: #232323;
        padding: 6px 10px;
        border-radius: 4px;
        border: 1px solid #202020;
    }
    .grade-detail-type {
        color: #e6eeff;
    }
    .grade-detail-type:after {
        background-color: #404040;
    }
    a.grade-detail-type:hover {
        background-color: #2c2c2c;
        color: #ffffff;
        border-radius: 6px;
    }
    .ql-container strong,
    .ql-editor strong {
        color: #ffffff;
    }
}
}

@-moz-document url-prefix("https://wfokafka.digitalesregister.it/"), url-prefix("https://fos-meran.digitalesregister.it/"), url-prefix("https://gymme.digitalesregister.it/"), url-prefix("https://sogym-fotour.digitalesregister.it/") {
::-webkit-scrollbar-track-piece {
    background-color: #2f2f2f;
    -webkit-box-shadow: inset 0 0 0 rgba(0, 0, 0, 0) !important;
    border-radius: 0 !important;
    border: none !important;
}
::-webkit-scrollbar {
    width: 5px;
    height: 5px;
}
::-webkit-scrollbar-thumb {
    background: #505050;
}
::-webkit-scrollbar-thumb:hover {
    background: #444;
}
::-webkit-scrollbar-corner {
    background: #2f2f2f;
}
}

@-moz-document url-prefix("https://www.digitalesregister.it/") {
/* Dark mode for the school register used in south tyrol.
If you want to contribute, change, fix something feel free to do so and contact me through my website or Discord.
Also important note: No. It's plain CSS, it's not going to change the way you communicate with the servers, it's not going to magically make a 10 appear in you grades.
If you are wondering, yes I dedicated this portion of the code just in case the school would stumble upon this and think it's some kind of "hack". Well it's not, it's just a dark mode.
*/
}
