css:
/*首页*/

@-moz-document domain("www.ali213.net") {
    .left-icon4/*游戏中心*/
    ,
    .right-icon4/*手机客户端*/
    ,
    .right-icon3/*微博*/
    ,
    .right-icon2/*微信*/
    ,
    .bg-shadow3.section4.section/*手机游戏版块*/
    ,
    .bg-shadow3.section5.section/*网络游戏版块*/
    ,
    .bg-shadow3.section6.section/*网页游戏版块*/
    ,
    .footer/*页脚*/
    ,
    .go-top/*返回顶部按钮*/
        {
        display: none!important;
    }
    div.top-search-con.bg-shadow2 {
        width: 1200px;
        height: 45px;
        position: absolute;
        top: -45px;
    }
    input#soinput.soinput {
        width: 1050px;
        height: 40px;
        position: absolute;
        top: 1px;
    }
    body > div.index-container > div.top-container {
        margin-top: 50px!important;
    }
}

/*搜索页*/

@-moz-document domain("so.ali213.net") {
    .t1c_app,
    .footer {
        display: none!important;
    }
    a,
    p,
    h2,
    h3 {
        color: rgb(213, 213, 213)!important;
    }
    body > div.searchContainer,
    body > div.searchContainer > div.searchHeader,
    body > div.searchContainer > div.contentHeader,
    body > div.searchContainer > div.contentHeader > div.firstLevel,
    body > div.searchContainer > div.contentHeader > ul > li > ul.secondLevel.clearFloat,
    body > div.ns_t1 {
        background: #363B3C!important;
    }
    body > div.searchContainer > div.searchHeader > div > form > input[type=text] {
        background: #222425!important;
    }
    /*#a94113
#c64711*/
    .t1c_l li {
        border: rgb(72, 78, 82)!important;
    }
    .modualTitle,
    body > div.searchContainer > div.contentHeader > ul > li > ul.secondLevelContent > li > div.resultContent > div > ul > li,
    body > div.searchContainer > div.contentHeader > ul > li > ul.secondLevelContent > li > div.resultContent > div > div.sameModual.resutlModual2 > ul > li,
    body > div.searchContainer > div.contentHeader > div.firstLevel {
        border-bottom: 1px solid rgb(105, 105, 105)!important;
    }
    .glModual1 > div {
        padding: 20px 0 10px;
        border-bottom: 1px solid rgb(105, 105, 105)!important;
    }
    body > div.searchContainer > div.contentHeader > div.rank > ul > li:nth-child(4) > em,
    body > div.searchContainer > div.contentHeader > div.rank > ul > li:nth-child(5) > em,
    body > div.searchContainer > div.contentHeader > div.rank > ul > li:nth-child(6) > em,
    body > div.searchContainer > div.contentHeader > div.rank > ul > li:nth-child(7) > em,
    body > div.searchContainer > div.contentHeader > div.rank > ul > li:nth-child(8) > em,
    body > div.searchContainer > div.contentHeader > div.rank > ul > li:nth-child(9) > em,
    body > div.searchContainer > div.contentHeader > div.rank > ul > li:nth-child(10) > em {
        background: #777777!important;
    }
}

/*游侠图片*/

@-moz-document domain("pic.ali213.net") {
    body > div.ns_t1 > div > div.t1c_app,
    body > div.ALi_wb,
    #gallery > div.Ali_wz > div.alidhLf,
    #gallery > div.Ali_wz > div.alidhRt > div > div.Ali_Pl,
    body > div.ALi_SM > div.alinadd > div.alinaddRight,
    #Ali_desc > div > div.aliTsInfo,
    #comment_display_content > div:nth-child(2),
    body > div.ALi_f {
        display: none!important;
    }
    body > div.ns_t1 {
        background: #202020!important;
    }
    #gallery > div.Ali_wz > div.alidhRt > div > div.Ali_Ts1 {
        position: relative!important;
        left: 200px!important;
    }
    #image-show > a.prev,
    #image-show > a.next {
        width: 150px!important;
        background: rgba(255, 255, 255, 0.1)!important;
    }
}
