css:
@-moz-document url-prefix("http://192.168.1.2:12345/") {
    body,
    .panel,
    #mochatoolbar,
    .dynamicTable tbody tr,
    .dynamicTable th,
    #desktopNavbar,
    #desktopHeader,
    .returnFalse,
    .toolbarTabs,
    #transferList_handle_icon,
    #filtersColumn_handle,
    #columnHandle,
    #propertiesPanel_header,
    #desktopNavbar li ul li a,
    #desktopNavbar li ul li,
    #desktopNavbar li ul,
    #desktopFooter,
    #torrentsFilterToolbar,
    #torrentsFilterInput,
    .filterWrapper li a,
    .mochaOverlay,
    .mochaTitle,
    .mochaContent,
    .mochaContentWrapper,
    input,
    select,
    button,
    h2,
    h3,
    h4,
    textarea {
        background: #2b2b2b !important;
        color: #a9b7c6 !important;
    }

    a {
        color: #a9b7c6;
    }

    .dynamicTable tbody tr:nth-child(2n),
    button {
        background-color: #1b1b1b !important;
    }

    li.divider {
        border-top: 3px solid #1b1b1b !important;
    }

    #mochaToolbar .divider {
        background: none;
        border-left: 3px solid #1b1b1b;
    }

    #pageWrapper,
    .columnHandle,
    .dynamicTable th,
    #propertiesPanel_header,
    fieldset,
    .contextMenu,
    .contextMenu .separator,
    .contextMenu li ul,
    #torrentsFilterToolbar,
    #torrentsFilterInput,
    .scrollableMenu,
    .mochaContentBorder,
    #uploadForm div,
    #urls,
    .mochaToolbarWrapper,
    .mochaToolbar,
    input,
    select,
    button,
    textarea,
    .select-watched-folder-editable {
        border-color: #1b1b1b !important;
    }

    td.statusBarSeparator {
        background-image: none;
        background: linear-gradient(#1b1b1b, #1b1b1b) no-repeat center/2px 100%;
    }

    #propertiesPanel_collapseToggle,
    .tab-menu {
        filter: invert(100%) grayscale(100%);
    }

    .filterWrapper li:nth-child(2n+1) a {
        background: #1b1b1b !important;
    }

    .filterWrapper li.selectedFilter a,
    .filterWrapper li a:hover,
    .dynamicTable tbody tr:hover,
    #desktopNavbar ul li a:hover,
    .contextMenu li a:hover,
    .dynamicTable tbody tr.selected {
        background: #214283 !important;
        color: #a9b7c6 !important;
    }

    .contextMenu li a {
        background-color: #2b2b2b !important;
        color: #a9b7c6 !important;
    }

    .progressbar_dark {
        background: #022a53 !important;
    }
}
