css:
@-moz-document domain("montevideo.com.uy"), domain("pantallazo.com.uy"), domain("gastronomia.com.uy"), domain("futbol.com.uy") {
body {
    background-color: #4a4a4a !important;
    color: rgb(230, 230, 230);
  }

  @media (min-width: 980px) {
    #header .nav .menu > li > a {
      color: rgb(230, 230, 230);
    }
  }

  /* texto de las notas */
  @media only screen and (min-width: 768px) {
    #despliegue.not_deportes .contenido p,
    #despliegue .contenido ul li,
    #despliegue .contenido ol li {
      color: rgb(230, 230, 230);
      font-family: sans-serif, Arial, Helvetica, sans-serif;
    }
  }

  a,
  input[type="submit"],
  button {
    color: rgb(186, 186, 186);
  }

  @media only screen and (min-width: 980px) {
    #portadas .subnav {
      background-color: #2f383d;
      color: wheat;
    }
  }

  @media only screen and (min-width: 980px) {
    #portadas .subnav ul li a {
      color: wheat;
    }
  }

  .bg,
  .bloque_gris {
    background: #2f383d;
  }

  .noticia .content a {
    color: wheat;
  }

  #colorarriba .noticia .content a {
    color: wheat;
  }

  #noseasmalo {
    background-color: #3f704d;
  }

  #quedate {
    background-color: #6082b6;
  }

  #sorteos .noticia.color-1 .content {
    background-color: #355797;
  }

  @media only screen and (min-width: 768px) {
    #sorteos .noticia.color-1 .content:before {
      background-color: #355797;
    }
  }

  #pantallazo .noticia.color-4 .content {
    background-color: #cc6600;
  }

  #pantallazo .noticia.color-2 .content {
    background-color: #3f704d;
  }

  #sorteos .noticia.color-3 .content {
    background-color: #9b870c;
  }

  #sorteos .noticia.color-2 .content {
    background-color: #8a496b;
  }

  @media only screen and (min-width: 768px) {
    #sorteos .noticia.color-3 .content:before {
      background-color: #9b870c;
    }

    #sorteos .noticia.color-2 .content:before {
      background-color: #8a496b;
    }
  }

  .fdopodcast,
  #colorarriba .tall {
    background-color: #b87700;
  }

  /* se quitan la incrustacion de twitter y de productos */
  #productos,
  #comunidad,
  #encuestas {
    display: none;
  }

  #sidebar .embed {
    display: none;
  }

  #header,
  #mobile-nav .menu .header {
    background-color: #2f383d;
  }

  /* paginas de noticia */
  #despliegue .summary .title {
    color: wheat;
  }

  #cnl-contenedor .central {
    background-color: #2f383d;
    border: none;
  }

  #interes .container {
    background-color: #2f383d;
    color: wheat;
  }

  #interes .container a {
    color: wheat;
  }

  .fdocantcomentarios {
    background-color: #2f383d;
  }

  .nrocomentarios {
    color: wheat;
  }

  @media only screen and (min-width: 980px) {
    #comentarios:before,
    #comentarios:after {
      background-color: #2f383d;
    }
  }

  .heading,
  #despliegue .categorias a,
  a.textocomentarios:link,
  a.textocomentarios:visited,
  a.commentExpand:link,
  a.commentExpand:visited,
  a.commentExpand:hover,
  a.btnShowComment:link,
  a.btnShowComment:visited,
  a.btnShowComment:hover,
  .user_state a:link,
  .user_state a:visited,
  .user_state a:hover,
  ol.simple-list > li:before,
  #despliegue .contenido p a,
  #despliegue .summary .kicker {
    color: #e18a07;
  }

  #tags-noticia .tags .tag > a {
    background: #e18a07;
  }

  /* menu superior */

  @media only screen and (min-width: 980px) {
    #portadas .subnav .title {
      color: #e18a07;
    }
  }

  @media (min-width: 980px) {
    #header .nav .menu > li.active > a,
    #header .nav .menu > li > a:hover,
    #header .nav .menu > li.children.show > a {
      background-color: inherit;
      color: #e18a07;
    }
  }

  @media (min-width: 600px) {
    #cnl-contenedor {
      background-color: inherit;
    }
  }

  /* menu emergente al elegir algo del menu superior */
  @media (min-width: 980px) {
    #header .nav .menu > li.children .holder .submenu {
      background: #2f383d;
    }
  }

  #header a,
  #header button {
    color: rgb(230, 230, 230);
  }

  .noticia .content .kicker {
    color: #e18a07;
  }

  .noticia .content a:hover {
    color: #e18a07;
  }

  #pantallazo .noticia.color-1 .content {
    background: inherit;
  }

  #pantallazo .noticia .content .title {
    font-family: inherit;
    font-style: normal;
  }

  iframe {
    display: none;
  }

  h3.heading {
    color: #7cb9e8;
  }

  #tags-noticia .title {
    color: #c0c0c0;
  }

  .sesioncontenedor {
    color: #c0c0c0;
  }

  .noticia.ppal {
    margin-bottom: 5rem;
  }
}
