css:
@-moz-document url-prefix("https://stats.foldingathome.org/"), url-prefix("http://localhost:7396") {
html {
    background-image: none;
    background: #222;
}

body {
    background-image: none;
    background: none;
}

.corner-all {
    border-radius: 2em;
}

.panel-header,
#menu {
    background: #606060;
    color: #00db00
}

#menu li {
    background: #000;
    border: 1px solid #333;
    border-bottom: 0px;
    color: #00db00;
    font-family: monospace
}

#menu li a {
    color: #00db00
}

#content table.results tr.description td,
#content {
    background: #000
}

#content table.search {
    background-color: #111
}

#content.os-page tr.Total td,
#content.os-page th,
#content table.results .user:nth-child(2n+1) > td {
    background-color: #444;
}

a, a:visited,
#content.os-page th,
#content.os-page td,
#content table.results .user:nth-child(2n+1) > td {
    color: #ccc
}

#content.os-page td,
#content table.results .user:nth-child(2n) > td {
    background-color: #777
}

*, ::after, ::before {
    box-sizing: unset;
}

#content p,
#content table.search,
.team-info th, .team-info td {
    color: #ddd
}

#content table.results th {
    background-color: #ee0000;
    color: #eee
}
}
