css:
@-moz-document domain(nimbusweb.me) {
.collapsed-list-item:after {
	content: "" !important;
	display: block !important;
	position: absolute !important;
	width: 10px !important;
	height: 10px !important;
	background-color: hsl(220,5%,43.6%) !important;
	z-index: 1 !important;
	background-image: none !important;
	border: unset !Important;
	margin-left: -49px !important;
	margin-top: 5px;
	left: 0;
	-webkit-mask-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 17 18' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M15.2425 7.46266L9.55362 1.77375C9.03979 1.25992 8.21134 1.24527 7.67966 1.74061C7.12071 2.26136 7.10514 3.14185 7.64533 3.68204L12.353 8.38978C12.7478 8.78454 12.7478 9.42457 12.353 9.81933L7.68464 14.4878C7.16008 15.0123 7.15735 15.862 7.67853 16.3899C8.20447 16.9227 9.06357 16.9254 9.59292 16.3961L15.2425 10.7464C16.1493 9.83966 16.1493 8.36945 15.2425 7.46266Z' fill='%23C4C4C4'/%3E%3C/svg%3E%0A") !important;

}
	div.paragraph {
		margin-top: 0px !important;
		margin-bottom: 0px !important;
	}

	.editable-text {
		-webkit-font-smoothing: antialiased;
	}

	.editable-text {
		transition: .2s;
	}

	.block-menu-container {
		z-index: 1000000000 !important;
	}

	/**
 **
 **
 **Scrollbar

::-webkit-scrollbar-thumb {
    border: 1px solid rgba(100,100,100,0) !important;
    background-color:rgba(100,100,100,1) !important;
    border-radius:100px !important;
}

::-webkit-scrollbar-track {
    background-color:  rgba(100,100,100,.01) !important;
}
::-webkit-scrollbar {
    background: rgba(0,0,0,.10) !important;
    width: 6px!important;
}

::-webkit-overflow-scrolling: auto !important;


*/


	/** ANCHOR HIGHLIGHTS**/
	html {
		-scroll-behavior: smooth;
	}

	h1.highlighted-block, h3.highlighted-block, h5.highlighted-block, .highlighted-block, h1.highlighted-block[style*="background-color"], h3.highlighted-block[style*="background-color"], h5.highlighted-block[style*="background-color"], .highlighted-block[style*="background-color"] {
		background-color: hsla(217, 29%, 39.5%, 0.6) !important;
		color: hsla(217, 89%, 80.5%, 1) !important;
		transition: .32s ease-in-out !important;

	}
	
	h1.highlighted-block[style*="background-color"], h3.highlighted-block[style*="background-color"], h5.highlighted-block[style*="background-color"], .highlighted-block[style*="background-color"] {
		box-shadow:inset 0 0 0 1px hsla(217, 89%, 73.5%, 0.8) !important;
	}

	h1.highlighted-block *, h3.highlighted-block *, h5.highlighted-block *, blockquote.highlighted-block, .highlighted-block strong {
		color: hsla(210, 96%, 70%, 1) !important;
	}

/*	.highlighted-block:before, li.highlighted-block:before {
		content: "" !important;
		display: block !important;
		position: absolute !important;
		width: 4px !important;
		height: 100% !important;
		background-color: hsla(217, 69%, 59.5%, 1) !important;
		border-top-left-radius: 4px;
		border-bottom-left-radius: 4px;
		transition: .32s ease-in-out !important;
	}*/

	.editable-text {
		transition: .2s ease-in-out;
		transition-delay: .1s;
	}




	/**
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **NAVBAR*/
	.note-top-panel--wrapper {
		display: flex;
		justify-content: space-between;
		padding: 6px 12px;
		border-bottom-left-radius: 5px;
		display: flex;
		z-index: 1000;
		backdrop-filter: blur(10px);
		background: hsl(220, 5%, 10.6%);
		background-color: rgba(0, 0, 0, 0) !important;
		background-image: none !important;
	}

	/** 
 **
 **NAV BAR TAGS*/
	.theme-dark note-tags-input .note-tags-input--tags .tags-wrapper .selected-tags .selected-tag {
		background-color: hsla(210, 17%, 25%, 0.9);
		border-radius: 2px;
		color: hsla(220, 17%, 55%, 1) !important;
	}

	.theme-light note-tags-input .note-tags-input--tags .tags-wrapper .selected-tags .selected-tag {
		border-radius: 4px;
	}

	/*NAV BAR TAGS - DROPDOWN TAG PANEL -  THEME DARK*/
	.theme-dark note-tags-input .note-tags-input--tags .tags-edit .tags-wrapper {
		background-color: rgba(255, 255, 255, 0.01);
		border-top-right-radius: 5px;
		border-top-left-radius: 5px;
		border-bottom-left-radius: 0px;
		border-bottom-right-radius: 0px;
	}

	/*NAV BAR TAGS - DROPDOWN TAG PANEL -  THEME DARK*/
	.theme-light note-tags-input .note-tags-input--tags .tags-edit .tags-wrapper {
		border-top-right-radius: 5px;
		border-top-left-radius: 5px;
		border-bottom-left-radius: 0px;
		border-bottom-right-radius: 0px;
	}

	/*NAV BAR TAGS - DROPDOWN TAG PANEL - DELETE TAG ICON - THEME DARK*/
	.theme-dark .selected-tag .delete-tag svg g path[fill-custom] {
		fill: hsla(210, 27%, 42%, 1);
	}

	.theme-dark .selected-tag .delete-tag:hover svg g path[fill-custom] {
		fill: hsla(210, 57%, 62%, 1);
	}

	/*NAV BAR TAGS - DROPDOWN TAG PANEL - TAG LIST CONTAINER - THEME DARK*/
	.theme-dark note-tags-input .note-tags-input--tags .tags-wrapper .tags-dropdown {
		top: calc(100% + 1px);
		left: -1px;
		background-color: rgba(45, 45, 45, .86);
		backdrop-filter: blur(15px) !important;
		border: 1px solid rgba(255, 255, 255, 0.16);
		border-top-width: 0px;
		border-top-left-radius: 0px !important;
		border-top-right-radius: 0px !important;
		border-radius: 5px;
	}

	/** 


	/** 
 **
 **
 **
 **NAV BAR FILE ATTACHMENTS */
	.attachment {
		border-radius: 8px;
		background-color: rgba(150, 150, 150, .05);
	}

	.attachment-icon--wrapper {
		border-radius: 8px;
	}

	.attachment-name, .attachment-size {
		font-size: 9px;
	}




	/**
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **FILE SIDEBAR*/
	.sidebar {
		min-width: 38px !important;
	}

	.tree-main {
		padding-top: 5px;
	}

	/*TREE TITLES*/
	.tree-main--item--title {
		font-weight: 600;
		padding-top: 4px;
		padding-bottom: 0px;
		min-width: 25px;
		padding-left: 8px;
		transition: 0.3s;
		margin-bottom: 0px;
	}

	.tree-main--item--title .tree-item--name {
		color: rgba(255, 255, 255, .4) !important;
	}

	/*TREE FOLDERS*/
	.tree-secondary {
		padding-left: 14px !important;
	}

	.tree-secondary--item--title {
		padding-top: 2px;
	}

	.tree-item--name {
		white-space: nowrap;
		overflow: hidden;
		max-width: calc(100% - 20px);
		text-overflow: ellipsis;
		font-size: 11.5px;
		font-weight: 600;
		color: white !important;
	}

	/*Nested Folder - 1 level*/

	.tree-secondary--item--title[style="padding-left: 42px; margin-left: -26px;"] {
		margin-left: -38px !important;
	}

	/*Nested Folder - 2 level*/
	.tree-secondary--item--title[style="padding-left: 58px; margin-left: -26px;"] {
		margin-left: -51px !important;
	}

	/*Folder Expand Collapse Icon */
	.tree-secondary--item--title svg-icon {
		margin-right: 6px;
	}


	/**
 **
 **
 **
 **
 **
 **
 **
 **Sheet SIDEBAR*/

	/**NOTE COLOR**/
	.notes-list-item::before {
		border-radius: 0px;
		content: '';
		display: inline-block;
		height: calc(100% - 1px);
		left: 0;
		opacity: .5;
		position: absolute;
		top: 1px;
		bottom: 3px;
		width: 2px;
		border-top-right-radius: 1px;
	}

	/**SHEET Sidebar - HEADER Preview - Overflow*/
	.notes-list-item__rolled .notes-list-item--title, .notes-list-item__rolled .notes-list-item--content {
		white-space: normal;
	}

	/**SHEET Sidebar - Sheet Container - Padding*/
	.notes-list-item {
		padding: 6px 10px 5px 30px;
	}

	/* SHEET Sidebar - Sheet Preview - HEADER TEXT*/
	.notes-list-item--title {
		margin-top: 5px !important;
		font-size: 12.25px;
	}

	/* SHEET Sidebar - Sheet Preview - BODY TEXT*/
	.notes-list-item__rolled .notes-list-item--title, .notes-list-item__rolled .notes-list-item--content {
		/*-webkit-text-stroke-width: 0.0px;*/
		letter-spacing: 0px;
	}

	.notes-list-item--content p {
		margin-top: 1px;
		text-overflow: unset !important;
		overflow: visible;
	}

	/* SHEET Sidebar - Sheet Preview - Date*/
	span.notes-list-item--content__date.ng-binding.ng-scope, .theme-dark .notes-list-item--content__date {
		position: absolute;
		bottom: 6px;
		left: 30px;
		color: #757575;
		/*-webkit-text-stroke-width: 0.2px;*/
		font-size: 10px;
	}

	/*SHEET SIDEBAR - ICONS */
	.notes-list-item__comfortable .notes-list-item--bottom {
		margin-top: 5px;
		min-height: 16px;
		margin-left: 56px;
	}

	.notes-list-item--action {
		font-size: 10px;
		margin-right: 4.4px;
		margin-top: 1px;
		line-height: 12px;
		-webkit-font-smoothing: subpixel-antialiased;
	}

	.notes-list-item--action span {
		margin-left: 1px;
		color: #757575;
	}

	.notes-list-item--action svg-icon {
		width: 12px;
		height: 12px;
		top: -.5px;
	}

	.notes-list-item--action svg-icon [fill-custom] {
		fill: hsla(60, 0%, 50%, 1);
	}

	/**
 **
 **SHEET Sidebar - Colors*/
	sidebar.ng-scope.ng-isolate-scope.sidebar-collapsed {
		background: black;
	}

	/*SHEET sidebar - background*/
	.theme-dark .background-default_light, .theme-dark notes-list-view {
		background-color: hsl(220, 5%, 10.6%);
	}

	/*SHEET sidebar - Container - border-color*/
	.theme-dark .border-color-default, .theme-dark notes-list-view {
		border-color: #000000;
		border-left: 1px solid #000000;
	}

	/*SHEET sidebar - Sheet item container - border-color*/
	.notes-list-item::after {
		background-color: #343434;
	}

	.theme-dark .notes-list-item:hover {
		background-color: rgba(200, 200, 200, .05) !important;
	}

	.notes-list-item:hover::after, .notes-list-item.active::after {
		opacity: 1;
		background-color: #343434 !important;
	}

	.notes-list-item.active:hover {
		background-color: rgba(200, 200, 200, .25) !important;
	}


	/*Sheet Sidebar - HEADER - SEARCH SECTION*/
	notes-list-view .notes-list-view--search {
		/* position:absolute;
	left: 1px;
	right: -1px;
	top:0px;
	padding-bottom:45px;
    border-bottom: 1px solid hsla(217, 6%, 16%, .5); */
		z-index: 100000000001 !important;
		background: hsl(220, 8%, 10.6%.2);
		-webkit-backdrop-filter: blur(20px);
		backdrop-filter: blur(20px);
	}

	.notes-list-view--search search-input {
		margin-left: 2px;
	}

	/*Sheet Sidebar - HEADER - NOTES HEADER*/
	.notes-list-header {
		padding-left: 30px;
		padding-top: 2px;
		padding-right: 11px;
		padding-bottom: 12px;
		border-bottom: 1px solid hsla(217, 6%, 16%, 1);
		/* position: absolute;
	left: 1px;
	right: -1px;
	top:45px;
	z-index: 10000000000 !important; */

	}

	/*Sheet Sidebar - HEADER - Color*/
	.theme-dark .color-ash, .theme-dark .notes-list-header-title, .theme-dark .notes-list-header .sort-menu>ul>li>span, .notes-list-header .theme-dark .sort-menu>ul>li>span {
		color: #DDDDDD;
	}

	/*Sheet Sidebar - HEADER - Padding*/

	.notes-list-header-title {
		padding: 0px;
	}

	/*Sheet Sidebar - HEADER - Icon - Display none*/
	.notes-list-header--icon {
		display: none;
		margin-right: 0px;
	}

	/* SHEET Sidebar - Sheet Preview - FOLDER TEXT - COLOR*/
	.theme-dark .color-ash, .theme-dark .notes-list-header-title, .theme-dark .notes-list-header .sort-menu>ul>li>span, .notes-list-header .theme-dark .sort-menu>ul>li>span {
		color: #ffffff;
		font-size: 1.3em;
	}

	.notes-list-header--option {
		padding-bottom: 0px;
		padding-top: 2px;
		margin-left: -10px;
	}

	/* SHEET Sidebar - Sheet Preview - HEADER TEXT - COLOR */
	.notes-list-item__rolled .notes-list-item--title, .notes-list-item__rolled .notes-list-item--content {
		/*-webkit-text-stroke-width: .2px;*/
		letter-spacing: 0.0px;
		-webkit-font-smoothing: subpixel-antialiased;
	}

	/* SHEET Sidebar - Sheet Preview - BODY TEXT - COLOR*/
	.notes-list-item__rolled .notes-list-item--content {
		/*-webkit-text-stroke-width: 0.2px;*/
		color: #9A9A9A;
	}

	/* Sheet Sidebar - SEARCH BOX */
	.dialog-input--search {
		height: 25px;
	}

	.theme-dark .dialog-input::placeholder {
		color: hsla(217, 8%, 40%, 0.5) !important;
	}

	.theme-dark .dialog-input {
		border: 1px solid hsla(217, 8%, 40%, 0.4);
	}

	.theme-dark .dialog-input {
		background-color: hsla(217, 8%, 5%, 0.3) !important;
		border-radius: 3.5px;
		color: #ffffff;
	}

	search-input .search-input-list svg path[fill-custom] {
		fill: hsla(217, 8%, 70%, 0.4) !important;
		;
	}

	/* Sheet Sidebar - NEW NOTE - BUTTON */
	.notes-list-view--search a.button-add-circle {
		width: 26px;
		height: 26px;
		border-radius: 3.5px;
		flex: 0 0 26px;
	}

	/* Sheet Sidebar - NEW NOTE - BUTTON - THEME DARK */
	.theme-dark .notes-list-view--search a.button-add-circle {
		background: hsla(217, 8%, 25%, .6)
	}

	.theme-dark .button-add-circle svg-icon [fill-custom] {
		fill: hsla(210, 0%, 55%, 1);
	}





	/**
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **
 **MAIN BODY*/


	/**
 **
 ** BODY SCALABLE */
	body {
		min-width: auto;
	}

	/**
 **
 ** NOTE TITLE */

	.note-title--wrapper {
		display: block;
		padding-top: 7px;
		padding-bottom: 20px;
	}

	/**
 **
 ** NOTE TITLE BACKGROUND COLOR */
	.note-title--wrapper {
		background: hsl(220, 5%, 10.6%)
	}


	/* NOTE TITLE - NOTE COLOR */
	note-title .note-title--color {
		display: none !important;
	}

	/* NOTE TITLE - FULL WIDTH - PADDING **/
	.note-fullwidth .note-title--container.ng-isolate-scope {
		margin: 0px !important;
		padding-left: 27px !important;
		max-width: 100% !important;
	}

	/* NOTE TITLE - THIN WIDTH - PADDING **/
	.ng-scope.ng-isolate-scope .note-title--container.ng-isolate-scope {
		max-width: 600px;
		margin: 0 auto;
		padding-left: 0px;
	}

	/* NOTE TITLE - FONT SIZE */
	note-title textarea {
		font-size: 50px !important;
		margin-top: 0px;
		line-height: 1;
		letter-spacing: -2px;
		font-weight: 800;
	}

	note-title h1 {
		font-size: 50px;
		margin-top: 0px;
		line-height: 1;
		letter-spacing: -2px;
		font-weight: 800;
	}

	/*NOTE TITLE - Note Color Icon */
	note-title .note-title--color.color-label- {
		display: none;
	}




	/**
 **
 **
 ** MAIN BODY */

	/*MAIN BODY - Container - BODY WIDTH - NORMAL **/
	.ng-scope.ng-isolate-scope .nedit-root {
		max-width: 625px;
	}

	/*MAIN BODY - Container - BODY WIDTH - FULL WIDTH **/
	.ng-scope.ng-isolate-scope.note-fullwidth .nedit-root {
		max-width: inherit;
	}



	/* MAIN BODY - HEADINGS - Font Size - LARGE */
	.nedit-root.size-large h1.editable-text {
		font-size: 41px;
		font-weight: 500;
		letter-spacing: -1.5px;
		margin-bottom: 10px;
		margin-top: 5px;
	}

	.nedit-root.size-large.style-serif h1.editable-text {
		letter-spacing: -1px;
	}

	.nedit-root.size-large h3.editable-text {
		font-size: 32px;
		font-weight: 500;
		letter-spacing: -1px;
		margin-bottom: 10px;
		margin-top: 5px;
	}

	.nedit-root.size-large.style-serif h3.editable-text {
		letter-spacing: -1px;

	}

	.nedit-root.size-large:not(.style-handwritten) h5.editable-text {
		font-size: 26px;
		font-weight: 500;
		letter-spacing: -.4px;
		margin-bottom: 10px;
		margin-top: 5px;
	}

	.nedit-root.size-large.style-serif h5.editable-text {
		letter-spacing: -1px;
	}

	/* MAIN BODY - HEADINGS - Font Size - NORMAL */
	.nedit-root.size-normal h1.editable-text {
		font-size: 38px !important;
		font-weight: 500;
		letter-spacing: -1.5px;
		margin-bottom: 15px;
		margin-top: 5px;
		line-height: 1;
	}

	.nedit-root.size-normal.style-serif h1.editable-text {
		letter-spacing: -1px;
		line-height: 1.15;
	}

	.nedit-root.size-normal h3.editable-text {
		font-size: 28px !important;
		font-weight: 500;
		letter-spacing: -.75px;
		margin-bottom: 16px;
		margin-top: 5px;
		line-height: 31px;
	}

	.nedit-root.size-normal.style-serif h3.editable-text {
		letter-spacing: -.4px;
		line-height: 1.15;
	}

	.nedit-root.size-normal:not(.style-handwritten) h5.editable-text {
		font-size: 21px !important;
		font-weight: 500;
		letter-spacing: -.4px;
		margin-bottom: 10px;
		margin-top: 5px;
		line-height: 24px;

	}

	.nedit-root.size-normal.style-serif h5.editable-text {
		letter-spacing: -.1px;
		line-height: 1.1;
	}


	/* MAIN BODY - HEADINGS - Font Size - SMALL */
	.nedit-root.size-small h1.editable-text {
		font-size: 38px !important;
		font-weight: 500;
		letter-spacing: -1.5px;
		margin-bottom: 10px;
		margin-top: 5px;
		line-height: 1;
	}

	.nedit-root.size-small.style-serif h1.editable-text {
		letter-spacing: -1px;
		line-height: 1.15;
	}

	.nedit-root.size-small h3.editable-text {
		font-size: 28px !important;
		font-weight: 500;
		letter-spacing: -.75px;
		margin-bottom: 10px;
		margin-top: 5px;
		line-height: 1.1;
	}

	.nedit-root.size-small.style-serif h3.editable-text {
		letter-spacing: -.4px;
		line-height: 1.15;
	}

	.nedit-root.size-small:not(.style-handwritten) h5.editable-text {
		font-size: 21px !important;
		font-weight: 500;
		letter-spacing: -.4px;
		margin-bottom: 10px;
		margin-top: 5px;
		line-height: 1.3;

	}

	.nedit-root.size-small.style-serif h5.editable-text {
		letter-spacing: -.1px;
		line-height: 1.2;
	}



	.theme-dark .nedit-root .nedit-heading strong {
		color: hsl(217, 8%, 90%);
	}

	h1 strong {
		letter-spacing: -1px;
	}

	.style-serif h1 strong {
		letter-spacing: -1px;
	}

	h3 strong {
		letter-spacing: -.75px;
	}

	.style-serif h3 strong {
		letter-spacing: -0.6px;
	}

	h5 strong {
		letter-spacing: -.5px;
	}

	.style-serif h5 strong {
		letter-spacing: -.51px;
	}



	/*MAIN BODY - BODY FONT - JUSTIFIED */
	.nedit-root:not(.style-handwritten) .editable-text:not(h1):not(h3):not(h5)[style*="text-align: justify"] {
		text-align: justify !important;
		white-space: unset !important;
	}


/* MAIN BODY -  BODY FONT  - LINE HEIGHT / line height - ALL SIZES */
.nedit-root.size-normal .editable-text {
	line-height: 1.49em;
}

.nedit-root.size-large .editable-text {
	line-height: 1.6em;
}

/* MAIN BODY -  BODY FONT - FONT FONT FONT - SANS SERIF - ALL SIZES */
.note-container {
	font-family:
		/*SegoeUI,*/
		-apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen-Sans, Ubuntu, Cantarell, Helvetica Neue, sans-serif, Apple Color Emoji, Segoe UI Emoji, Segoe UI Symbol, sans-serif;
}

/*.note-container{
    font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen-Sans,Ubuntu,Cantarell,Helvetica Neue,sans-serif,Apple Color Emoji,Segoe UI Emoji,Segoe UI Symbol;
    }*/


/*MAIN BODY - BODY FONT - BOLD - STROKE WIDTH */
.editable-text:not(h1):not(h3):not(h5) strong {
	-webkit-text-stroke-width: 0px;
}

/* MAIN BODY -  BODY FONT  - SANS SERIF - LARGE SIZE DOCUMENT */
