	align-items: center;
	justify-content: center;
	content: "EXCERPT";
	color: white !important;
	font-size: 5.6px !important;
	letter-spacing: -.9px !important;
	font-weight: bold;
}

.popup-menu.block-menu-color-picker div[data-color-name="black"] div.color-icon[style*="background-color: rgb(0, 0, 0);"]:after,
.popup-menu.block-menu-color-picker div[data-color-name="black"] div.color-icon[style*="background: rgb(0, 0, 0);"]:after {
	display: none;
}





