 POPUP MENU*/


/** POPUP - THREE DOTS MENU **/

.popup-menu.block-menu-list.block-menu-main[style*="transform: translate3d"] {
	transform: unset !important;
	will-change: unset !important;
}

/** POPUP - FLOATING TEXT SELECTION POPUP */
.floating-toolbar-container .floating-toolbar {
	display: flex;
	overflow-y: initial;
	padding-left: 4px;
	padding-right: 4px;
	background: hsl(222, 5%, 44%, 0.4) !important;
	-webkit-backdrop-filter: blur(12px);
	backdrop-filter: blur(12px);
	border: 1px solid hsl(222, 5%, 39%, 0.3) !important;
}




/**
 POPUP - LINK POPUP menu */
.link-form-container .popup-menu.link-edit {
	background-color: hsl(222, 5%, 44%, 0.4) !important;
	backdrop-filter: blur(40px);
	-webkit-backdrop-filter: blur(15px) !important;
	box-shadow: inset 0 0 0 1px hsl(222, 5%, 39%, 0.25) !important;
	border: none !important;

}

.link-form-container .popup-menu.link-edit label {
	color: hsl(212, 5%, 70%, 1) !important;
}

.link-form-container .popup-menu.link-edit input {
	background-color: hsl(222, 5%, 44%, 0.2) !important;
	color: hsl(212, 5%, 80%, 1) !important;
	font-weight: 600 !Important;
	border: 1px solid hsl(222, 5%, 39%, 0.15) !important;
	-webkit-backdrop-filter: blur(10px);
}


.link-form-container {
    z-index: 9999999999999;
}











