	/* OUTLINE - POPUP */
	.outline .popup-menu.outline-menu.position-absolute {
		left: 158px !important;
		background: rgba(50, 50, 50, 0.8);
		backdrop-filter: blur(14px);
	}

	/* OUTLINE - CONTAINER - POSITIONING */
	.outline {
		float:left;
		position: sticky;
		z-index: 100 !important;
		background: rgba(50, 50, 50,0);
		left: 0px !important;
		top: 10px;
		margin-left: -294px;
		max-width: 250px;
		/*-webkit-text-stroke-width: 0px !important;*/
		min-width: 200px;
		border-right: 1px solid rgba(0, 0, 0, 0) !important;
		border-top: none !important;
		border-left: none !important;
		border-bottom: none !important;
	}

.outline-body {
    background:  hsl(0, 0%, 1%);
    border-bottom-left-radius:4px;
    border-bottom-right-radius:4px;
    border:1px solid hsl(0, 0%, 18%);
    border-top:none;
    max-height:48.4vw;
    overflow:scroll;
                                            
}
.outline-body:before {
    content: "";
    width: calc(100% - 2px);
    height: 100px;
    background: hsl(0, 0%, 1%);
    position: absolute;
    display: block;
    left: 0;
    z-index: -1;
    top: 0px;
    border-radius: 4px;
    border:1px solid hsl(0, 0%, 18%);
}
	.grid-wrapper .outline {
		max-width: 300px;
		margin-left: 0px;
		float: none;
		z-index: 2 !important;
		overflow-y: scroll;
		overflow-x: hidden;
		top: 10px;
		position: sticky !important;
	}

	.grid-wrapper .outline .outline-container .outline-content-wrapper .outline-body ul.outline-list.outline-numbered .outline-list-item {
		font-size: 13px !important;
	}

