css:
@-moz-document url-prefix("https://mangadex.org/"), url-prefix("https://mangadex.org/chapter/")
{
  body
  {
    /*color: none;*/
    background-color: #ffe0f8;
  }

  .bg-dark
  {
    background-color: #ff8fd5;
    background-image: none;
  }

  .border-bottom
  {
    border-bottom: 1px solid #ffffff;
  }

  .card
  {
    background-color: #ffe0f8;
    border: 1px solid #ffffff;
  }

  .list-group-item
  {
    background-color: #ffe0f8;
    border: 1px solid #ffffff;
  }

  body
  {
    color: #fff;
  }

  .card-header
  {
    color: #fff;
    background-color: #ffe0fe;
    background-image: none;
    border-bottom: 1px solid #ffffff;
  }

  .nav-pills .nav-link.active, .nav-pills .show > .nav-link
  {
    color: #fff;
    background-color: #ffd4fe;
    background-image: none;
  }

  a
  {
    color: #fff;
    text-decoration: none;
  }

  .navbar-dark .navbar-nav .nav-link
  {
    color: #fff;
  }

  .form-control, .form-control:focus, .code-box
  {
    color: #fff;
    background-color: #ffc4f1;
  }

  .form-control
  {
    color: #ccc;
    background-color: #333;
    background-image: none;
    border: 1px solid #ffffff;
  }

  .form-control::placeholder
  {
    color: #fff;
    opacity: 1;
  }

  .btn-secondary:hover, .btn-secondary:focus, .btn-secondary.focus, .btn-light:hover, .btn-light:focus, .btn-light.focus
  {
    color: #fff;
    background-color: #ffc4f1;
    background-image: none;
    border-color: #004C57;
  }

  .btn-secondary, .btn-light
  {
    color: #fff;
    background-color: #ffc4f1;
    background-image: none;
    background-repeat: repeat-x;
    border-color: #ffffff;
  }

  .form-control
  {
    color: #fff;
    background-color: #ffc4f1;
    background-image: none;
    border: 1px solid #ffffff;
  }

  .dropdown-menu
  {
    background-color: #ffb8ee;
    border: 1px solid #ffffff;
  }

  .dropdown-item
  {
    color: #fff;
  }

  #reader-controls-collapser
  {
    border-left: 1px solid #ffffff;
    border-right: 1px solid #ffffff;
  }

  .bg-reader-controls
  {
    background-color: #ffb8ee;
  }
}
