css:
@-moz-document url-prefix("https://www.rescuetime.com/") {
*{
    font-family: Google Sans, sans-serif !important;
}

h2 {
	font-weight: bold !important;
}

h3 {
	font-style: normal !important;
    font-weight: bold !important;
}

h3 em {
	font-style: normal !important;
}

#page-content-wrapper {
	background-color: #fcfcfc !important;
}

HEADER {
	box-shadow: none;
}

HEADER NAV {
    background-color: white;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05) !important;
}

#nav-dashboard-link {
	display: none !important;
}

#mobile-dashboard-link > a {
	padding-left: 0 !important;
}

/* Menu item hover */
HEADER A:hover {
	color: gray !important;
}

HEADER NAV UL.nav-list>LI>A {
    color: black;
}

HEADER NAV UL.nav-list>LI#email-salutation P, HEADER NAV UL.nav-list>LI#email-salutation A {
	color: black;
}

HEADER NAV UL.nav-list LI.expandable A#report-toggle SPAN.expandable-toggle {
    display: none;
}

HEADER NAV UL.nav-list LI.expandable DIV.expandable-wrapper {
	border-radius: 10px;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.05);
}

UL#top-report-navigation Li, UL#settings-navigation Li {
	border: none;
}

SECTION#page-title-block {
    padding-top: 20px !important;
	background-image: none;
    background-color: #fcfcfc;
}

#desktop-date-picker-selector {
	border: none !important;
    background-color: white !important;
}

#desktop-date-picker-linear-back {
	background-color: #161A3B !important;
    border-radius: 7px 0 0 7px;
}

#desktop-date-picker-linear-forward {
	background-color: #161A3B !important;
    border-radius: 0 7px 7px 0;
}

SECTION#page-title-block DIV.row DIV#desktop-date-picker-wrapper DIV#desktop-date-picker DIV#desktop-date-picker-flyout DIV.date-picker-flipper {
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
    border: none;
}

#date-picker-schedule-selector {
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1) !important;
    border: none !important;
}

#date-picker-schedule-selector > a:last-child {
	border-bottom: none !important;
}

#desktop-date-picker-panel-nav li span {
    border: none !important;
}

.tappable-item-label {
    border: none !important;
    background-color: #f6f6f6 !important;
}

.tappable-item-label .spotlight-name {
	font-style: normal !important;
}

.tappable-item-panel {
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1) !important;
    border: none !important;
    margin-top: 10px;
    border-radius: 7px !important;
}

.tappable-select DIV.tappable-select-list LI A {
	border: none !important;
    padding: 10px 20px !important;
}

.tappable-select DIV.tappable-select-list LI A:hover {
	background-color: #f6f6f6;
}

.tappable-select DIV.tappable-select-list UL LI:first-child A:hover {
	border-radius: 7px 7px 0 0;
}

.tappable-select DIV.tappable-select-list UL LI:last-child A:hover {
	border-radius: 0 0 7px 7px;
}

BODY.dashboard DIV.overview-data {
	background-color: white;
    border: none !important;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05) !important;
}

.overview-item:hover {
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05) !important;
    border-radius: 5px !important;
}

#dashboard-devices-breakdown {
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05) !important;
    border-radius: 10px !important;
}

#dashboard-devices-breakdown > ul > li {
	border: none !important;
    font-family: "Helvetica Neue", sans-serif !important;
}

circle.arc-inner-circle-shadow {
	display: none !important;
}

circle.arc-inner-circle {
    fill: white !important;
	stroke: none !important;
}

svg > g:nth-child(1) > g:nth-child(3){fill:#e34663 !important;}	/*very distrating*/
svg > g:nth-child(1) > g:nth-child(4){fill:#fa69a5 !important;}	/*distrating*/
svg > g:nth-child(1) > g:nth-child(5){fill:#e9e9e9 !important;} /*neutral*/
svg > g:nth-child(1) > g:nth-child(6){fill:#69d7ff !important;}	/*productive*/
svg > g:nth-child(1) > g:nth-child(7){fill:#4294ff !important;} /*very productive*/

svg > g > path:nth-child(2){fill:#4294ff;} /*very productive*/
svg > g > path:nth-child(3){fill:#69d7ff;} /*productive*/
svg > g > path:nth-child(4){fill:#e9e9e9;} /*neutral*/
svg > g > path:nth-child(5){fill:#fa69a5;} /*distrating*/
svg > g > path:nth-child(6){fill:#e34663;} /*very distrating*/

.overview-bar[style*="background: rgb(0, 85, 196);"] {
    background: #4294ff !important;
} /*very productive*/

.overview-bar[style*="background: rgb(214, 24, 0);"] {
    background: #e34663 !important;
} /*very distrating*/

.overview-bar[style*="background: rgb(61, 128, 224);"] {
    background: #69d7ff !important;
} /*productive*/

.overview-bar[style*="background: rgb(177, 193, 191);"] {
    background: #e9e9e9 !important;
} /*neutral*/

.overview-bar[style*="background: rgb(220, 104, 90);"] {
    background: #fa69a5 !important;
} /*distrating*/


rect[style*="fill: rgb(220, 104, 90);"]
{ fill: #fa69a5 !important;}

rect[style*="fill: rgb(177, 193, 191);"]
{ fill: #e9e9e9 !important;}

rect[style*="fill: rgb(214, 24, 0);"]
{ fill: #e34663 !important;}

rect[style*="fill: rgb(0, 85, 196);"]
{ fill: #4294ff !important;}

rect[style*="fill: rgb(61, 128, 224);"]
{ fill: #69d7ff !important;}

span[style*="background: #D61800;"]
{ background-color: #e34663 !important;}

span[style*="background: #0055C4;"]
{ background-color: #4294ff !important;}

span[style*="background: #B1C1BF;"]
{ background-color: #e9e9e9 !important;}

span[style*="background: #DC685A;"]
{ background-color: #fa69a5 !important;}

span[style*="background: #3D80E0;"]
{ background-color: #69d7ff !important;}

#dashboard-insight-nav {
	background-color: #69d7ff !important;
}

#dashboard-insight-nav:hover {
	box-shadow: 0 3px 10px rgba(0, 0, 0, 0.1) !important;
}

#dashboard-insight-nav > span {
	background-color: #4294ff !important;
}

#dashboard-insight-nav > span.cycle-bullet.selected {
	background-color: white !important;
}

#goals-list > div > div {
	background-color: white !important;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.03) !important;
    border-radius: 10px !important;
}

#goals-list > div > div > div.goal-block-title-wrapper > div.goal-block-totals > div.goal-block-total-wrapper > div > small {
	text-align: left;
}


#dashboard-achievements-total > ul > li:nth-child(3) > span.achievement-value {
	color: #e34663 !important;
}

#dashboard-achievements-total > ul > li:nth-child(2) > span.achievement-value {
	color: #4294ff !important;
}

#beacon-container > button {
	background-color: white !important;
}

#dashboard-achievements-wrapper {
    background-color: white !important;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.03) !important;
    border-radius: 10px !important;
}

#dashboard-achievements-total > ul > li {
	border: none !important;
    padding: 10px 0 !important;
}

#dashboard-achievements-tabs {
    padding: 0 !important;
}

#dashboard-achievements-tabs > div {
	margin-top: 30px !important;
}

#dashboard-achievements-max > ul > li > span.achievement-value > span > a {
	font-style: normal;
}

div.alert-box {
	border-radius: 10px !important;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.03) !important;
    line-height: 1.5 !important;
    padding: 15px 20px !important;
}

#page-nav-bar > div > div > a {
	background-color: #e34663 !important;
    border-radius: 5px !important;
}

.report-actions-container UL {
	padding-bottom: 20px !important;
}

.report-actions-container UL LI IMG {
	display: none;
}

.report-actions-container UL LI {
    background-color: #65a870 !important;
	display: none;
    box-shadow: 0 5px 10px rgba(101, 168, 112, 0.2) !important;
}

.interval-items[style*="fill: rgb(214, 39, 40);"] {
	fill: black !important;
}

#device-type-tabs {
	box-shadow: none !important;
    border-radius: 10px !important;
    background-color: #f6f6f6 !important;
}

#device-type-tabs div::before, #device-type-tabs div::after {
	display: none;
}

#device-type-tabs > div.device-type-tab.selected {
	background-color: white;
    border-radius: 10px !important;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05) !important;
}
}
