css:
@-moz-document url-prefix("https://admin.karte.io/project") {
.site-switcher__list-item {
    align-items: center;
}
.site-switcher__list-item + .site-switcher__list-item {
    border-top: solid 1px #d7dcda33;
}
.site-switcher__list-item-actions {
    display: flex;
    align-items: center;
    padding: 0;
    height: 100%;
    width: 80px;
}
.site-switcher__list-item-cog {
    width: 100%;
    text-align: center;
}
.site-switcher__list-item-title {
    flex: 1 1 0;
    padding: 1em 2.75em;
    padding-right: 80px;
    font-size: 12px;
    font-weight: 600;
    font-family: "Hiragino Sans", sans-serif;
    font-feature-settings: "palt";
    letter-spacing: .05em;
}
.site-switcher__list-item-title .label--ghost {
    position: absolute;
    top: 50%;
    transform: translate(0, -50%);
    left: 0;
    box-sizing: content-box;
    border-left: 0;
    border-radius: 0 4px 4px 0;
    padding: .5em;
    width: 1em;
    white-space: normal;
    font-size: 10px;
    line-height: 1;
}
.site-switcher__list-item-url {
    max-width: 100%;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    letter-spacing: 0;
}
}
