css:
@-moz-document url-prefix("https://batch.cardsync.xyz/") {
html,
body {
    font-family: -apple-system, BlinkMacSystemFont, Roboto, "Segoe UI", Oxygen, Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;
    height: 100vh;
    display: flex;
    flex-direction: column;
    flex: 1 1 0;
    align-items: center;
    align-content: center;
    vertical-align: middle;
}

div[data-reactroot] {
    display: flex;
    flex-direction: column;
    flex: 1 1 0;
    align-items: center;
    align-content: center;
    vertical-align: middle;
}

.pure-menu.pure-menu-horizontal {
    display: flex;
    flex-direction: column;
    align-items: center;
    align-content: center;
    vertical-align: middle;
}
.pure-menu-heading {
    font-size: 1.75rem;
    margin-bottom: 0.5rem;
    font-family: inherit;
    font-weight: 500;
    line-height: 1.2;
    color: inherit;
}
.pure-menu-link,
.pure-button.pure-button-primary {
    border-radius: 5px;
    color: #fff;
    background-color: #007bff;
    border-color: #007bff;
    text-decoration: none;
    outline: 0;
}
.pure-menu-link:hover,
.pure-button.pure-button-primary:hover {
    color: #fff;
    background-color: #0069d9;
    border-color: #0062cc;
}
.pure-menu-link:focus,
.pure-button.pure-button-primary:focus {
    box-shadow: 0 0 0 0.2rem rgba(0, 123, 255, 0.5);
}

#main {
    padding-top: 10vh;
}

.pure-table thead {
    background-color: transparent;
}

.pure-table td,
.pure-table th {
    border-left: 1px solid #cbcbcb;
    border-width: 0 0 0 1px;
    font-size: inherit;
    margin: 0;
    overflow: visible;
    padding: 0.5em 1em;
    padding: 0.75rem;
    vertical-align: top;
    border-top: 1px solid #dee2e6;
    border-bottom: 2px solid #dee2e6;
}

table {
    width: 96vw;
    margin-bottom: 2vw;
    margin-right: 2vh;
    margin-left: 2vw;
}

thead tr:nth-child(2) {
    font-weight: bold;
}
}
