css:
@-moz-document domain("eurogamer.pl"), domain("eurogamer.cz"), domain("eurogamer.es"), domain("eurogamer.it"), domain("eurogamer.net"), domain("eurogamer.nl"), domain("eurogamer.pt") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #0084cc #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #0084cc !important;
}
a:hover,p,span{
    color: #fff !important;
}
*{
    box-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #0084cc !important;
}
input[type="text"]::placeholder,input[type="password"]::placeholder,input[type="email"]::placeholder,input[type="search"]::placeholder,textarea::placeholder{
    color: #fff !important;
}
input[type="submit"]:hover{
    background: #0084cc !important;
    color: #fff !important;
}
/*HEADER*/
.desktop-header{
    background: #1c1c1c;
}
.desktop-header .search .fa{
    color: #0084cc;
}
.desktop-header nav .secondary a{
    color: #1c1c1c !important
}
/*HEADER*/
/*MAINPAGE*/
.below, .document, .end,.medium-list.hd .list-item,.stack .list-item{
    background: #1c1c1c;
}
.medium-list.hd .list-item .details,.stack .list-item .details{
    background: #1c1c1c;
    border: 1px solid #0084cc;
}
/*MAINPAGE*/
/*ARTICLE*/
.article-header .metadata{
    color: #fff;
}
.article .body table thead{
    background: #0084cc;
    color: #1c1c1c;
}
.article .body figure figcaption{
    background: #1c1c1c;
    border: 1px solid #0084cc;
    color: #fff;
}
.button.outline,.button-group .button.outline:first-of-type,.button-group .button.outline.on{
    border-color: #0084cc;
    color: #0084cc;
}
.button.outline:hover,.button-group .button.outline.on:hover{
    background: #0084cc;
    color: #1c1c1c !important;
}
.article .body strong{
    color: #0084cc;
}
.article .body figure.video{
    background: #1c1c1c;
}
.article .body figure.video figcaption{
    background: #1c1c1c;
    padding: 0;
}
/*COMMENTS*/
.alert{
    background: #1c1c1c;
    border: 1px solid #0084cc;
}
/*COMMENTS*/
/*ARTICLE*/
/*SUBPAGES*/
/*TUTORIALS*/
.game-grid-block .grid-item{
    background: #1c1c1c;
}
/*TUTORIALS*/
/*REVIEWS*/
.article .body aside > div{
    background: #1c1c1c;
    width: calc(100% - 0px);
}
.article .body aside.no-image::before{
    border-left-color: #0084cc;
}
.article .body aside.no-image::after{
    border-bottom-color: #0084cc;
}
/*REVIEWS*/
/*SEARCH*/
.gsc-control-cse,.gsc-input-box{
    background: #1c1c1c;
}
input.gsc-input,.gsc-webResult.gsc-result{
    border: 0 !important;
}
.gs-no-results-result .gs-snippet{
    background: #1c1c1c;
    color: #fc3;
}
.gsc-control-cse .gs-snippet,.gsc-result-info,.gsc-results .gsc-cursor-box .gsc-cursor-page{
    color: #fff;
}
.gcsc-find-more-on-google-magnifier{
    fill: #fff;
}
/*SEARCH*/
/*LOGIN*/
.sign-in-overlay .sign-in-content > div,.form .validator{
    background: #1c1c1c;
    border: 1px solid #0084cc;
}
.form label{
    color: #0084cc;
}
.form input{
    background: #1c1c1c;
    border: 1px solid #0084cc;
    color: #fff;
}
.form input[type="checkbox"] + label{
    color: #fff;
}
/*LOGIN*/
/*COOKIE*/
.cookie-gdpr{
    background: #1c1c1c;
}
.cookie-gdpr .container-1260,.optanon-alert-box-wrapper{
    border: 1px solid #0084cc;
    background: #1c1c1c !important;
}
/*COOKIE*/
/*NEWSLETTER*/
.newsletter-promo{
    border: 1px solid #0084cc;
    background: #1c1c1c;
}
/*NEWSLETTER*/
/*SUBPAGES*/
/*FOOTER*/
footer .container-1260:after{
    content: "Dark theme by Maciejozaman";
    color: #0084cc;
    cursor: none;
}
/*FOOTER*/
}
