@-moz-document domain("fuckinghomepage.com") {
    #Footer {
        color: #f59178 !important;
        font-family: Roboto;
        font-size: 9px;
        margin-top: 125px;
        text-shadow: 2px 2px 4px #000000;
    }

    #Footer a {
        color: #ca401d !important;
        font-family: Roboto;
        font-size: 9px;
        margin-top: 125px;
        text-shadow: 2px 2px 1px #000 !important;
        text-decoration: none !important;
        font-style: normal !important;
    }

    #Header {
        border-bottom-width: 0;
        border-left-width: 0;
        border-right-width: 0;
        border-style: none;
        border-top-width: 0;
        border-width: 0;
        margin-bottom: -28px;
        margin-top: 28px;
    }

    .J9VCO {
        background-color: #576a6b;
    }

    .Title {
        -webkit-mask-image: -webkit-linear-gradient(180deg, #0000 0, #fff 69%);
        -webkit-mask-position: right;
        border-bottom-width: 4px !important;
        border-color: #8f4444 !important;
        border-left-width: 0;
        border-right-width: 0;
        border-style: solid !important;
        border-top-width: 0;
        color: #8f4444 !important;
        font-style: normal;
        font-weight: bold;
        margin-left: -169px;
        padding-bottom: 8px;
        text-shadow: 2px 2px 5px #000000 !important;
    }

    a {
        color: #dcdee0;
        font-weight: bold;
        text-decoration: none;
    }

    a.select-text-inside-a-link {
        background-color: #995454;
        border-color: #751d1d;
        border-radius: 10px;
        border-style: solid;
        border-width: 1px;
        color: #182e33;
        font-size: 18px;
        padding: 15px;
        text-shadow: 2px 2px 8px #000000;
    }

    /*
