    div#Main b {
        font-family: Lato;
        font-size: 18px;
        font-style: italic;
        letter-spacing: 4px;
        margin-left: -69px;
    }

    div#Main div.Post>a {
        background: #995454ad;
        border-color: #692d2deb;
        border-radius: 10px;
        border-style: solid;
        border-width: 2px;
        color: #182e33;
        font-family: Lato;
        font-size: 18px;
        font-weight: bold;
        letter-spacing: 3px;
        padding: 5px;
        padding-left: 69px;
        padding-right: 69px;
        text-shadow: 2px 2px 8px #000000;
        -webkit-box-reflect: below 0px -webkit-gradient(linear, left top, left bottom, from(#0000), color-stop(.7, #0000), to(#0000001a)) !important;
        opacity: 0.25;
    }

    div#Main div.Post>a:hover {
        /* 	background-image:-webkit-linear-gradient(180deg, #000, #fff); */
        background: #995454;
        -webkit-box-reflect: below 0px -webkit-gradient(linear, left top, left bottom, from(#0000), color- stop(.7, #0000), to(#0006)) !important;
        -webkit-box-shadow: 0 0 69px rgba(92, 51, 51, .95) !important;
        -moz-box-shadow: 0 0 69px rgba(92, 51, 51, .95) !important;
        box-shadow: 0 0 69px rgba(92, 51, 51, .95) !important;
        transition: 1.69s;
        filter: drop-shadow(5px 5px rgba(92, 51, 51, .95));
        opacity: 1;
    }

    div#Main p:nth-child(2)>i>small {
        line-height: 2px;
    }

    div#Main p:nth-child(even) {
        border-width: 0px;
        -webkit-mask-image: -webkit-linear-gradient(180deg, #0000 50%, #fff 69%);
        -webkit-mask-position: right;
        border-bottom-width: 1px;
        border-style: solid;
    }

    div.Post a {
        font-weight: normal;
        line-height: 34px;
    }

    div.PostBody {
        color: #6f9c85;
    }

    div.PostBody p {
        border-bottom-width: 1px;
        border-style: none;
        border-width: 0;
        color: #236770;
        letter-spacing: 1.2px;
        margin-left: -25px;
        margin-top: -2px;
        text-shadow: 1px 1px 8px #000000;
    }

    div.PostBody p b {
        -webkit-mask-image: -webkit-linear-gradient(180deg, #0000, #ffffff 15%);
        -webkit-mask-position: right;
        border-bottom-width: 1.69px;
        /* 	border-top-left-radius: 10px; */
        /* 	border-bottom-left-radius: 10px; */
        border-left-width: 0;
        border-right-width: 0;
        border-style: solid;
        border-top-width: 0;
        color: #739986;
        padding-bottom: 4px;
    }

    div.UtilityLinks {
        color: #3e4e5c;
    }

    div._3Lynt {
        background-color: #192326;
    }

    div._3ggvs {
        background-color: #203233;
    }

    h2._3LoFw {
        color: #999aa1;
        font-size: 25px;
        font-variant: small-caps;
    }

    html body {
        background-color: #1d2729;
        background-image: none;
    }

    p i small {
        font-style: normal;
        letter-spacing: 2px;
        margin-left: 15px;
    }

    p small a {
        color: #d4662a !important;
    }
}

@-moz-document domain("fuckinghomepage.com") {

    /* Random iframe shitz... */
    .iframe-controls-container:not(:hover) {
        transform: translateX(280px);
        transition: 1.69s;
    }

    .iframe-controls-container:hover {
        transition: 1.69s;
    }

    .iframe-controls-container:not(:hover) .follow-button:not(:hover) {
        padding-left: 69px;
        padding-bottom: 15px;
        padding-top: 15px;
        border-radius: 10px;
        border: 1px solid #822e2e;
        opacity: 0.68;
    }
}
