css:
@-moz-document url("https://www.animefenix.com/") {
/* Scrollbar */
    body::-webkit-scrollbar {
        width: 1em;
        background-color: hsl(0, 0%, 7%);
    }
    /* Scrollbar */
    body::-webkit-scrollbar-thumb {
      background-color: rgba(240, 240, 240, 0.2);
    }

body > div > section.section.has-background-darkx,
body > section > div,
body > div > section:nth-child(1) > div > div > div,
body > div > section:nth-child(3),
body > div > section:nth-child(2),
body > nav > div > div.navbar-brand,
#navbarBasicExample > div.navbar-start > a:nth-child(4),
#navbarBasicExample > div.navbar-start > a:nth-child(5),
#navbarBasicExample > div.navbar-start > a:nth-child(6),
#navbarBasicExample > div.navbar-start > a:nth-child(7),
#navbarBasicExample > div.navbar-start > a:nth-child(8),
#navbarBasicExample > div.navbar-end.has-text-weight-semibold.is-uppercase > div:nth-child(1),
body > div > section:nth-child(1)
{
    display: none
}

body > div > section.section.page-home__latest-series > div > h1
{
    margin-bottom: 0.5rem !important;
}

#navbarBasicExample > div.navbar-end.has-text-weight-semibold.is-uppercase
{
    position:relative;
    left:6px;
}

body > div > section:nth-child(4) > div > div
{
    position: relative;
    left:-18px;
    width:1225px!important;
}

body > div > section:nth-child(4) > div > h1
{
    margin-bottom: 0.5rem !important;
    margin-top: 0.8rem !important;
    position:relative;
    left:9px;
}

body > div > section:nth-child(4)
{
    margin: 0;
    padding: 0
}
}

@-moz-document url-prefix("https://www.animefenix.com/ver/") {
html
{
    overflow: hidden;
}

body > div > section.section.has-background-darkx,
body > section > div,
body > div > section:nth-child(1) > div > div > div,
body > div > section:nth-child(3),
body > nav > div > div.navbar-brand,
#navbarBasicExample > div.navbar-start > a:nth-child(4),
#navbarBasicExample > div.navbar-start > a:nth-child(5),
#navbarBasicExample > div.navbar-start > a:nth-child(6),
#navbarBasicExample > div.navbar-start > a:nth-child(7),
#navbarBasicExample > div.navbar-start > a:nth-child(8),
#navbarBasicExample > div.navbar-end.has-text-weight-semibold.is-uppercase > div:nth-child(1),
body > div > section:nth-child(1),
body > div > section.section.has-background-dark,
body > div > section.section.has-background-darkX > div > div > div.column.is-12-mobile.is-3-tablet.is-3-deskop > h5,
body > div > section.section.has-background-darkX > div > div > div.column.is-12-mobile.is-3-tablet.is-3-deskop > ul
{
    display: none
}

#navbarBasicExample > div.navbar-start
{
    position:relative;
    left:-182px;
}

#navbarBasicExample > div.navbar-end.has-text-weight-semibold.is-uppercase
{
    position:relative;
    left:179px;
}

body > div > section.section.page-home__latest-series > div > h1
{
    margin-bottom: 0.5rem !important;
}

body > div > section:nth-child(4) > div > h1
{
    margin-bottom: 0.5rem !important;
    margin-top: 0.8rem !important
}

body > div > section:nth-child(4),
body > div > section.section.has-background-darkX
{
    margin: 0;
    padding: 0;
}

body > div > section.section.has-background-darkX > div > div > div.column.is-12 > h1
{
    position:relative;
    top:-1px
}

body > div > section.section.has-background-darkX > div > div > div.column.is-12-mobile.is-9-tablet.is-9-desktop
{
    max-height:914px; 
}

body > div > section.section.has-background-darkX > div
{
    max-height:963px;
}

body > div > section.section.has-background-darkX > div > div > div.column.is-12-mobile.is-9-tablet.is-9-desktop > div
{
    max-height:878px;
}

body > div > section.section.has-background-darkX > div > div > div.column.is-12
{
    padding-top:1.44rem!important;
    position:relative;
    left:-170px
}

#video_player
{

    position:relative;
    top:-250px;
    left:-350px;
    height:1100px!important;
    width: 2220px!important;
}
#video_player > iframe
{

    position:relative;
    top:-252px;
    left: 171px;
    height:861px!important;
    width: 1528px!important;
}

body > div > section.section.has-background-darkX > div > div > div.column.is-12-mobile.is-9-tablet.is-9-desktop > div > div.tabs.is-toggle.is-toggle-roundedX.is-small.episode-page__servers
{
    position:relative;
    top:-251px;
    left:-179px;
    width:1528px
}

body > div > section.section.has-background-darkX > div > div > div.column.is-12-mobile.is-3-tablet.is-3-deskop > div.buttons > a.button.is-orange.is-fullwidth,
body > div > section.section.has-background-darkX > div > div > div.column.is-12-mobile.is-3-tablet.is-3-deskop > div.buttons > a.button.is-danger.is-fullwidth,
#showModal
{
    position:absolute;
    top:19px;
}

body > div > section.section.has-background-darkX > div > div > div.column.is-12-mobile.is-3-tablet.is-3-deskop > div.buttons > a.button.is-orange.is-fullwidth
{
    left:902px;
    width:10.8rem;
}

body > div > section.section.has-background-darkX > div > div > div.column.is-12-mobile.is-3-tablet.is-3-deskop > div.buttons > a.button.is-danger.is-fullwidth
{
    left:1080px;
    width:9.5rem;
}

#showModal
{
    left:1237px;
    width:7rem;
}
}

@-moz-document url-prefix("https://www.animefenix.com/animes") {
body > div > section.section.page-home__slider-container > div > div > div,
body > div > section.section.page-home__slider-container,
body > nav > div > div.navbar-brand,
#navbarBasicExample > div.navbar-start > a:nth-child(4),
#navbarBasicExample > div.navbar-start > a:nth-child(5),
#navbarBasicExample > div.navbar-start > a:nth-child(6),
#navbarBasicExample > div.navbar-start > a:nth-child(7),
#navbarBasicExample > div.navbar-start > a:nth-child(8),
#navbarBasicExample > div.navbar-end.has-text-weight-semibold.is-uppercase > div:nth-child(1)
{DISPLAY:NONE}

body > div > section.section.has-background-darkx
{
    padding-top:1rem;
}
}
