css:
@-moz-document domain("vaughn.live") {
div.mvn {
    width: 100%;
    height: 50px;
    position: absolute;
    z-index: 100;
}

div.mvn:hover ~ div.header {
    display: block;
}

div.header:hover {
    display: block;
}

div.header {
    display: none;
}

div.header_spacer {
    display: none;
}

div.height10px {
    display: none;
}

div.wrapper {
    width: 100% !important;
    max-width: 100%;
}

div.videoStatus {
    width: auto;
    position: absolute;
    z-index: 50;
    top: 40px;
    min-width: unset;
    background-color: transparent;
    border-radius: 0 8px 8px 0;
    max-width: 30%;
    height: auto;
    max-height: unset;
}
div.videoStatus:hover {background-color: #0000006e;}
div.chanStatusLeft{visibility: hidden;}
div.videoStatus:hover div.chanStatusLeft{visibility: visible;}
div.chanStatusRight{visibility: hidden;}
div.videoStatus:hover div.chanStatusRight{visibility: visible;}

div.vaughnLeft {
    width: 100% !important;
    height: 100vh;
    max-width: unset !important;
}

div.videoPlayer {
    width: 100% !important;
    height: 100vh !important;
    max-width: unset;
}

div.abvsContainer {
    display: none !important;
}

div.vidPlayer {top: 0 !important;}

div#MvnHlsPlayer_wrapper {
    width: 100% !important;
    height: 100vh !important;
}

div.MvnHlsPlayerBox {
    width: 100% !important;
    height: 100vh !important;
}

div#MvnHlsPlayer {
    width: 100% !important;
    height: 100vh !important;
}

video#MvnHlsPlayerLive {
    width: 100% !important;
    height: 100vh !important;
}

div.MvnAbvsLowerThirdWrapper {
    display: none;
}

div.MvnHlsPlayerBar {
    width: calc(100% - 10px) !important;
}
div.MvnHlsPlayerOverlay {
    width: 100% !important;
    height: 100vh !important;
    margin-top: 0 !important;
    top: 0;
}
.MvnHlsPlayerPlayButdivtonOverlay {
    display: none;
}
div.CV3wrapper {
    width: 344px !important;
    height: 100vh !important;
    background-color: white;
}
div.CV3body {
    height: calc(100vh - 75px) !important;
}
div.theChatToggle {
    height: 100vh !important;
}
div.chatBox {
    height: 100vh !important;
}
div.chatBoxHeader {
    display: none;
}
div.abvsHeaderWrapper { display: none; }
}
