css:
@-moz-document regexp("https://.*\\.luogu\\.com\\.cn.*") {
.card, .lg-article, .am-comment-main, .am-panel, .lg-table-row,.lg-mcard {
    box-shadow: 4px 4px 20px #d7d7d7!important;
    border-radius: 2.5px;
    border:none;
}
.card:hover, .lg-article:hover, .am-comment-main:hover, .am-panel:hover, .lg-table-row:hover {
    box-shadow: 6px 6px 20px #d2d2d2!important;
}
.header :not(.copy-btn):not(.copy-btn)[type="button"]{
    box-shadow: 0px 0px 10px #555;
}
.header :not(.copy-btn):not(.copy-btn)[type="button"]:hover {
    box-shadow: 0px 0px 13px #555 !important;
}
.copy-btn {
    background-color: rgb(52, 152, 219) !important;
    color: white !important;
    box-shadow: -3px 3px 5px #e2e2e2;
    border: none !important;
}
.copy-btn:hover {
    box-shadow: -3px 3px 8px #d2d2d2 !important;
}
.lfe-caption[data-v-20b7d558] {
    box-shadow: 3px -1px 5px 0px #e2e2e2;
    margin-top: 5px;
    margin-bottom: 3px;
    margin-right: 5px;
}
.lfe-caption[data-v-20b7d558]:hover {
    box-shadow: 3px -1px 8px 0px #d5d5d5!important;
}
.wrapper.wrapped.lfe-body {
    box-shadow: 0px -4px 7px #c8c8c8;
}
[data-v-27b2cd59][data-v-8a79b9a2][data-v-be2136da] {
    box-shadow: 0px 0px 20px -8px #555;
}
.wrapper.wrapped.lfe-body.header-layout.tiny[data-v-7958fe72][data-v-be2136da] {
    box-shadow: 0px 3px 8px #c8c8c8;
}
.lfe-form-sz-middle[data-v-370e72e2][data-v-05df4462][data-v-72107c51] {
    box-shadow: rgb(216, 216, 216) 4px 4px 4px;
}
.lfe-form-sz-middle[data-v-370e72e2][data-v-05df4462][data-v-72107c51]:hover {
    box-shadow: rgb(216, 216, 216) 2px 2px 3px;
}
button[data-v-25aefbae], .am-pagination.am-pagination-centered a {
    border-radius: 50px !important;
}
.am-pagination.am-pagination-centered a {
    width:auto;
    height:30px;
    padding: 4px 9px 4px 9px;
}
button[data-v-25aefbae][class], .am-pagination.am-pagination-centered a {
    box-shadow: 2px 2px 5px #d5d5d5;
}
button[data-v-25aefbae][class]:hover, .am-pagination.am-pagination-centered a:hover {
    box-shadow: 2px 2px 8px #d2d2d2;
}
button[data-v-25aefbae]:not(.selected)[class], .am-pagination.am-pagination-centered>li:not(.am-active):not(:first-child):not(:last-child)>a:not([rel="next"]):not([rel="prev"]) {
    background: var(--lfe-color--primary);
    border-color: var(--lfe-color--primary);
    cursor: default;
    color: #fff !important;
}
button[data-v-25aefbae]:not(.selected)[class]:hover, .am-pagination.am-pagination-centered>li:not(.am-active):not(:last-child)>a:not([rel="next"]):not([rel="prev"]):hover {
    background: #347fdb;
    border-color: #347fdb;
}
button[data-v-25aefbae].selected, .am-pagination.am-pagination-centered>li.am-active>a {
    background: #5E72E4;
    border-color: #5E72E4;
    cursor: default;
    color: #fff !important;
}
.selected[data-v-8feadc5c] {
    margin-bottom: 5px;
    box-shadow: 3px 3px 5px #ddd;
}
.am-btn, .btn-follow, .am-input-group-label,button:not(.copy-btn)[type="button"] {
    box-shadow: 3px 3px 5px #ddd;
}
.am-btn:hover, .btn-follow:hover, .am-input-group-label:hover,button:not(.copy-btn)[type="button"]:hover {
    box-shadow: 3px 3px 7px #c7c7c7 !important;
}
.am-input-group-label {
    border-radius: 0px 5px 5px 0px;
}
.am-comment-primary .am-comment-main:before, .am-comment-danger .am-comment-main:before {
    border: none;
}
.am-comment-main {
    border: none;
}
[data-v-42d1070d][data-v-f469cfb2][data-v-27b2cd59].popup {
    border: none;
    box-shadow: 0 0 20px -10px #333;
}
input, .text.lfe-form-sz-small,textarea {
    border:none !important;
    box-shadow: 0 0 5px 0px #ddd;
}
input:focus, .text.lfe-form-sz-small:hover,textarea:hover {
    border-bottom:none !important;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 5px rgba(59, 180, 242, .3) !important;
}
input:focus::-webkit-input-placeholder,textarea:focus::-webkit-input-placeholder {
    text-align: right;
    font-size: 25%;
}
::-webkit-scrollbar-track-piece {
    background-color:#f8f8f8 !important;
}
::-webkit-scrollbar {
    width:4px !important;
    height:4px !important;
}
::-webkit-scrollbar-thumb {
    background-color:#dddddd !important;
    background-clip:padding-box !important;
    min-height:28px !important;
}
::-webkit-scrollbar-thumb:hover {
    background-color:#bbb !important;
}
.search-wrap[data-v-034eb354] input {
    background:black;
    color:white;
    border-radius: 3px;
}
.am-slider-default .am-direction-nav a {
    z-index:0;
}
.feed-selector a{
    border: solid 1px #3498db;
    border-radius: 5px !important;
}
.feed-selector.am-active a {
    box-shadow: 3px 3px 6px #c5c5c5;
}
.feed-selector:not(.am-active) a:hover {
	 box-shadow: 3px 3px 6px #c5c5c5;
    background: #3498db;
    color:white;
}
.wrapper.wrapped.lfe-body.header-layout .background,.wrapped.lfe-body.header-layout.narrow{
    filter: blur(0px) brightness(100%) !important;
    background-repeat: no-repeat !important;
    background-position: 50% 50% !important;
    background-size: cover !important;
    background-image: url(http://free-cn-01.cdn.bilnn.com/ddimg/jfs/t1/110000/31/9713/173635/5e75e8d1E06bfdd7a/819bfa6064502d88.jpg), linear-gradient(90deg, rgb(91, 165, 197), rgb(207, 148, 197)) !important;
}
.wrapper.wrapped.lfe-body.header-layout {
	  box-shadow: 0px -4px 11px black;
}
.wrapper.wrapped.lfe-body:not(.header-layout) .background[data-v-7958fe72]{
    background: rgb(91, 165, 197) !important;
    filter: blur(0px) brightness(100%) !important;
}
nav.lfe-body {
    background-color: rgb(255, 255, 255) !important;
    color: rgb(91, 165, 197) !important;
	 box-shadow: 0 0 15px -8px black;
}
.lfe-body[data-v-27b2cd59][data-v-8a79b9a2][data-v-be2136da]>div[data-v-27b2cd59]:not(.popup-button) {
    background-color: rgb(91, 165, 197) !important;
}
.progress-frame.rate {
    border:none;
    border-radius:5px;
    box-shadow: 0px 0px 4px #d5d5d5;
}
.rate-popup {
    z-index: 1000000 !important;
}
.test-case {
    box-shadow: 0 0 12px -5px black;
    border-radius: 5px;
}
.test-case:hover {
    box-shadow: 0 0 10px -3px black;
}
.am-comment-avatar {
    border: none;
}
.lfe-form-sz-middle, .lfe-form-sz-middle:hover{
    border: none !important;
    box-shadow: none !important;
}
}
