css:
@-moz-document domain('nltk.org'), url-prefix('https://www.nltk.org/book/') {
:root {
  --regular-text: #e3e3e3;
}

html, body{
    background-color: black;
    color: var(--regular-text);
    display: block;
    margin-left: auto;
    margin-right: auto;
    width: 85%;
}

img {
    filter: invert(100%);
}

/*Special blocks*/
div.admonition, div.attention, div.caution, div.danger, div.error, div.hint, div.important, div.note, div.tip, div.warning, div.system-message {
    background-color: #150000;
	border: none;
}

div.note {
	border: #000 1px solid;
	padding: 4px;
	margin: 2px 5% 10px;
	font-style: normal;
	font-family: verdana, arial;
	background-color: #001d26;
}

pre.literal-block, pre.doctest-block {
	padding: 4px;
	margin-left: 2em;
	margin-right: 2em;
	background-color: #261400;
}

/*Python code*/
table.pylisting {
	border: 1px solid #000;
}

td.codeblock table tr td.copybar {
	background: #006823;
	border: 1px solid #0000;
	font-family: monospace;
	padding: 0;
	margin: 0;
}

table tr td.codeblock{
    background-color: #000f00;
    border: 1px solid black;
}

td.doctest table tr td.copybar {
	background: #39008e;
	border: 1px solid #0000;
	font-family: monospace;
	padding: 0;
	margin: 0;
}

table.doctest {
	margin: 0;
	padding: 0;
	border: 1px solid black;
}

table tr td.doctest {
	background-color: #05000f;
    border: 1px solid black;
}

/*Syntax highlighting*/
span.pysrc-output {
	color: #00b3ff;
}
}
