@-moz-document domain("re-library.com") {
html, body, hr {
    font-family: "/*[[rlfont]]*/", sans-serif !important;
    background-color: #111111 !important;
    background-image: none !important;
    scrollbar-color: #444444 #222222;
}

div#content.site-content,
div#comments.comments-area {
    margin: 0 auto !important;
}
div.content-sidebar,
div#ezmobfooter.ezmob-footer {
    display: none;
}

div#main.site-main,
div#content.site-content,
article.page.type-page.hentry[id^="post-"],
div.entry-content {
    background-color: #222222 !important;
    color: #d4d4d4;
}
.entry-header,
.entry-title,
.entry-title a {
    background-color: #222222 !important;
    color: #778899 !important;
    font-style: italic !important;
}

div.entry-content table,
div.entry-content table tr td,
div.entry-content table.light,
div.entry-content table tr td.light {
    background-color: #111111 !important;
    border: 1px solid #444444; 
}
div.entry-content table tr td,
div.entry-content table tr td.light,
div.entry-content table tr td span,
div.entry-content table tr td.light span {
    color: #778899 !important;
    font-size: 12pt !important;
}

span.ezoic-ad.adtester-container, 
span.ezoic-ad[ezaw] {
    border: none !important;
    min-height: 0 !important;
    max-height: 10px !important;
    height: 10px !important;
}

div#main.site-main div#content.site-content article.page.type-page.hentry[id^="post-"] div.entry-content p {
    font-size: /*[[rlsize]]*/;
    line-height: 1.4em;
}

.su-button-style-default span,
.su-button-style-default span.light {
    border: none; 
    background-color: #111111 !important;
    color: #778899 !important;
}
}
