a {
    color: rgb(140, 140, 250);
}
::-webkit-scrollbar-track-piece {
    background-color: rgba(255, 255, 255, 0.2) !important;
}
::-webkit-scrollbar-track {
    background-color: rgba(255, 255, 255, 0.3) !important;
}
::-webkit-scrollbar-thumb {
    background-color: rgba(255, 255, 255, 0.5) !important;
}
embed[type="application/pdf"] {
    filter: invert(90%);
}

html {
    color: rgb(191, 191, 191);
    background: rgb(31, 31, 31) !important;
}
body {
    background-color: rgb(31, 31, 31);
    font-family: "Fira Code", Consolas, Menlo, monospace, arial, sans-serif;

}
input,
select,
textarea,
button {
    font-family: "Fira Code", Consolas, Menlo, monospace, "Lucida Grande", Tahoma, Arial, sans-serif;
    color: rgb(191, 191, 191);
    background-color: rgb(31, 31, 31);
}
font {
    color: rgb(191, 191, 191);
}

html {
    filter: contrast(100%) brightness(100%) saturate(100%);
}

img[src="sqlpponline.png"] {
  visibility: collapse;
}

img[src="/images/banners/sqlpp_ssms_780_90.png"] {
  visibility: collapse;
}

