@-moz-document domain(scrim.app) {

*, :after, :before {
    border-width: 0;
    border-style: solid;
    border-color: #000000;
    color: #859900;
}

.navigation[data-v-a84eabd2] {
    background-color: #002b36;
    min-width: -webkit-fit-content;
    min-width: -moz-fit-content;
    min-width: fit-content;
}

body {
    background: #073642;
}

.bg-white {
    background-color: #002b36;
}

.text-gray-600 {
    color: #000000;
}

.text-gray-800 {
    color: #fdf6e3;
}

.text-green-500 {
    color: #268bd2;
}

.text-gray-700 {
    color: #2aa198;
}

.text-lg {
    font-size: 1.125rem;
    color: #268bd2;
    background: #073642;
}

.avatar[data-v-557d41c5] {
    width: 64px;
    height: 64px;
    background-color: #657b83;
}

.focus\:outline-none:focus, .hover\:outline-none:hover, .outline-none {
    outline: 0;
    background: #073642;
}

.bg-gray-100 {
    background-color: #073642;
}

.modal[data-v-1c59aa7c] {
    padding: 1rem;
    background-color: #073A42;
    border-width: 2px;
    overflow-y: auto;
    overflow-x: hidden;
    z-index: 40;
    width: 420px;
    min-height: 50vh;
    max-height: 80vh;
}

.theme-light {
    --bg-background-primary: #002b36;
    --bg-background-secondary: #073642;
    --bg-background-tertiary: #000000;
    --text-title-primary: rgba(50,54,57,0.75);
    --text-title-secondary: #268bd2;
    --text-title-tertiary: rgba(50,54,57,0.5);
    --text-body-primary: rgba(61,63,66,0.7);
    --text-body-secondary: rgba(61,63,66,0.9);
    --text-body-tertiary: rgba(61,63,66,0.5);
}
}
