css:
@-moz-document url-prefix("https://www.cannaweed.com/") {
/*modif le fond du site en noir*/
body {background: #000000;}

/*modif le fond d'une case sur 2 des colonnes de chaque coté de la page des forums*/
.ipsAreaBackground_reset {
    background:#313131 ; 
    border-width: 1px;
    border-color: black;
    border-style: solid;} 

.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected ):not( .ipsModerated ):not( .ipsDataItem_new ):not( .ipsDataItem_success ):not( .ipsDataItem_warning ):not( .ipsDataItem_error ):not( .ipsDataItem_info ):not( .ipsDataItem_status ):nth-child(even) {
    background: #313131;}

/*modif l'espace entre les sujets*/
.ipsDataList_large .ipsDataItem_main, .ipsDataList_large .ipsDataItem_stats, .ipsDataList_large .ipsDataItem_lastPoster, .ipsDataList_large .ipsDataItem_generic {
    border: 1px;
    border-style: solid;
    border-color: black;
    border-radius:2px;
    background:#1f1f1f;}

/*modif l'espace entre les cases de colonne de gauche*/
html[dir="ltr"] .ipsDataList_large .ipsDataItem_icon {
    padding-right: 15px;
    border: 1px;
    border-color: black;
    border-style: solid;}

/*modif l'espece de la topiclist*/
.cTopicList .ipsDataItem_main {
    padding: 12px 10px;
    border-width: 1px;
    border-color: black;
    border-style: solid;}

/*modifie l'espace colonne a gauche de topiclist*/
html[dir="ltr"] .cTopicList .ipsDataItem_icon {
    min-width: 35px;
    border-style: solid;
    border-width: 1px;
    border-color: #000;}

/*modif l'espace colonne a droite des topic liste*/
.ipsDataItem_stats {
    width: 15%;
    min-width: 150px;
    text-align: right;
    list-style: none;
    border-style: solid;
    border-width: 1px;
    border-color: #000;}

.ipsDataItem_lastPoster {
    width: 24%;
    max-width: 24%;
    min-width: 270px;
    list-style: none;
    margin: 0;
    padding: 0;
    border-style: solid;
    border-width: 1px;
    border-color: #000;
}

/*décale vers la droite le contenu des messages forums*/
.cPost .cPost_contentWrap {
    position: relative;
    padding: 7px 15px 15px;}

/*modif lefond descolonnes forums*/
.ipsDataList_large .ipsDataItem_main, .ipsDataList_large .ipsDataItem_stats, .ipsDataList_large .ipsDataItem_lastPoster, .ipsDataList_large .ipsDataItem_generic {
    background-color: #1f1f1f;} 


/*modif le bouton rond a gauche des forums*/
.ipsItemStatus.ipsItemStatus_large {
    background: #000000;}
.fa-comments:before {
    content: "\f086";
    color: #359c39;}
.fa-circle:before {
    content: "\f111";
    color: #359c39;
}



#ipsLayout_contentWrapper {width: 100%; background-color: #3c3c3c;}

.ipsType_blendLinks, .ipsType_blendLinks a {color: #359c39;}

.ipsType_light {color: #359c39;}

.ipsType_pageTitle {color: #fdfdfd;}

.ipsBreadcrumb > ul > li {color: #ffffff;}

.ipsBreadcrumb[data-action="defaultStream"], .ipsBreadcrumb[data-action="markSiteRead"] {color: #ffffff;}

/*modif la couleur des polices en blanc dans les contenu*/
a, input, textarea, button {color: #ffffff;}

.ipsButtonBar {background: #000000;}

.ipsButtonBar .ipsPagination .ipsPagination_pageJump a {color: #f9f9f9;}

.ipsPagination.ipsPagination_mini a {color: #ffffff;}

.ipsBox:not(.ipsBox_transparent):not(.ipsModerated) {background-color: #585858;}

/*change la couleur du de fond des cadre poilers*/
.ipsSpoiler_header {background: #000;}

/*change le fond de spoiler ouvert*/
.ipsSpoiler, .ipsStyle_spoiler {background: #3c3c3c;}

.ipsApp .ipsType_light .ipsButton, .ipsApp .ipsButton_link.ipsFollow, .ipsApp .ipsButton_link.ipsPromote {background: #359c39;}

.ipsApp .ipsButton_link {background: #359c39; font-size: 18px;
    font-family: "Roboto Slab", serif;
    background: #359C39;
    color: #ffffff;
    border-radius: 10px;
    padding:0px 20px;
    border: 0;
    font-weight: bold;
    display: inline-block;
    vertical-align: middle;
    text-align: center;}

.btn, .ipsToolList_primaryAction .ipsButton {
    font-size: 18px;
    font-family: "Roboto Slab", serif;
    background: #359C39;
    border-radius: 10px;
    padding: 0px 20px;
    border: 0;
    box-shadow: none;
    font-weight: bold;
    display: inline-block;
    vertical-align: middle;
    text-align: center;}

.ipsApp .ipsButton.cMultiQuote {
    line-height: 22px;
    padding: 0 6px;
    background-color: #359c39;}

.ipsType_richText p:not( [class] ) {
    margin-top: 0;
    margin-bottom: 0;
    color: white;}

.ipsColumns.ipsColumns_noSpacing > .ipsColumn:not( .ipsPad ) {
    padding-right: 0;
    color: white;
    background-color: #1f1f1f;
    border-width: 2px;
    border-color: #000000;
    border-style: solid;}

body.ipsApp blockquote {
    margin: 0;
    padding: 0px 15px;
    border-width: 1px 1px 1px 2px;
    border-color: #ededed #e8e8e8 #dbdbdb #666666;
    border-style: solid;
    position: relative;
    background: #3c3c3c;
    clear: both;}

.ipsQuote_citation {
    background: #060606;
    margin: 0px -15px;
    padding: 5px 15px;
    color: #fff;
    font-weight: bold;
    font-size: 13px;
    display: block;}
    
    .ipsQuote .ipsQuote_citation a {
    color: #359c39;
    text-decoration: none;}

    .item__content {
    color: #ffffff;
    bottom: 33px;
    left: 33px;
    width: 55%;
    padding: 16px 20px;
    background: #000;
    transition: color .4s ease-out, background .4s ease-out;}

.ipsType_sectionTitle {
  font-size: 15px;
  font-weight: 400;
  padding: 11px 15px 11px 15px;
  margin-top: 0;
  background: #000000;
  color: #ffffff;
  border-radius: 2px 2px 0px 0px;
  font-family: "Roboto Slab", serif;}

*, .ipsApp input[type="search"] {-webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box; color: #efefef;}

.footer__bottom {background: #000000;}
                                        
.footer__background {background: #000000;}

.footer__advertisement {background: #000;}


/*modif fb, twitter vimeo en noir*/
.fa-twitter:before {content: "\f099"; color: black;}

.fa-facebook-f:before, .fa-facebook:before { color: #000;}

.fa-vimeo:before {color: #000;}



.ipsBreadcrumb [data-action="defaultStream"], .ipsBreadcrumb [data-action="markSiteRead"] {font-size: 12px; font-weight: 400; color: #f9f9f9;}  



.ipsToggle.ipsToggle_off {background: #000000;}

.nav {background: #000000;}

.soc__image {
    width: 47px;
    height: 47px;
    background: #ffffff;
    line-height: 53px;
    text-align: center;
    color: #000000;
    display: inline-block;
    vertical-align: middle;
    margin-right: 19px;
    transition: background .3s linear; }

html[dir="ltr"] .ipsComment_tools.ipsList_inline > li {
    margin-right: 0;
    margin-left: 10px;
    background-color: #359c39;
    color: white;
    border-radius: 5px;}

html[dir="ltr"] .ipsComment_controls > li {
    margin-right: 15px;
    margin-left: 15px;
    background-color: #359c39;
    border-radius: 10px;
    padding: 0px 20px;}

.ipsPagination:not( .ipsPagination_mini ) .ipsPagination_page a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_next a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_prev a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_first a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_last a {
    padding: 0 10px;
    border-radius: 2px;
    font-weight: bold;
    text-align: center;
    background: #1f1f1f;
    color: #ffffff;
    vertical-align: middle;
    text-decoration: none;}

.cke_wysiwyg_frame, .cke_wysiwyg_div {
    background-color: #1f1f1f;}

.ipsApp .ipsField_autocomplete, .ipsApp textarea, .ipsApp input[type="text"], .ipsApp input[type="password"], .ipsApp input[type="datetime"], .ipsApp input[type="datetime-local"], .ipsApp input[type="date"], .ipsApp input[type="month"], .ipsApp input[type="time"], .ipsApp input[type="week"], .ipsApp input[type="number"], .ipsApp input[type="email"], .ipsApp input[type="url"], .ipsApp input[type="search"], .ipsApp input[type="tel"] {
    background-color: #1f1f1f;
    border-width: 1px;
    border-style: solid;
    border-color: rgba(0,0,0,0.2);
    border-radius: 3px;
    width: 100%;
    max-width: 350px;
    padding: 7px;}

.ipsRichEmbed_header {
    border-bottom: 1px solid rgba(0,0,0,0.05);
    padding: 10px 45px 10px 15px;
    background-color: #000;}

.ipsPad, .ipsApp ul.ipsPad, .ipsApp ol.ipsPad {
    padding: 7px;
    background-color: #3c3c3c;}


                                           /*modif le cadre de l'editeur rapide en noir*/
.ipsComposeArea_editor {background: #000;}

                                          /*modif le petit fond fichiers joint sous l'editeur*/
.ipsComposeArea_dropZone {
    padding: 12px;
    background-color: #1f1f1f;}

                                         /*modif la couleur des boutons de l'éditeur en vert*/

.cke_reset_all, .cke_reset_all *, .cke_reset_all a, .cke_reset_all textarea { background: #359c39; }

                                         /*modif le fond derriere l'editeur rapide*/
.ipsApp .ipsSpacer_top, .ipsApp .ipsSpacer_both {background-color: #3c3c3c;}

                                  /*modif la couleur du haut de la boite de dialogue en vert*/
.ipsDialog.ipsDialog_medium > div {background-color: #359c39;}



                                   /*modif les cardres entourant la liste des pseudos dans la boite de dialogue qui a aimé?*/

html[dir="ltr"] .ipsPhotoPanel.ipsPhotoPanel_mini > div {border-radius: 10px;}  

                                   /*modif le fond de la boite de dialogue qui a aimé? en noir*/
ul.ipsGrid, ol.ipsGrid {
    padding: 0;
    margin: 0;
    background-color: #151515;} 



                                     /*modif la couleur en dégrader pour la boite de notification*/
.ipsMenu_headerBar, .ipsMenu_footerBar {
    background: linear-gradient(to bottom, #181818 30%, #f2f4f7 100%);   
    padding: 10px;
    line-height: 24px;}

                                    /*modif la couleur de fond de la boite de notification*/
.ipsDataItem_main {background-color: #313131;}

                                     /*modif la couleur de fond des icones avatar dans la boite de notification et aussi ailleurs lol*/
html[dir="ltr"] .ipsDataItem_icon {
    padding-right: 0;
    background-color: #313131;}

                                                 /*les stats en verts*/
.ipsDataItem_stats dt, .ipsDataItem_stats dd {
    margin-left: 0;
    color: #359c39;}

                                    /*modif en noir les cadres des news*/
.news_big .news__content, .news__content {
    background-color: black;}

                                     /*modif en vert le titre des news*/
.news_big .news__title {
    color: #359c39;}

                                     /*modif vert petit titre des news*/
.news__title {
    color: #359c39;
    font-size: 1.3rem;
    font-weight: bold;
    font-family: "Roboto Slab", serif;
    display: inline-block;
    vertical-align: middle;
    margin-bottom: 10px;
    transition: color .3s linear;
    line-height: 26px;}

                                    /*modif le bouton ajouter un com des news*/
.news .btn_comment {
    left: auto;
    right: 17px;
    margin-bottom: 0px;
    color: black;}

                                    /*petit cadre en noir sur news*/
.news__tags a, .news__tags span {
    margin: 7px 5px 0;
    padding: 4px 9px;
    background: #000;
    color: #f1eded;}

/*modif le bouton en savoir plus des news*/


                                    /*modif la virgule en vert sous le bouton ajouter un com*/
.btn_comment:before {
    position: absolute;
    content: "";
    top: 100%;
    left: 14px;
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 10px 15px 0 0;
    border-color: #359c39 transparent transparent transparent;
    transition: border .3s linear;}
    
                                    /*modifi le fond boite de dialogue sur pseudo en haut a gauche*/
.ipsMenu_title {background: linear-gradient(to bottom, #000 30%, #f2f4f7 100%)}

.ipsMenu_item > a:not( .ipsMenu_itemInline ), .ipsMenu_item > span:not( .ipsMenu_itemInline ) {
    color: #f3e6e6;
    background-color: #313131;}


}
