css:
@-moz-document domain("www.nitrotype.com")
{
  body {
    background: none;
}
html {
    background: fixed url("https://i.imgur.com/qZkgQq8.jpg"), #001d45;
    background-size: 100%;
}
.bg--gradient {
    background: fixed;
}
.theme--pDefault .profile-bg
  {
    background: url("https://imgur.com/YOTPzj0.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
    background-size: 100%
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://i.imgur.com/YOTPzj0.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
   background-size: 100%
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://i.imgur.com/YOTPzj0.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
   background-size: 100%
  }
  .structure.theme--season28 
  {
      background-image: none;
  }

  .card--a
  {
    background-color: #fff0;
    color: #ffffff;
  }

  .card--shadow-l
  {
    box-shadow: none;
  }
  .theme--pDefault .profile-levelNum {
    color: #ffffff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
}
.news--solo .news-body p {
    color: #ffffff;
    line-height: 1.8;
}
.event--holiday .header:before, .event--holiday .header-core::before, .event--holiday .header-core::after 
{
    display: none;
}
.news-teaser {
    color: #ffffff;
    margin-bottom: 40px;
}
.prog-points {
    bottom: 100%;
    color: #000;
    font-size: 12px;
    font-weight: 600;
    left: 0;
    position: absolute;
    text-align: right;
    text-shadow: 0 1px 10px rgba(2, 2, 2, 0.14);
    white-space: nowrap;
    width: 100%;
}
.theme--pDefault .profile-head {
    color: #000;
}
.theme--pDefault .profile-levelNum {
    color: #000000;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
}
.card--a {
    color: #ffffff;
    background: transparent;
}
.card--shadow-l {
    box-shadow: none;
}
.btn--inky {
    color: #fff;
}
.card--b {
    color: #fff;
}
.theme--pDefault .profile-body {
    color: #ffffff;
}
.card--grit {
    background: none;
}
.dash-copyContainer {
    background: #1d1e23;
    box-shadow: 0 1px 157px 10px rgba(0, 0, 0, 0.78);
}
.dash-copy {
    color: #2E9AFE;
    font-size: 28px;
}
}
