css:
@-moz-document domain('www.garsoniera.com.pl') {

body, html {
    background: none;
    background-color: #262626;
}
body {
    color: #CCC;
}
#user_navigation a {
    color: #E4E7EC;
}
#branding {
    background-color: #262626;
}
#ipbwrapper {
    background: none;
    background-color: #555;
}
.maintitle {
    background-color: #262626;
}
.bar {
    background-color: #262626;
}
.ipsSideBlock h3 {
    background-color: #262626;
}
.general_box h3 {
    background-color: #262626;
}
.statistics_head {
    background: none;
    background-color: #262626;
    border-bottom: 1px solid #bfbfbf;
}
.statistics_head, .statistics_head a {
    color: #AAA;
}
#vnc_filter_popup_close {
    background-color: #262626;
}
body#ipboard_body fieldset.submit {
    background-color: #262626;
}
body#ipboard_body p.submit {
    background-color: #262626;
}
.ipsForm_submit {
    background-color: #262626;
}
.post_block h3 {
    background: none;
    background-color: #262626;
}
.column_view .post_wrap {
    background: none;
    background-color: #262626;
}
.pagination .pages li.active {
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    background-color: #333;
}
ul.post_controls {
    background-color: #262626;
}
ul.post_controls a {
    background-color: #262626;
}
.pagination .page a {
    background-color: #262626;
}
.pagination .back a {
    background-color: #262626;
}
.pagination .forward a {
    background-color: #262626;
}
.topic_buttons li a {
    background-color: #262626;
}
.ipsButton {
    background-color: #262626;
}
ul.mini_pagination li a {
    background-color: #262626;
}
#backtotop {
    background-color: #262626;
}
#bottomScroll {
    background-color: #262626;
}
#board_stats .value {
    background-color: #262626;
}
#primary_nav {
    background-color: #262626;
}
.maintitle {
    border-color: #262626;
}
.general_box h3 {
    color: #262626;
}
.general_box h3 a {
    color: #262626;
}
.bar {
    color: #262626;
}
.bar a {
    color: #262626;
}
.post_block h3 {
    color: #262626;
}
.post_block h3 a {
    color: #262626;
}
.ipsHeaderMenu {
    background-color: #262626;
}
a {
    color: #DDD;
}
a:hover {
    color: #FFF;
}
#user_navigation a#user_link.menu_active, #user_navigation a#notify_link.menu_active, #user_navigation a#inbox_link.menu_active {
    background-color: #555;
    color: #eee;
}
.input_text, .ipsTagBox_wrapper, textarea {
    background: url('http://www.garsoniera.com.pl/forum/public/style_images/elegant/_custom/transwhite.png') repeat;
    color: #ccc;
    border: 1px solid #666;
}
textarea:focus, .input_text:focus {
    background-color: #fefefe;
    color: #555;
    border-color: #a0a0a0;
}
.ipsButton_secondary, .bbc_spoiler_show, .user_controls li a {
    background-color: #333;
    color: #ccc;
    border: 1px solid #666;
}
.ipsButton_secondary:hover, .bbc_spoiler_show:hover, .user_controls li a:hover {
    background-color: #222;
    color: #eee;
}
#search_options_menucontent {
    background: #333;
}
#search_options_menucontent label:hover {
    background: #666;
}
#secondary_navigation a {
    color: #CCC;
}
#secondary_navigation span {
    color: #DDD;
}
#secondary_navigation span.navsep {
    color: #DDD;
}
#secondary_navigation a:hover {
    color: #EEE;
}
#secondary_links img {
    opacity: 0.9;
}
textarea:focus, .input_text:focus {
    background-color: #444;
    color: #eee;
    border-color: #666;
}
.ipsButton_extra {
    color: #ccc;
}
.ipsType_pagetitle, .ipsType_subtitle {
    color: #fff;
}
.desc.lighter, .desc.lighter.blend_links a {
    color: #ddd;
}
.pagination a {
    color: #999;
}
.pagination .pagejump a:hover {
    color: #bbb;
}
/*
.topic_buttons li.non_button a {
    color: #999;
}
*/
.topic_buttons li a:hover, .ipsButton:hover {
    opacity: 1;
    color: #bbb;
}
.border, .statistics, .post_block, .ipsComment, .popupInner, .no_messages, .poll_question ol, .ipsBox_container, .ipsFloatingAction, .column_view .post_body {
    background: none;
    background-color: #555555;
}
.column_view .post_body {
    background-color: #262626;
}
.post_body {
    color: #eee;
}
.bbc_url, .bbc_email {
    color: #55a4f7;
}
p.citation {
    background: none;
    background-color: #262626;
    color: #ddd;
}
blockquote.ipsBlockquote {
    background: inherit;
    color: inherit;
}
.edit {
    color: #999;
    background-color: inherit;
}
.custom_fields .ft {
    color: #666;
}
.post_block:first-of-type h3 {
    border-top: 1px solid #bfbfbf;
}
.post_block h3 {
    border-top: 1px solid #bfbfbf;
    border-bottom: 1px solid #bfbfbf;
}
.post_block h3, .post_block h3 a {
    color: #777;
}
.post_date, div.post_username a {
    color: #ddd;
}
ul.post_controls {
    background: none;
    background-color: #333;
    border-top: 1px solid #bfbfbf;
}
.desc, .desc.blend_links a, p.posted_info {
    color: #AAA;
}
.author_info .group_title {
    color: #ddd;
}
.maintitle {
    display: none;
}
.sb_titlebox {
    border-bottom: 2px solid #bfbfbf;
}
.breadcrumb {
    font-size: 11px;
}
#conversation .topic_controls {
    border-right: 1px solid #bfbfbf;
    border-left: 1px solid #bfbfbf;
}

#conversation .topic_controls:first-of-type {
    border-right: none;
    border-left: none;
}
.ipsSideBlock {
    background: inherit;
}
.popupInner h3 {
    background: inherit;
    background-color: #333;
}
.popupInner h3 a {
    color: #eee;
}
.bar, .ipsSideBlock h3, .general_box h3 {
    background: inherit;
    background-color: #262626;
    border-bottom: 1px solid #bfbfbf;
}
.ipsSideBlock h3, .ipsSideBlock h3 a {
    color: #ddd;
}
#vnc_filter_popup_close, body#ipboard_body fieldset.submit, body#ipboard_body p.submit, .ipsForm_submit {
    background: none;
    background-color: #555;
}
#message_table {
    border-top: 1px solid;
}
.ipsType_sectiontitle {
    color: inherit;
}
.highlighted, .highlighted .altrow {
    background-color: #444;
}    
.unread {
    background-color: #333;
}
table.ipb_table {
	border-top: 1px solid;    
}
#fast_reply_wrapper, #postingform {
	border-top: 1px solid #bfbfbf;    
}
table.ipb_table th {
    background: #BFBFBF;
}
.userpopup .side {
    margin-top: -28px;
}
.ipsBox {
    background: none;
    background-color: #262626;
}
.ipsPostForm_sidebar .ipsPostForm_sidebar_block:first-of-type h3.bar, .bar.noTopBorder {
    color: #777;
}
.ipsType_pagedesc {
    color: #999;
}
.ipsSideMenu h4 {
    color: #eee;
}
#footer_utilities, #footer_utilities a {
    color: #666;
    text-shadow: none;
}
.row1, .post_block.row1 {
    background-color: inherit;
}
.header {
    background: none;
	background-color: #262626;
    color: #444;
    text-shadow: none;
}
.row2, .post_block.row2 {
    background-color: inherit;
}
#cblock_right .general_box h3, #cblock_right .general_box h3 a {
    color: #ccc;
}
#cblock_right .row2, #cblock_right .post_block.row2 {
    background-color: #555;
}
#cblock_right .header .row1.altrow.cwd {
    color: #ccc;
}
p > span[style="color:#0000ff"] {
    color: #e9e9ff !important;
    font-weight: bold;
}
.ipsVerticalTabbed_tabs li {
    background: inherit;
    color: inherit;
}
.ipb_table.notification_table th {
    color: #333;
}
.ipsButton_secondary a {
    color: #ccc;
}
.ipsButton_secondary:hover a {
    color: #eee;
}
#profile_background .bar, #profile_background  .ipsSideBlock h3, #profile_background  .general_box h3 {
    color: #777;
}
#user_latest_status .lighter a {
    color: #333;
}
#profile_background .ipsSideBlock, #profile_background .general_box {
	background-color: #555;
}
.cke_editor textarea:focus {
    color: #444;
}
.topic_buttons li.non_button a {
    color: #f0f0f0;
    background: #444 url('http://www.garsoniera.com.pl/forum/public/style_images/elegant/highlight_faint.png') repeat-x 0 1px !important;
    border-radius: 2px !important;
    height: 26px;
    line-height: 26px;
    padding: 0 10px;
    text-decoration: none;
    margin-left: 4px;
    display: block;
}
.topic_buttons li.non_button {
    padding: 7px 7px 0 0;
}
.breadcrumb span[itemprop="title"] {
    font-weight: bold;
}

}
