css:
@-moz-document domain("wolley.io") {
body {
    background-color: #f6f6ef;
}
.news-list,
.news-item,
.item-view,
.view,
.item-view-header,
.item-view-comments {
    background-color: #f6f6ef;
}
.header {
    background-color: #ff6600;
}
.header .inner a {
    color: #000000;
}
a {
    color: #ff6600;
}
.news-item .item-text .title a {
    color: #000000;
}
.footer {
    border-top: 1px solid #ff6600;
}
.vote-box .margin-right img {
    content: url("data:image/png;base64,")
}
.vote-box img {
    content: url("data:image/png;base64,")
}
}
