css:
@-moz-document domain("help.salesforce.com") {
.ht-slds #content{
    background-color: #333;
    color: whitesmoke;
}

.slds-text-title_caps {
    color: #279e09;
}
.ht-slds .slds-text-title--caps, .ht-slds .slds-text-title_caps {    
   color: #279e09;
}

.slds-theme_shade {
    background-color: #3e3e3e;
}

.slds-section__content {
    background-color: #3e3e3e;
}

.slds-cell-wrap{
    background-color: #3e3e3e;
    color: #f3e3e3;
}

.slds-scrollable_x{
	border: 1px solid #dddbda;
    border-radius: 6px;
}

.ht-slds .slds-media {
    background-color: #3e3e3e;
    color: #f3e3e3;
}

.ht-slds a, .ht-slds button {
    cursor: pointer;
    color: #96d292;
}

.article-viewer .article-data .article-content knowledge-article a{
     color: #96d292!important;
}

.CoveoResult .coveo-title a.CoveoResultLink:focus {
    color: #158c1d;
}

.CoveoResult .coveo-title a.CoveoResultLink:hover {
    color: #158c1d;
}

.article-viewer .article-data .article-content knowledge-article a:hover{
     color: #158c1d!important;
}

.ht-slds .slds-breadcrumb .slds-list__item:before, .ht-slds .slds-breadcrumb__item:before {
    color: #96d292;
}

.ht-slds .slds-box {    
    border-radius: .25rem;
    border: none;
}

.ht-slds kbd {    
    color: #8bb4f1;   
}

.ht-slds .slds-box {    
    background-color: #585858;
}

.ht-slds #div2, .ht-slds #div3 {    
    overflow: visible;    
}

.ht-slds strong {   
    color: #d65e5e;
}

.tdx-card>p {
    color: inherit;     
}

.article-viewer .article-bottom, .article-viewer .article-data, .article-viewer .back-to-search {
    background-color: #3e3e3e;
    color: #f3e3e3;
}

.article-viewer .article-data .article-content knowledge-article h1 span{
    color: #36a739!important;
}

.article-viewer .article-data .article-content knowledge-article p{
     color: #f7f7f7!important;
}
.article-viewer .article-data .article-content knowledge-article div ol li{
    color: #f7f7f7!important;
}

.article-viewer .article-data .article-content knowledge-article h3 b{
    color: #63c766!important;
}

.article-viewer .article-data .article-content knowledge-article ul{
    color: #f7f7f7!important;
}
.article-viewer .article-data .article-content knowledge-article ol b{
    color: #da7575!important;
}
.article-viewer .article-data .article-content knowledge-article p b{
    color: #f5bc55!important;
}
.article-viewer .article-data .article-content knowledge-article h2 b{
    color: #63c766!important;
}
.article-viewer .article-data .article-content knowledge-article ul b{
    color: #f5bc55!important;
}

/*code*/
.ht-slds #content code {
    color: #dcdcdc;    
}
}

@-moz-document domain("help.salesforce.com") {
.topo-background {
    background-color: #1d1c1ce8;
    background-image: none;
}

.ht-slds #div1, .ht-slds #switch {
    box-shadow: 5px 0 4px rgba(0,0,0,.07);
    background-color: #273521;
}

toc-sidebar {   
    background-color: inherit;    
}

.footer {    
    background-color: #3e3e3e;
}

.tdx-scope .tdx-global-header_container {   
    background-color: #3e3e3e;
}

/* -- Side panels e.g. help etc -- */
.ht-slds #div3 .tdx-card.slds-m-bottom_medium {    
    background-color: #504f4f;
}

.ht-slds .cards-height {
   	background-color: #504f4f;
    color: #f3e3e3;
}

.ht-slds .tdx-card h3.card-title {
    color: #f3e3e3;
}

/* -- Side panels e.g. help etc -- */

.coveo-recommendation-body .CoveoResultList .CoveoResult {
    background-color: #504f4f;
}

.ht-slds .tdx-scope.comty-home .card-title {
    color: #f3e3e3;
}

.CoveoResult .coveo-title a.CoveoResultLink {
    color: #96d292;
}
}

@-moz-document domain("help.salesforce.com") {
.ht-slds .slds-table:not(.slds-no-row-hover) tbody tr:focus>td, .ht-slds .slds-table:not(.slds-no-row-hover) tbody tr:focus>th, .ht-slds .slds-table:not(.slds-no-row-hover) tbody tr:hover>td, .ht-slds .slds-table:not(.slds-no-row-hover) tbody tr:hover>th {
    background-color: #505050;
}

.background-position .background-tile{
    background-color: #505050;
}

.footer .footer-header {
    color: #a8c3ea; 
    
}
.ht-slds img {    
    background-color: #8c8c8c;
    border-radius: 10px;
}
}
