css:
@-moz-document domain("play.typeracer.com"), domain("data.typeracer.com"), domain("preview.typeracer.com"), domain(staging.typeracer.com) {
html {
    min-height: 100%;
}
body {
    height: 100%;
    background: 0;
}

.statsView {
    border: 2px solid #f00 !important;
    box-shadow: 0px 0px 50px #000000d6;
    overflow: visible !important;
    z-index: 50;
}

/* Navigation */
.themeHeader,
.mainUserHeader {
    background: 0;
    background-color: #f00;
}
.themeHeader a {
    color: #fff;
}
.themeHeader a:hover,
.themeHeader a:focus {
    color: #c1c1c1;
}
.themeHeader .title {
    padding: 25px;
}
.themeHeader .title h1 a {
    text-transform: uppercase;
}
.themeHeader .navigation {
    padding: 22px 20px;
    height: 0;
}
.themeHeader #tstats {
    padding-top: 10px;
}
.themeHeader #tstats .mainUserInfoBox tbody tr td {
    color: #fff;
}
.subTitle {
    display: none;
}
/* Player Info */
.playerInfoPopup {
    background: #000;
    color: #f00;
    border-color: #000;
}
.playerInfoPopup a {
    color: #f00;
}
.playerInfoPopup a:hover,
.playerInfoPopup a:focus {
    color: #f00;
}
/* Content */
.main,
.themeContent {
    background: 0;
    background-color: #fff;
    margin: 0 auto;
}
.nonHideableWords,
.inputPanel span {
    letter-spacing: 0.25px;
    word-spacing: 2px;
    font-size: 20px !important;
    text-decoration: none;
}

.friendsTable .headerRow {
    font-size: 14px;
    text-transform: capitalize;
    font-style: normal !important;
}
.friendsTable tr:hover,
.friendsTable tr:focus {
    background-color: #f00;
}
.friendsTable .headerRow:hover,
.friendsTable .headerRow:focus {
    background-color: #f00;
}
.gwt-TabBar .gwt-TabBarItem {
    color: #f00;
}

/* Ted Race Animations */
input:focus {
    outline: none;
}
.gameStatusLabel,
.arrowPopupText {
    color: #f00 !important;
    opacity: 1;
    transition: opacity .4s ease-in-out;
    -moz-transition: opacity .4s ease-in-out;
    -webkit-transition: opacity .4s ease-in-out;
}
.gameStatusLabel-flashOn,
.arrowPopupText-flashOn {
    opacity: 0.66;
    transition: opacity .4s ease-in-out;
    -moz-transition: opacity .4s ease-in-out;
    -webkit-transition: opacity .4s ease-in-out;
}
.txtInput {
    background: none !important;
    border: none !important;
    border-bottom: 2px solid #f00 !important;
    transition: all 0.2s ease;
    font-size: 22px !important;
    color: #999999 !important;
}
.txtInput-error {
    background: none !important;
    border-bottom: 2px solid #f00 !important;
    transition: all 0.2s ease;
}
[style="text-decoration: underline; color: rgb(153, 204, 0);"],
[style="color: rgb(153, 204, 0);"] {
    color: #f00 !important;
    text-decoration: none !important;
}

/* Hide IDM Popup */
.InstaGibPopup {
    display: none;
}

/* Footer */
.footer {
    background: 0;
    background-color: #f00;
    color: #f00;
}
.chrome_l,
.chrome_m,
.chrome_r,
.chrome_t,
.chrome_tr,
.chrome_tl,
.chrome_b,
.chrome_bl,
.chrome_br {
    background: 0;
}
.popupContent {
    background: #fff;
}
/* DARK THEME */
body {
    background: #000 !important;
    color: #f00;
}
a {
    color: #f00;
}
a:hover,
a:focus {
    color: #f00;
}

.mainViewport,
.main,
.themeContent,
.statsView table {
    background-color: #000;
    color: #888;
}
.friendsTable tr:hover,
.friendsTable tr:focus {
    background-color: #000;
}
.friendsTable .headerRow:hover,
d .friendsTable .headerRow:focus {
    background-color: #111;
}
.popupContent {
    background: #000;
}
.gwt-TabBar .gwt-TabBarItem {
    color: #f00;
}
.gwt-TabBar .gwt-TabBarItem:hover,
.gwt-TabBar .gwt-TabBarItem:focus {
    color: #f00;
}

/* Smooth Cars & Gray Progress Bar*/
.scoreboard .row .progressBar {
    border-color: #292723;
    border-bottom-style: solid;
    border-width: 4px;
    transition: linear 2s;
}
}
