/* ==UserStyle==
@name           Mi Community Dark Theme By EgoistDeveloper
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Mi Community Dark Theme
@author         https://github.com/EgoistDeveloper
==/UserStyle== */

@-moz-document url-prefix("https://c.mi.com/"), url-prefix("https://in.c.mi.com/"), url-prefix("https://ru.c.mi.com/"), url-prefix("https://c.mi.com/it/"), url-prefix("https://c.mi.com/mx/"), url-prefix("https://c.mi.com/tr/"), url-prefix("https://c.mi.com/global/") {

    ::root {
        --themecolor: #ff6700;
        --background: #1E1E1E;
        --barpopup: #2d2d2d;
        --navpopup: #292323;
        --thread: #292828;
    }

    /* Scrollbar */
    ::-webkit-scrollbar {
        background: #2d3033;
        height: 10px;
        width: 10px;
    }

    ::-webkit-scrollbar-track {
        border-radius: 0px;
    }

    ::-webkit-scrollbar-thumb {
        background: #ff670077;
        border-radius: 0px;
    }

    ::-webkit-scrollbar-thumb:hover {
        background: #666;
    }

    ::selection {
        background: #ff670077;
    }

    /* General */
    * {
        color: #bdbdbd;
    }

    svg {
        fill: #bdbdbd;
    }

    a {
        color: #8b8b8b !important;
        transition: color 0.8s;
    }

    a:hover {
        color: #ff6700 !important;
    }

    input,
    textarea,
    .form-control {
        color: #eeeeee;
        background-color: #373232 !important;
        border: 1px dashed #939090;
        border-radius: 0px !important;
    }

    body {
        background-color: #1E1E1E;
    }

    blockquote {
        margin-bottom: 10px;
        background-color: #373232 !important;
    }

    *:focus {
        outline: 0 !important;
    }

    /* Header */

    .mi-community-text {
        color: #ff6700 !important;
    }

    .interbar .country-select #targetCountry {
        background: #ff6700;
        color: #fff !important;
    }

    .interbar .country-select .hd-menu-list-wrapper,
    .account-info .hd-menu-list-ul {
        background: #2d2d2d;
    }

    .interbar .country-select .hd-menu-list-wrapper dt {
        color: #ff6700 !important;
    }

    #hd {
        position: relative;
        background-color: #2d2d2d;
        height: 56px;
        z-index: 200;
        color: #919191 !important;
    }

    #hd .main-nav-ul>li .nav-item .hd-menu {
        background: #292323;
    }

    div.hd-menu.sub-navs::before {
        border: none !important;
    }

    /* Main Page */

    .xm-box,
    .interbar {
        background-color: #1E1E1E;
    }

    .top-seperator {
        background-color: #3e3e3e;
        margin: 50px 0px 30px 0px;
    }

    .seperator {
        background-color: #3e3e3e !important;
    }

    .thread-list {
        padding: 15px 0px;
    }

    .thread-list .thread-list-ul>li {
        background-color: #292828;
    }

    .thread-list .thread-list-ul .thread-plain {
        height: 200px;
    }

    .thread-list .thread-list-ul .thread-plain .thumb {
        width: 230px;
        height: 200px;
        overflow: hidden;
    }

    .thread-list .thread-list-ul .thread-plain .thumb .thread-feature-img {
        width: 230px;
        height: 200px;
        background-size: cover;
        background-position: center center;
    }

    .thread-list .thread-list-ul .thread-plain .content {
        margin-right: 20px;
        position: relative;
        width: 590px;
        height: 90%;
        box-sizing: border-box;
        padding-top: 32px;
        border-top-color: #f77e7e;
    }

    .thread-list .thread-list-ul .thread-plain .category {
        background: #ff6700 !important;
    }

    .thread-list .thread-list-ul .thread-plain .category a {
        color: #cacaca !important;
    }

    .thread-list .thread-list-ul .thread-plain .thread-info .z {
        background: #535353 !important;
        border-radius: 20px;
        padding: 5px 10px;
    }

    .thread-list .thread-list-ul .thread-plain .thread-info .z a,
    .thread-list .thread-list-ul .thread-plain .thread-info .z .auth-name {
        color: #cacaca !important;
    }

    /* Sidebar */

    .xm-side-box .xm-box {
        background: #292828;
    }

    /* Calendar */

    .xm-side-box .checkin-box .calendar-topbanner {
        height: 5px;
        background-color: #ff6700;
    }

    .xm-side-box .checkin-box .dayMisstionBox .todayMissionBox {
        color: #cacaca !important;
        background-color: #ff6700;
        margin: 0px 10px;
        width: 280px;
    }

    /* MIUI Log */

    .xm-side-box .rom-box .rom-box-cover a {
        background: #cacaca77;
        color: #fff !important;
    }

    /* Featured Box */

    .xm-side-box .featured-box h4 {
        color: #cacaca;
        padding: 15px 0px 0px 20px;
    }

    .xm-side-box .featured-box .seperator {
        border: none;
        border-bottom: 1px solid #3e3e3e;
    }

    .xm-side-box .featured-box ul {
        list-style: none !important;
    }

    /* Ranks */

    .xm-side-box .users-box h3 {
        color: #cacaca;
        padding: 15px 0px 0px 20px;
    }

    .xm-side-box .users-box h4.tab-active {
        color: #ff6700;
    }

    .xm-side-box .users-box .tab-content {
        margin: 10px 0 !important;
        padding: 10px 10px;
    }

    .xm-side-box .users-box ul li {
        padding: 10px 10px;
        border-bottom: 1px solid #535353;
    }

    .xm-side-box .users-box .user-avatar img {
        width: 100%;
        height: 100%;
    }

    .xm-side-box .users-box .content .user-name {
        color: #cacaca;
    }

    .xm-side-box .users-box ul .user-rank {
        color: #ff6700;
    }

    /* QR */

    .xm-side-box .qr-box .qrc {
        background: #292828;
        border: none;
    }

    .xm-side-box .qr-box .qrc .content h3 {
        color: #cacaca !important;
    }

    .xm-side-box .qr-box .download a {
        color: #c0c0c0 !important;
    }

    .xm-side-box .qr-box .download a:hover {
        color: #d9d9d9 !important;
    }

    /* Forum */

    .xm-tabs>li {
        border-top: 4px solid #2d2d2d;
    }

    .xm-threads-list>li {
        background-color: #292828;
        border: none;
        margin: 20px 0px;
        min-height: 115px;
    }

    .pgbtn a {
        background-color: #ff6700 !important;
        color: #e6e6e6 !important;
    }

    .xm-side-box .checkin-box .uncheck .checkin-btn {
        width: 280px;
        margin: 5px 10px;
        color: #e2e2e2 !important;
    }

    /* Filter */

    .sectionFilter {
        border: 1px solid #4e4e4e;
    }

    .filter-time {
        border-bottom: 1px solid #474747;
    }


    /* Thread */

    .xm-side-box .thread-author-box .author-medal {
        border: none;
    }

    .xm-box.xm-subject {
        background-color: #292828;
    }

    .np-title {
        color: #c4c4c4 !important;
    }

    .subject-content+.subject-content {
        border-top: 1px solid #474747;
    }

    .subject-content .sign {
        border: none;
    }

    .subject-content.other-floor .node-author .auth-title {
        background: #535353 !important;
        border-radius: 20px;
        padding: 3px 3px
    }

    .subject-content.other-floor .node-author .auth-title a,
    .subject-content.other-floor .node-author .xi2,
    .tabContent li .num,
    .xm-side-box .thread-author-box .name {
        color: #c6c6c6 !important;
    }

    .xm-pagenav .pg a {
        background-color: #292828;
    }

    .tedt,
    .tedt .fpd a {
        border: none;
    }

    .tedt .bar {
        background-color: #292828;
        border-bottom: 1px solid #535353;
    }

    .tedt .area {
        background-color: #292828;
    }

    /* Footer */

    #ft {
        border-top: 1px solid #535353;
    }

    /* User Profile */

    .profile-section .profile-node-list-ul>li {
        background-color: #292828;
    }

    .pl .quote {
        background: #373232;
        border: 1px dashed #535353;
        padding: 20px 30px
    }

    /* Rom Downloads */

    .rom-list-container {
        background-color: #292828;
    }

    .rom-list-item a {
        border: 1px dashed #535353;
    }

    .rom-list-item a .rom-phone-name {
        border: 1px solid #535353;
    }
}
