css:
/* PJe Gray Style
 * By: Geyslan G. Bem
 */

@-moz-document regexp("https?://pje\\..*\\.jus\\.br.*") {

/* LOGIN */
.body {
	background-color: #bbb;
}
.pageBody {
	background-color: #bbb;
}
div.identificacao, .conteudos-links {
	background-color: #ddd;
}
.rich-mpnl-panel .rich-mpnl-content, .modal-popup {
	background-color: #ddd;	
}

/* LOGIN - Opções Laterais */
div > .container-fluid > div > ul {
	background-color: #ddd;
}

/* QUADRO AVISOS */
.pje-content.container {
	background-color: #ddd;
}

/* PJe ROOT */
:root, .pje-root, .app, .html, .body {
	background-color: #aaa !important;
}

/* PESQUISA RÁPIDA */
.mat-card#menu-item-1 {
	background-color: #ddd !important;
}
/* PESQUISA RÁPIDA - Seta */
.icone-seta-menu {
	color: #ddd !important;
}

/* PERFIL USUÁRIO, RESUMO PROCESSO */
.mat-dialog-container {
	background-color: #ddd;
}
#partes-processo-autuacao .partes-representante {
	color: black !important;
	font-weight: bold;
}
#partes-processo-autuacao .span-informacao {
	color: #777 !important;
	font-weight: normal;
}

/* MEU PAINEL, PAINEL GLOBAL, ESCANINHO, PERÍCIAS, RELATÓRIOS GIGS, IMPRESSÃO EXP */
/* Filtros */
.card-filtros {
	background-color: #666 !important;
	color: #eee !important;	
}
.card-filtros label {
	color: #ddd !important;	
}
.ng-trigger-transformPanel {
	background-color: #ddd;
}
.mat-calendar-table-header-divider {
	background-color: #ddd !important;
}

/* Header, Pagination (Lista Processos) */
thead, .mat-table-pagination {
	background-color: #555 !important;
	color: #ddd !important;	
}
.mat-select-value {
	color: #ddd !important;	
}

/* Table  */
.t-class {
	background-color: #888 !important;	
}

/* Table Rows */
.tr-class:hover, .tr-class:focus {
	background-color: #ccc !important;
}


/* PAUTA AUDIÊNCIA */
/* Calendário */
.card-pauta {
	background-color: #ddd !important;
}
.cal-month-view {
	background-color: #fff !important;
}
.cal-day-cell .background-feriado-recesso {
	background-color: #c7c7c7 !important;
}
.cal-day-cell .cor-label-feriado-recesso {
	color: #000 !important;
}
mat-card.card-pauta.mat-card pje-calendario-pauta-audiencia.ng-untouched.ng-pristine.ng-valid mat-card.mat-card.ng-star-inserted {
	background-color: #eee !important;
}

/* RELATÓRIOS GERENCIAIS */

.execucao,
.execucao .mat-card.mat-card-tabela-crud.mat-card {
	background-color: #ddd !important;
}
.execucao .icone-arquivo {
	background-color: transparent !important;
}

/* PROCESSO */

/* PROCESSO - Chips Incluir */
/* PROCESSO - Chips Filtro */
mat-card-content.mat-card-content:nth-child(2) > div:nth-child(1) > mat-card:nth-child(1) {
	background-color: #999 !important;	
}
mat-card-content.mat-card-content:nth-child(2) > div:nth-child(1) > mat-card:nth-child(1) label {	
	color: #000 !important;
}
/* PROCESSO - Chips Table */
pje-inclui-etiquetas-dialogo.ng-star-inserted mat-card-content.mat-card-content pje-data-table.dataTable div.t-container-class table.t-class tbody.ng-star-inserted,
pje-inclui-etiquetas-dialogo.ng-star-inserted mat-card-content.mat-card-content pje-data-table.dataTable div.t-container-class table.t-class tbody.ng-star-inserted tr.tr-class.ng-star-inserted {
	background-color: #bbb !important;
}
pje-inclui-etiquetas-dialogo.ng-star-inserted mat-card-content.mat-card-content pje-data-table.dataTable div.t-container-class table.t-class tbody.ng-star-inserted tr.tr-class.ng-star-inserted:hover {
	background-color: #ccc !important;
}

/* PROCESSO - Post-it */
.mat-expansion-panel-header.post-it-header.mat-expansion-panel-header {
	background-color: orange !important;
}
.post-it-header-comandos {
	background-color: orange !important;
}

/* PROCESSO - MENU */
.menu-processo {
	background-color: #ddd;
}

/* PROCESSO - MENU - GIGS - Inclusão */
.mat-expansion-panel-header {
	background-color: #bbb !important;
}
.mat-expansion-panel-header.mat-expanded {
	background-color: #aaa !important;
}
.mat-expansion-panel {
	background-color: #ddd;
}

/* PROCESSO - MENU - GIGS - Menu de Autocomplete */
.mat-autocomplete-panel {
	background-color: #ddd;
}

/* PROCESSO - MENU - Acesso Terceiros */

/* PROCESSO - Anexar - Editor */
.editor-wrapper {
	background-color: #bbb !important;
}
.editor-wrapper .layout-papel {	
	background-color: #dad2c4 !important;
}


/* PROCESSO - MENU - Audiências e Sessões */
/* PROCESSO - MENU - BNDT */
/* PROCESSO - MENU - Cálculos */

/* PROCESSO - MENU - Comunicações e Expedientes */
#pec-container-cadastro {
	background-color: #ddd !important;
}

/* PROCESSO - MENU - Segredo Justiça */
/* PROCESSO - MENU - Dados Financeiros */
/* PROCESSO - MENU - Expedientes */

/* PROCESSO - MENU - Histórico Sigilo */
/* PROCESSO - MENU - Inserir Lembrete */
/* PROCESSO - MENU - Lançar Movimentos */
/* PROCESSO - MENU - Obrigação Pagar */
/* PROCESSO - MENU - Pagamento */
/* PROCESSO - MENU - Perícias */
/* PROCESSO - MENU - Quadro Recursos */
/* PROCESSO - MENU - Reprocessar Chips */
/* PROCESSO - MENU - Retificar Autuação */
/* PROCESSO - MENU - Selo Histórico */
/* PROCESSO - MENU - Verificar Impedimentos Suspeições */


/* PROCESSO - Timeline - Filtro */
.timeline-busca,
.timeline-busca label,
.timeline-busca .icone-sozinho,
.timeline-busca .icone-principal,
.mat-menu-panel,
.mat-menu-panel .mat-menu-item,
.mat-menu-panel .tl-cinza.icone-sobrescrito {
	background-color: #666 !important;
	color: #eee !important;
}
/* PROCESSO - Timeline - Botão */
button.botao-menu.mat-mini-fab.mat-accent {
	background-color: #53a0e4 !important;
}
/* PROCESSO - Timeline - Balão */
#divTimeLine .tl-item-desc {
	background-color: #ccc !important;	 
}
/* PROCESSO - Timeline - Seta Balão */
#divTimeLine .tl-icon::before {
	border-right: 19px solid #ccc !important;
}
#divTimeLine .tl-icon::after {
	content: none !important;	
}
/* PROCESSO - Timeline - Fonte */
#divTimeLine .tl-documento {
	color: #0000cd !important;
}

/* PROCESSO - Expedientes - Header */
.mat-card-header,
.mat-card-header .link,
.mat-card-header .lupa-doc-apreciado,
.mat-card-subtitle,
.ficha-processo-card {
	background-color: #666 !important;
	color: #eee !important;
}

/* PROCESSO - Expedientes - HTML */
.container-html .conteudo-html {
	background-color: #dad2c4 !important;
}
/* PROCESSO - Expedientes - PDF */
.container-pdf .textLayer {
	opacity: .25;
	background-color: #704214;
}

}
