css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("www.cs-manager.com") {



/* center */

#container, #footer_container,.post,#content, #header { margin: 0 auto !important; }



/* icons */

ul#friend a[title^="Idle:"] span, ul#friend a[title="Active"] span{

  background-color: transparent !important;

  background-repeat: no-repeat !important;

  background-position: 50% 50% !important;

  color: white !important;

  padding: 0px !important;

  padding-left: 10px !important;

  margin: 0px !important;

  float: left !important;

  width: 3px !important;

}

ul#friend a[title^="Idle:"] span{background-image:url("http://i296.photobucket.com/albums/mm187/csmpresentations/sub/id.png") !important;}

ul#friend a[title="Active"] span{background-image:url("http://i296.photobucket.com/albums/mm187/csmpresentations/sub/on.png") !important;}

ul#friend a{padding-left: 14px !important;}

ul#friend a[title^="Idle:"], ul#friend a[title="Active"]{ margin: 0px !important; padding: 0px !important; text-align: left !important; float: left !important;}







body, a{ font-family: arial, sans-serif !important;}

a{color: #3e74c0 !important;}

div#container div#menu, div#container div#info, div#container{background-color: #ffffff !important;}

div#container, div#footer_container{ border: 0 !important;}

body{ background-color: white !important; }

div#header{ background-image: url('http://i296.photobucket.com/albums/mm187/csmpresentations/sub/blue.jpg') !important; background-color: white !important; border: 0 !important; background-repeat: no-repeat !important; }

div#header a img{display: none !important;}



/* login */

div#content > div table.rank tbody tr td.head,

div#content > div form table.login tbody tr td.head,

div#content > div table.login tbody tr td.head

{background: none !important; border: 0 !important; background-color: #333333 !important; color: #cccccc !important;font-weight: normal !important; font-size: 12px !important;}

div#content > div table.rank tbody tr td,

div#content > div form table.login tbody tr td,

div#content > div table.login tbody tr td

{background-color: #ffffff !important; font-family: arial, sans-serif !important;}

div#content > div form table.login, div#content > div table.rank, div#content > div table.login{border-color: #ccc !important;}

div#content > div form table.login tbody tr td.box input.text{border:1px solid #CCCCCC !important; color:#333333 !important; font-size:12px !important; margin-top:3px !important; padding:2px 3px !important; background-color: #f5f5ef !important; font-family: arial, sans-serif !important;}

div#content > div form table.login tbody tr td.box input.buttonsmall{margin-top: 3px !important;}



input.text{padding: 0px !important; margin: 0px !important; height: 15px !important;}

select[name^="eco_t"]{margin-top: 1px !important;}

img.img_input{margin-top: -1px !important; height: 17px !important;}



ul#tabtop{margin: 50px 0 0 !important;}

div#header ul#tabtop li a{border: 0 !important; background: none !important; font-size: 12px !important;} 

div#header ul#tabtop li a[title="Forum"]{color: #3e74c0 !important;}

div#header ul#tabtop li a[title="CSM"]{color: #3e74c0 !important;}

div#header ul#tabtop li a[title="Log out"]{color: #666666 !important;}



div.space{ background-color: white !important;}

div#container div#menu, div#container div#info{border: 0 !important; color: #333333 !important;}

div#container div#menu h3, div#container div#info h3{ border: 0 !important; background-color: #333333 !important; color: #cccccc !important;font-weight: normal !important; font-size: 12px !important;}



div#container div#menu ul.menu li a, 

div#container div#info ul.gameinfo li a,

div#container div#info ul#friend li a{border: 0 !important; background: white !important; color: black !important; font-size: 10px !important;}

/* hover */

div#container div#menu ul.menu li a:hover, 

div#container div#info ul.gameinfo li a:hover,

div#container div#info ul#friend li a:hover{background: #f2f2f2 !important; color: black !important; font-size: 10px !important;}



div.hr{margin-left: 10px !important; margin-right: 10px !important;}



/* search + buttons */

div#container form#search{background: none !important; border: 0 !important;}

input.button, input.browse, input.buttonsmall{background-color: #3e74c0 !important; border: 0 !important; color: #ffffff !important; font-size:10px !important; height:17px !important; margin-right:1px !important; top:1px !important; font-family: arial, sans-serif !important;}

input.full, div#container form#search select.full{border:1px solid #CCCCCC !important; color:#333333 !important; font-size:12px !important; margin-top:3px !important; padding:2px 3px !important; background-color: #f5f5ef !important; font-family: arial, sans-serif !important;}



/* tabs */

div#tab_container{padding-left: 9px !important; padding-right:8px !important;}

div#tab_container ul#tab {border: 0 !important; background-color: #333333 !important; padding-bottom: 8px !important;}



div#tab_container ul#tab li span, div#tab_container ul#tab li a{border: 0 !important; background: none !important; font-size: 12px !important;}

div#tab_container ul#tab li a{color: #cccccc !important; }

div#tab_container ul#tab li a:hover{color: #ffffff !important; }

div#tab_container ul#tab li span{color: #3e74c0!important; font-weight: normal !important;}



/* center */

div#content_fixed table.dev tbody tr td.head{background: none !important; color: #666666 !important; border: none !important; font-size:20px !important; font-weight:normal !important; letter-spacing:-1px !important;}

div#content_fixed table.dev tbody tr td.head div{color:red !important;}

div#content_fixed table.dev tbody tr td{border: 0 !important; background: none !important; font-family: arial, sans-serif !important; color: #333333 !important; font-size: 10px !important;}

div#content_fixed table.dev tbody tr td.info{text-align: left !important;}

div#content_fixed table.dev tbody tr td.value{text-align: left !important; color: #000000 !important;}

div#content_fixed table.dev tbody tr td.foot{font-size: 12px !important;}



/* presentation */

div#content_fixed div.post{border: 0px !important; background: #ffffff !important;}



/* commnuity_news */

div#content_fixed table.dev tbody tr td.head a{background: none !important; color: #666666 !important; border: none !important; font-size:20px !important; font-weight:normal !important; letter-spacing:-1px !important;}



/* news */

div#content_fixed table#clan_news_table tbody tr td.foot{padding-bottom: 0px !important;}

div#content_fixed table#clan_news_table tbody tr td.foot b{font-family: arial, sans-serif !important; color: #333333 !important; font-style:normal !important; font-weight:normal !important; font-size: 12px !important;}



/* players */

div#content_fixed table.dev tbody tr td.head span[style^="float"]{visibility: hidden !important}
div#content_fixed table.dev tbody tr td.head span[style^="float"] a{font-size: 11px !important; visibility: visible !important;}
div#content_fixed table.dev tbody tr td.head span[onclick^="setShare"]{visibility: visible !important;};


div#content_fixed table.dev tr td.head{background: none !important; color: #666666 !important; border: none !important; font-size:20px !important; font-weight:normal !important; letter-spacing:-1px !important;}

div#content_fixed table.dev tr td#csm_avg_data{border: 0 !important; background: none !important;}



/* mail */

div#content_fixed form#form_mail table.mail tbody tr.head td{font-family: arial, sans-serif !important; border: 0 !important; background: #ffffff !important;}

div#content_fixed form#form_mail table.mail tbody tr td a{color: #333333 !important;}

div#content_fixed form#form_mail table.mail tbody tr td{background: #ffffff !important;font-family: arial, sans-serif !important; border: 0 !important; border-bottom: 1px solid #cccccc !important; margin: 0px !important;}

div#content_fixed form#form_mail table.mail tbody tr td.head{background: #ffffff !important; color: #666666 !important; border: none !important; font-size:20px !important; font-weight:normal !important; letter-spacing:-1px !important;}

div#content_fixed form#form_mail table.mail tbody tr td.head div{font-size: 12px !important; padding-top: 6px !important;}

div#content_fixed form#form_mail table.mail tbody tr td.head div a{color: #3e74c0 !important;}

div#content_fixed form#form_mail table.mail tbody tr[style] td{background-color: #e6e6e6 !important;}



/* mail content + guestbook */

div#content_fixed div#guestbook h2{background: #ffffff !important; color: #666666 !important; border: none !important; font-size:20px !important; font-weight:normal !important; letter-spacing:-1px !important;}

div#content_fixed div#guestbook h3{background: #ffffff !important; border:0 !important; border-bottom: 1px solid #cccccc !important; }

div#content_fixed div#guestbook div.post{padding-bottom: 12px !important;}

div#content_fixed div#guestbook div.post div.modify{background: none !important; border: 0 !important;}

div#content_fixed div#guestbook div.post div.modify a{color: red !important;}

div#content_fixed div#guestbook div.post div.modify a:first-child{color: green !important;}

div#content_fixed div#guestbook div.post p{font-size: 12px !important; color: #333333 !important; font-family: arial, sans-serif !important;}



/* finance */

div#content_fixed table.dev tbody tr td.value[style^="color: green"]{color: green !important;}

div#content_fixed table.dev tbody tr td.value[style^="color: red"]{color: red!important;}



/* tactics */

div#content_fixed table.lineup tbody tr td.head, div#content_fixed form table.data tbody tr td.head{background: #ffffff !important; color: #666666 !important; border: none !important; font-size:20px !important; font-weight:normal !important; letter-spacing:-1px !important;}

div#content_fixed table.lineup tbody tr td{background: #ffffff !important; border:0 !important; border-bottom: 1px solid #cccccc !important; }

div#content_fixed table.lineup tbody tr td.position, div#content_fixed table.lineup tbody tr td.info{font-family: arial, sans-serif !important; font-size: 12px !important;}

div#content_fixed table.lineup tbody tr td.player a{background: #ffffff !important;}

div#content_fixed table.lineup tbody tr td.space{border: 0px !important;}

div#content_fixed div#popup table.popup tbody tr td.head{border-color: #333333 !important; background: #333333 !important;}  

div#content_fixed div#popup table.popup tbody tr td.data table#lineup_players tbody tr td{font-family: arial, sans-serif !important;}

div#content_fixed div#popup table.popup tbody tr td.data{background:url('http://i296.photobucket.com/albums/mm187/csmpresentations/sub/trans.png') !important;}

div#content_fixed table.dev tbody tr td.info[width^="30%"], div#content_fixed table.dev tbody tr td.value[width^="70%"]{font-size: 12px !important;}

div#content_fixed table.dev tbody tr td.info a[href^="javascript: launchWindow('/applet/tactics/"]{color: #666666 !important;}

div#content_fixed table.data tbody tr td{background: #ffffff !important; border:0 !important; font-family: arial, sans-serif !important; font-size: 12px !important;}

div#content_fixed table.data tbody tr td.value input[type='text']{border:1px solid #CCCCCC !important; color:#333333 !important; font-size:12px !important; margin-top:3px !important; padding:2px 3px !important; background-color: #f5f5ef !important; font-family: arial, sans-serif !important;}



/* advanced */

div#content_fixed div h2{background: #ffffff !important; color: #666666 !important; border: none !important; font-size:20px !important; font-weight:normal !important; letter-spacing:-1px !important;}

div#content_fixed div h3{margin-right: 1px !important;}

div#content_fixed div h3, div#content_fixed div div.players ul#players li a{background: #ffffff !important; }

div#content_fixed div#advanced_info table.info tbody{background-color: #ffffff !important;}

div#content_fixed div#advanced_info table.info tbody tr td p{background-color: #ffffff !important; font-family: arial, sans-serif !important;}

div#content_fixed table.dev tbody tr td a[href^='javascript: chooseWeapon'], div#content_fixed table.dev tbody tr td a[href^='javascript: chooseBuyOrder']{color: #333333 !important; background: none !important;}  







/* league */

div#content_fixed table.dev tbody tr.dev td.value a[href^='/csm/other/?p=other_info&s=clan']{color: #666666 !important;}

div#content_fixed table.table tr td{background-color: #ffffff !important; font-family: arial, sans-serif !important;}

div#content_fixed table.table tr.promotion td, div#content_fixed table.table tr.promotion_line td{color: green !important;}

div#content_fixed h3{background-color: white !important;}



/* games + ladder + cup */

div#content_fixed table.dev tbody tr td.value b a[href^='/csm/other/?p=other_info&'], div#content_fixed table.dev tbody tr td.value a[href^='?p=game_ladder&'] b, div#content_fixed table.dev tbody tr td.value[align^='left'] a[href^='/csm/other/?p=other_info&'], div#content_fixed table.dev tbody tr td.value a[href^='/csm/other/?p=other_info&']{color: #666666 !important; font-size: 12px !important; font-weight: normal !important;}



/* tourns */

div#content_fixed table.dev tbody tr td.info b a[href^='?p=game_tournament']{color: #666666 !important;}

div#content_fixed div#world_join_popup table.popup tr td.head{border-color: #333333 !important; background: #333333 !important;}  

div#content_fixed div#world_join_popup table.popup tr td.data div.service div{font-family: arial, sans-serif !important;}

div#content_fixed div#world_join_popup table.popup tr td.data{background:url('http://i296.photobucket.com/albums/mm187/csmpresentations/sub/trans.png') !important;}

div#content_fixed div#world_join_popup table.popup tr td.data div.service{border: 0 !important; background: none !important;}

div#content_fixed div#world_join_popup table.popup tr td.data div.service div.link a{background-color: #3e74c0 !important; border: 0 !important; color: #ffffff !important; font-size:10px !important; height:17px !important; margin-right:1px !important; top:0px !important; font-family: arial, sans-serif !important; padding-bottom: 3px !important;}

/* div#content_fixed table.dev tbody tr td.head div{visibility: hidden !important} */

div#content_fixed table.dev tbody tr td.head div a{font-size: 11px !important; visibility: visible !important; padding-right: 2px !important;}



/* server */

div#content_fixed div#popup3 table.popup tbody tr td.head, div#content_fixed div#popup2 table.popup tbody tr td.head{border-color: #333333 !important; background: #333333 !important;}  

div#content_fixed div#popup3 table.popup tbody tr td.data table.inline tbody tr td, div#content_fixed div#popup2 table.popup tbody tr td.data table.inline tbody tr td{font-family: arial, sans-serif !important;}

div#content_fixed div#popup3 table.popup tbody tr td.data, div#content_fixed div#popup2 table.popup tbody tr td.data{background:url('http://i296.photobucket.com/albums/mm187/csmpresentations/sub/trans.png') !important;}



/* staff */

div#content_fixed div div div.staff_disabled{background-image: url('http://i296.photobucket.com/albums/mm187/csmpresentations/sub/bg_trans.png') !important;}

div#content_fixed div div div.staff_disabled a{background-color: transparent !important; height: 250px !important;}



/* forum*/

div#container div#content > table.dev tbody tr td a{float: none !important;}

div#container div#content table.info{background-color: #ffffff !important; }

div#container div#content table.info tbody tr td p{font-family: arial, sans-serif !important;}

div#container div#content > table.dev tbody tr td{background-color: #fff !important;  border: 0px !important; border-bottom: 1px solid #ccc !important }

div#container div#content > table.dev tbody tr td.head{background-color: #333333 !important; border: 0 !important; color: #cccccc !important; font-weight: normal !important;}

div#container div#content > table.dev tbody tr td.foot{background-color: #ffffff !important; border: 0 !important;}

div#container div#content > table.dev tbody tr td.foot span b{font-size: 11px !important; }

div#container div#content > table.dev tbody tr td.foot b{background-color: #ffffff !important; color: #666666 !important; border: none !important; font-size:20px !important; font-weight:normal !important; letter-spacing:-1px !important;}

div#container div#content > table.dev tbody tr td{font-family: arial, sans-serif !important;}

div#container div#content > table.dev tbody tr td a[href^='/forum/?p=topic&']{font-size: 12px !important;}

div#container div#content > table.dev tbody tr.even td a[href^='/forum/?p=topic&']{color: #000 !important;}

div#container div#content > table.dev tbody tr.odd td a[href^='/forum/?p=topic&']{color: #666 !important;}





/* trainig */

div#content_fixed > table.dev  tbody  tr.lan{background-color: #e6e6e6 !important;}

div#content_fixed > table.dev tbody tr.lan td.info{background-image:url('http://i296.photobucket.com/albums/mm187/csmpresentations/sub/lc.png') !important; background-repeat: no-repeat !important; background-position: 185px 0px !important;}





div#footer_container div#footer_left, div#footer_container div#footer_right, div#footer_container div#footer{border: 0px !important; background-color: white !important; }

}
