css:
@-moz-document url-prefix("https://mewe.com/myworld"), url-prefix("https://mewe.com/groups") {
.c-app-route #content-wrapper {
    display: unset;
}

.content-all-wrapper.ember-view {
    max-width: none;
}

.c-feed-simple {
    display: grid !important;
    grid-template-columns: repeat(auto-fill, minmax(450px, 1fr));
    grid-gap: 1rem;
    max-width: none;
    justify-items: stretch;
    align-items: stretch;
}

.h-width_full {
    display: flex;
    justify-content: center;
    align-content: center;
}

.c-app-route {
    min-width: 450px;
}

.h-sidebar-right {
    display: none;
    visibility: hidden;
}

.h-sidebar-left {
    display: none;
    visibility: hidden;
}

.c-mw-postbox-placeholder {
    width: 40%;
    min-width: 550px;
    max-width: none;
}

.c-mw-postbox {
    max-width: none;
}

.feed-filter-dropdown {
    padding: 0 1em 0 0;
    justify-content: flex-end !important;
    max-width: none !important;
    margin-left: -2.5rem;
    font-size: 1em !important;
}

.mw-box .mw-box_header {
    flex: 0 0 38px;
    height: 38px;
    padding: 0 16px
}

.c-mw-post {
    align-self: start
}
}
