css:
@-moz-document domain("skyscrapercity.com") {
html:not(.template-california_index_view) div.sidebar {
  display: none !important;
}

section#header-banner div.p-header-banner.p-header-banner-placeholder {
  display: none !important;
}

header.p-header {
  margin-top: initial !important;
  background-color: #232324 !important;
}
header.is-sticky {
  position: static !important;
  display: block !important;
}

.pageNav-jump,
.pageNav-main {
  border-width: 1px !important;
  top: 0px !important;
}

.pageNav-jump {
  padding: 8px 10px !important;
  height: 34px !important;
}

html.template-thread_view div.p-pageWrapper,
.p-sectionLinks,
hr.sticky-divider {
  background: #ececec !important;
}
hr.sticky-divider + div.california-thread-item {
  border-top: none !important;
}
hr.sticky-divider {
  height: 3px !important;
  margin: 1em 0em !important;
}
svg.sticky-pin path {
  color: #b00020 !important;
  fill: #b00020 !important;
}

.message--post {
  background: #fefefe !important;
  margin: 1em 0em !important;
  border: 1px solid #cbcbcb !important;
}

.message--post.is-mod-selected {
  background: #fffcd7 !important;
}

.message--post > div.message-inner > div {
  padding: 1.2em 1.2em 0.2em 1.2em ! important;
}

.message--post > div.message-inner > div.message-cell.message-cell--user {
  background: #f5f5f5 !important;
}

.bbCodeBlock,
div.bbCodeBlock--unfurl,
div.block-outer .button,
div.block-outer a.button,
.fr-toolbar {
  background: #f5f5f5 !important;
}

div.bbCodeBlock-expandLink {
  background: linear-gradient(to bottom, #f5f5f5ff, #f5f5f500 50%) !important;
}

.message--post > div.message-inner > div.message-cell.message-cell--main header.message-attribution {
  font-family: "Segoe UI", "Helvetica Neue", Helvetica, Roboto, Oxygen, Ubuntu, Cantarell, "Fira Sans", "Droid Sans", sans-serif !important;
  margin: -1.2em auto 1.3em -0.8em !important;
}

.message--post > div.comment-options > span.actionBar-action {
  padding: 13px 3px 3px 3px !important;
}

.message--post > div.comment-options > span.actionBar-action,
.message--post > div.comment-options > div.actionBar-set {
  margin-top: -0.5em !important;
}
.message--post > div.comment-options .message-newIndicator {
  background: #b00020 !important;
}

.message--post > div.comment-options > div.actionBar-set button.menuTrigger {
  margin-top: -1px !important;
}

.message--post .message-signature.signature-collapsed .signature-overlay {
  background: linear-gradient(#fefefe00, #fefefeff) !important;
}
.message--post.is-mod-selected .message-signature.signature-collapsed .signature-overlay {
  background: linear-gradient(#fffcd700, #fffcd7ff) !important;
}
.message--post > div.message-inner > div.message-cell footer {
  display: inline-block !important;
  align-self: flex-end !important;
  width: 100% !important;
  margin-top: 2em !important;
}

.menu.menu--left {
  min-width: 30% !important;
  max-width: 66% !important;
}
}
