css:
@-moz-document domain("stackoverflow.com") {
  body {
	background-color: #0b3800;
}

.s-notice__info {
	background-color: #c2e3cd;
	font-style: italic;
	color: #404040;
	/* 	color:white; */
}
.svg-icon {
	color: #9d9d9d !important;
}
.top-bar {
	background-color: #0a480a;
}

.js-header-rep,
.top-bar .my-profile .-rep {
	color: #dcdcdc;
}
.top-bar .-marketing-link {
	color: #dcdcdc;
}
.s-sidebarwidget__yellow {
	background-color: #8c8622;
	font-weight: bold;
}
.show-votes .sidebar-linked .spacer > a:first-child .answer-votes.answered-accepted,
.show-votes .sidebar-related .spacer > a:first-child .answer-votes.answered-accepted {
	color: #fff;
	background-color: #167034;
}
.s-sidebarwidget__yellow .s-sidebarwidget--header {
	background-color: #2e794b;
}
.top-bar .my-profile .-badges .badge3 + .badgecount {
	color: #dcdcdc;
	font-weight: bold;
}
.post-tag,
.geo-tag,
.container .chosen-choices .search-choice,
.container .chosen-container-multi .chosen-choices li.search-choice {
	color: var(--powder-700);
	background-color: #024813;
	border-color: transparent;
	color: #1cb27e;
}

.top-bar .-logo {
	background-color: #639a69;
}

#left-sidebar,
#nav-questions {

	background-color: #097109;
}


.left-sidebar a,
.nav-links li {

	color: white !important
}

.post-tag .js-gps-track {

	color: #bcbcbc !important;
}


#content,
#question-header .question-hyperlink {

	background-color: #015900;
	color: #ddd;
}

li .youarehere {
	background-color: #005400 !important;
	color: black !important
}

#content p {
	/* 	font-size:1.25rem; */
	font-weight: bold;
	color: #d2d2d2;
	/* 	font-style:italic; */
	font-family: courier;
	/* 	text-shadow: 1px 1px #757575; */
}

#content p code,
#content code {
	/* 	color:initial; */
	color: #a01111;
	font-family: century;
}

svg,
.js-vote-count,
.js-favorite-count {
	color: #e9e9e9 !important;
}

#content p a {
	color: inherit;
}
.fc-light {
	color: white !important;
}

#hot-network-questions a {
	font-weight: bold;
	color: #00a458
}

.question-hyperlink,
.answer-hyperlink {
	color: #dbdbdb;
}
}
