css:
/* i really want this to be global */

#action-buttoncontrol {
    background-color: #61f75b;
    color: #333333;
    border: solid 1px #61f75b;
  	text-align: center;
  	transition-duration: 0.2s;
}

#action-buttoncontrol:hover {
    background-color: #fff;
    color: #333333;
    border: solid 1px #61f75b;
}

#action-buttontip {
    border: 1px solid #61f75b;
    color: #333333;
  	transition-duration: 0.2s;
}

#action-buttontip:hover {
    background-color: #61f75b;
    color: #333333;
    border: solid 1px #61f75b;
}

#action-buttontipl:hover:hover {
    background-color: #61f75b;
    color: #333333;
    border: solid 1px #61f75b;
}

.btn-yellow {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    cursor: pointer;
    display: inline-block;
    font-size: 14px;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    background-color: #29bb00;
    text-decoration: none;
    vertical-align: middle;
    border-radius: 5px;
    border: 1px solid #29bb00;
    overflow: hidden;
    padding: .75rem 2rem;
    text-align: center;
    color: #333;
  	transition-duration: 0.2s;
}

.btn-yellow:hover {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    cursor: pointer;
    display: inline-block;
    font-size: 14px;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    background-color: #31de00;
    text-decoration: none;
    vertical-align: middle;
    border-radius: 5px;
    border: 1px solid #31de00;
    overflow: hidden;
    padding: .75rem 2rem;
    text-align: center;
    color: #333;
}

.btn-yellow:hover:hover {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    cursor: pointer;
    display: inline-block;
    font-size: 14px;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    background-color: #31de00;
    text-decoration: none;
    vertical-align: middle;
    border-radius: 5px;
    border: 1px solid #31de00;
    overflow: hidden;
    padding: .75rem 2rem;
    text-align: center;
    color: #333;
}

#action-buttonfollowing {
    background-color: #31de00;
    color: #333333;
    border: solid 1px #31de00;
    padding-right: 30px;
  	transition-duration: 0.2s;
}

#action-buttonfollowing:hover {
    background-color: #fff;
    color: #333333;
    border: solid 1px #31de00;
    padding-right: 30px;
}

.learning-item.-finished .item-circle, .learning-item.-finished .learning-item__title {
    color: #38dc05;
    border-color: #38dc05;
}

.item-circle {
    display: inline-block;
    min-width: 36px;
    max-width: 36px;
    height: 36px;
    line-height: 35px;
    color: #099600;
    background-color: #fff;
    border-radius: 100%;
    border-width: 1px;
    border-style: solid;
    border-color: #02d200;
    text-align: center;
    font-size: .85rem;
    letter-spacing: -.5px;
    font-weight: 600;
    position: relative;
}

.learning-item__title {
    margin-left: 12px;
    color: #1e9600;
    overflow: hidden;
    display: flex;
    flex: 1 1 auto;
}

.learning-item.-current .item-circle:not(.no-current-color), .learning-item.-current .learning-item__title {
    color: #179600;
    border-color: #089600;
}

.plselfScoreFeedback .selfbutton.answered {
    border: solid 1px #4dff33;
}

.assignment .default-header.chapter-ob.odd {
    background-color: #31bd32;
}

.topSteps .topStepsButton.current.completed {
    border-bottom: 4px solid #d6d6d6;
    background: linear-gradient(to bottom, #20d200, #20d200 25px, #d6d6d6 4px, #d6d6d6);
}

.topSteps .topStepsButton.completed {
    border-bottom: 4px solid #d6d6d6;
    background: linear-gradient(to bottom, transparent, transparent 25px, #d6d6d6 4px, #d6d6d6);
}

.topSteps .topStepsButton.current {
    color: #FFFFFF;
    font-weight: bold;
    background-color: #20d200;
}

.topSteps .topStepsButton {
    font-weight: normal;
    color: #20d200;
    background-color: #FFFFFF;
    border-left: solid 1px #CCCCCC;
    width: 30px;
    font-family: Helvetica bold, Arial Bold, Sans-serif;
    font-size: 12px;
    text-align: center;
    float: left;
    cursor: pointer;
    height: 100%;
    padding: 6px;
}

.sidebar {
    top: 0;
    margin-top: 80px;
    left: 0;
    bottom: 20px;
    background-color: #edfbee;
    z-index: 101;
    width: 220px;
    display: flex;
    flex-direction: column;
}

.sidebar__header {
    width: 220px;
    background-color: #edfbee;
    display: flex;
    flex-wrap: wrap;
    z-index: 10;
    color: #109600;
    padding-left: 0;
    float: left;
}

.sidebar__modal-toggle {
    flex: 0 1 100%;
    text-align: center;
    border-top: 1px solid #86e87f;
    border-bottom: 1px solid #86e87f;
    height: 30px;
    line-height: 30px;
}

.learning-item__grade {
    font-size: 12px;
    color: #179600;
    line-height: 37px;
    margin-left: 8px;
}

.learning-item:active, .learning-item:hover {
    background-color: #d1f8cd;
    cursor: pointer;
}

.dropdown ul.dropdown-menu li a:active, .dropdown ul.dropdown-menu li a:hover {
    background-color: #25d200;
    color: #fff;
}

.itemPreview .footer .navbar-right .btn:hover {
    color: #FFFFFF;
    border: solid 1px #FFFFFF;
    background: #009605;
}

.itemPreview .footer .navbar-right .btn {
    color: #FFFFFF;
    border: solid 1px #FFFFFF;
    background: #00d210;
}

.pl-container .feature2.featuredPanel * {
    color: #0dd200;
}

.pl-container .textblock a {
    color: #0dd200 !important;
    text-decoration: none;
}

a {
    color: #fff;
    text-decoration: none;
}

.plselfScoreFeedback .selfbutton:hover {
    border: solid 1px #3aff33;
}

.textEntryInteraction:hover .textEntryInput input:hover, .textEntryInput-correct input:hover, .textEntryInteraction:hover .textEntryInput textarea:hover, .textEntryInput-correct textarea:hover, .textEntryInput-correct:hover .answerInput:hover {
    border: solid 1px #2cff4b;
}

.textEntryInteraction:focus .textEntryInput input:focus, .textEntryInput-correct input:focus, .textEntryInteraction:focus .textEntryInput textarea:focus, .textEntryInput-correct textarea:focus, .textEntryInput-correct:focus .answerInput:focus {
    border: solid 1px #2cff4b;
}

.topSteps:hover .topStepsButton:hover {
    font-weight: normal;
    color: #00d206;
    background-color: #FFFFFF;
    border-left: solid 1px #CCCCCC;
    width: 30px;
    font-family: Helvetica bold, Arial Bold, Sans-serif;
    font-size: 12px;
    text-align: center;
    float: left;
    cursor: pointer;
    height: 100%;
    padding: 6px;
}
