css:
@-moz-document domain("www.nitrotype.com")
{
 body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .news-teaser
  {
    color: #fff;
  }

  .flash--beta
  {
    display: none;
  }

  .theme--pDefault .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/665021465665470464/715325417073934384/thanc.png") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
    background-size: 100%
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://cdn.discordapp.com/attachments/665021465665470464/715325417073934384/thanc.png") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
   background-size: 100%
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/665021465665470464/715325417073934384/thanc.png") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
   background-size: 100%
  }

  .card--a
  {
    background-color: #fff0;
    color: #ffffff;
  }

  .card--shadow-l
  {
    box-shadow: none;
  }
  .theme--pDefault .profile-levelNum {
    color: #ffffff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }
  .theme--pDefault .profile-title {
    color: #ffffff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }
  .btn--inky, .btn--inky:focus:not(:hover) {
    color: #ffffff;
  }
  .card--b {
    color: #fff;
  }
  .theme--pDefault .profile-body {
    color: #ffffff;
  }

  html
  {
    background: fixed url("https://cdn.discordapp.com/attachments/665021465665470464/715325975570939904/Screen_Shot_2020-05-27_at_6.09.48_PM.png") repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
  }

  .structure-ad
  {
    display: none;
  }

  .ad
  {
    display: none;
    position: relative;
    margin-right: auto;
    margin-left: auto;
    text-align: center;
  }

  .ad--sq
  {
    display: none;
    width: 336px;
    height: 280px;
    background: #f2f2f2;
  }

  .ad--ldrbrd
  {
    display: none;
    width: 728px;
    height: 90px;
    background: #f2f2f2;
  }

  .ad--ldrbrd_l
  {
    display: none;
    width: 970px;
    height: 90px;
  }

  .ad--fixed
  {
    display: none;
    position: fixed;
    top: 30px;
  }

  .structure-header
  {
    margin-left: auto;
    margin-right: auto;
    max-width: 1020px;
    padding-left: 0px;
    padding-right: 0px;
  }

  .card--grit
  {
    background-image: url("https://cdn.discordapp.com/attachments/665021465665470464/715328318374805574/huh.png");
      background-size: 100%
  }

  .table--b .table-cell
  {
    border-bottom: none;
  }

  .tc-ts
  {
    color: #f7f4f4;
  }

  .card--b
  {
    color: #fff;
  }

  .tc-emerald
  {
    color: #00ff6c;
  }

  .tc-i
  {
    color: #fff;
  }

  .tc-dodger
  {
    color: #0004ff;
  }

  .tc-lemon
  {
    color: #ffcb00;
  }

  .news--solo .news-body p
  {
    color: #fff;
  }

  .news--solo .news-body .yellow
  {
    color: #a28100;
  }

  .link--nav
  {
    color: #000000ba;
  }

  .link--nav.is-current
  {
    color: #000;
  }

  .link--nav:hover
  {
    color: #000;
  }

  .bg--gradient
  {
    background: #00000000;
  }

  path:nth-child(2)
  {
    fill: #fff;
  }

  path:nth-child(3)
  {
    fill: #fff;
  }

  path:nth-child(4)
  {
    fill: #fff;
  }

  path:nth-child(5)
  {
    fill: #fff;
  }

  path:nth-child(6)
  {
    fill: #fff;
  }

  path:nth-child(8)
  {
    fill: #fff;
  }
   .dash-copyContainer
  {
    background: #1d1e23;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }

  .dash-copy
  {
    color: #fff;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #1976c6;
    color: white;
  }
}
