@-moz-document domain("nitrotype.com")
{
body {
    background: none;
}
html {
    background: fixed url("https://mocah.org/uploads/posts/4599806-space-planet-space-art-purple-digital-art-artwork.jpg"), #001d45;
    background-size: 100%;
}
.bg--gradient {
    background: fixed;
}
.theme--pDefault .profile-bg
  {
    background: url("https://wallpaperaccess.com/full/459740.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
    background-size: 110%
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://wallpaperaccess.com/full/459740.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
   background-size: 110%
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://wallpaperaccess.com/full/459740.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
   background-size: 110%
  }
  .structure.theme--season28 
  {
      background-image: none;
  }

  .card--a
  {
    background-color: #fff0;
    color: #ffffff;
  }

  .card--shadow-l
  {
    box-shadow: none;
  }
  .theme--pDefault .profile-levelNum {
    color: #ffffff;
