css:
@-moz-document domain("t.bilibili.com") {
/* 背景色 */
#app {
    background: #080f1e;
}
#app .fixed-bg {
    display: none
}
/* 加载过程 */
#app .loading-content .loading-text .loading-img {
    display: none
}
/* 顶栏 */
#app .sticky-bar {
    background: #1a335b;
    box-shadow: none
}
#app .sticky-bar .bar-content .title {
    color: #c3c4ce
}
/* 左边栏-用户面板 */
#app .user-panel {
    background: #092951;
    height: auto;
}
#app .user-panel .content {
    height: 148px;
}
#app .user-pic {
    display: none;
}
#app .user-panel .content .user-name {
    padding-top: 35px;
    margin-left: 125px;
    color: #c3c4ce;
    font-size: 15px;
}
#app .user-panel .content .bottom .number-part .numbers {
    padding-top: 30px;
}
#app .user-panel .content .user-head {
    top: 15px;
    left: 40px;
}
}

@-moz-document domain("t.bilibili.com") {
/* 中间栏-发送栏 */
.home-page .home-container .home-content .center-panel .section-block,
.publish-panel {
    background-color: #092951!important;
}
.input-section .textarea {
    background-color: #121e3a!important;
    color: #8c9ab6
}
.input-section .placeholder-shown::before {
    color: #7896be;
}
.input-section {
    border: 1px solid #092951!important;
    color: #909aa4!important;
}
.input-section:hover,
.input-section:focus,
.input-section.active {
    background-color: #121e3a!important
}
.input-section .editor:focus,
.input-section .editor:hover {
    background-color: #262a38!important;
    color: #8fa8d4!important
}
.static-popup {
    background-color: #3d568e!important;
    border: none!important
}
.static-popup.bp-arrow:before {
    display: none!important
}
/* 表情 */
.static-popup .title {
    color: #fff
}
.static-popup .emoji {
    background-color: #3d568e;
    color: #9cb5e0
}
.static-popup .emoji:hover {
    background-color: #20335e
}
.static-popup .pagination {
    background-color: #253760
}
.static-popup .emoji-cover.selected {
    background-color: #4f6fb7
}
.static-popup .emoji-cover .kaomoji {
    color: #fff
}
/* 话题/@ */
#app .bg-white {
    background-color: #3d568e;
    border: none
}
#app .hash-popup .topic-container .item.is-selected,
#app .at-popup .user-container .item.is-selected {
    background-color: #1f2d4f;
    color: #9cb5e0
}
#app .hash-popup .topic-container .item,
#app .at-popup .user-container .item {
    color: #9cb5e0
}
/* 投票 */
.vote-box .create-vote {
    border: none;
    background-color: #3d568e;
}
.vote-box .title,
.vote-box .bp-radio,
.button-bar .bp-button {
    color: #fff
}
input::-webkit-input-placeholder {
    color: #a2b1c6;
}
.bp-input .input-box .indicator,
.bp-vote-container .bp-selector select {
    background-color: #3d568e;
    color: #a2b1c6;
}
/* 定时 */
.input-section .schedule-panel,
.schedule-panel .date-input {
    background-color: #0c1937
}
.schedule-panel .date-input > input,
.schedule-panel .date-input > input:focus,
.schedule-panel .scroll-input .s-input,
.schedule-panel .scroll-input .s-input:focus,
.schedule-panel .s-panel,
.schedule-panel .date-panel,
.schedule-panel .date-panel:hover {
    background-color: #3d568e;
    border: none
}
.schedule-panel .s-item {
    color: #a2b1c6;
}
.date-panel .ym {
    border-bottom: none;
    color: #a2b1c6
}
.ym .left {
    border-left: 2px solid #a2b1c6
}
.ym .arrow {
    border-top: 2px solid #a2b1c6
}
.ym .right {
    border-right: 2px solid #a2b1c6
}
.md .week {
    color: #0a1737
}
.md .day.disabled {
    color: #333
}
.md .day {
    color: #ccc
}
}

@-moz-document domain("t.bilibili.com") {
/* 中间栏-动态栏 */
.feed-card .bg-white {
    background-color: #092951
}

.feed-card .card .main-content .user-name a {
    color: #c6cfdb
}
.feed-card .new-notice-bar {
    background: #476b97
}
.feed-card .new-notice-bar .message {
    color: #c6cfdb
}
.feed-card .tab-bar,
.feed-card .imagesbox .boost-img {
    background: #092951
}
.feed-card .card {
    background: #092951;
    border: none!important
}
.feed-card .content {
    color: #8c9ab6
}
.feed-card .video-container {
    border: none;
    background: #184184
}
.feed-card .video-container .text-area .title,
.feed-card .live-container .text-area .title,
.feed-card .bangumi-container .text-area .title,
.feed-card .article-container .text-area .title {
    color: #dbe3e8
}
.feed-card .video-container .text-area .content {
    color: #73abca
}
.feed-card .video-container .types,
.feed-card .live-container .status {
    background: #305591;
    color: #dbe3e8
}
.feed-card .imagesbox .boost-control {
    background: #305591
}
.feed-card .imagesbox .boost-control li {
    color: #8c9ab6
}
.feed-card .card .more-panel {
    background: #305591;
    border: 1px solid #305591;
    color: #dbe3e8
}
.feed-card .card .more-panel:after {
    background: #305591;
    border-top: 1px solid #305591;
    border-left: 1px solid #305591;
}
.feed-card .card-content .repost,
.feed-card .card-content .not-support,
.feed-card .card-content .deleted {
    background: #305591
}
.feed-card .live-container,
.feed-card .bangumi-container,
.feed-card .article-container {
    background: #113159;
    border: none;
}
.feed-card .card-content .repost .content {
    color: #b9c2d5
}
.feed-card .article-container:hover .text-area {
    box-shadow: none;
    background: #163b6a
}
.feed-card .bp-popup-panel {
    background: #305591
}
.feed-card .im-popup .bp-popup-panel .title-ctnr .popup-title {
    color: #b9c2d5!important
}
.feed-card .bp-popup-panel .content .content-text {
    color: #bbc5d0;
}
.feed-card .bp-popup-panel .content .selector-box {
    color: #a1b8d0;
}
.feed-card .bl-button--primary:disabled {
    background-color: #71747d
}
.feed-card .bl-button--ghost {
    color: #bbc2d5;
    border: 1px solid #bbc2d5;
}
.feed-card .panel-area .bb-comment .fold-comment,
.feed-card .forw-area {
    background-color: #092951
}
.feed-card .bb-comment,
.feed-card .comment-bilibili-fold {
    background: #092951
}
.feed-card .bb-comment .comment-header .tabs-order .new-sort,
.feed-card .dynamic-list-item-wrap .item-detail .text {
    color: #bbc2d5
}
.feed-card .bb-comment .comment-header,
.feed-card .comment-bilibili-fold .comment-header {
    border-bottom: 1px solid #71747d;
}
.feed-card .panel-area .bb-comment .bottom-page.center,
.feed-card .dynamic-list-item-wrap,
.feed-card .forw-area .forw-more {
    border-top: 1px solid #71747d;
}
.feed-card .bb-comment .comment-list .list-item .con,
.feed-card .comment-bilibili-fold .comment-list .list-item .con {
    border-top: 1px solid #71747d;
}
.feed-card .textarea-container .textarea {
    background-color: #121e3a!important;
    color: #8c9ab6
}
.feed-card .textarea-container textarea,
.feed-card .textarea-container .comm-nologin {
    border: 1px solid #71747d;
}
.feed-card .textarea-container .comm-tool .comm-emoji {
    background-color: #121e3a!important;
    color: #8c9ab6;
    border: 1px solid #71747d;
}
.feed-card .at-popup {
    border: 1px solid #71747d;
}
.feed-card .fold-box {
    border-top: 1px solid #092951
}
}

@-moz-document domain("t.bilibili.com") {
/* 右边栏 */
/* 公告栏 */
#app .notice-panel {
    display: none!important
}
/* 正在直播 */
#app .live-panel,
#app .tag-panel {
    background-color: #092951
}
#app .live-panel .title,
#app .tag-panel .title {
    color: #b9c2d5
}
.live-panel .tc-dark-slate {
    color: #bbc5d0
}
/* 用户信息 */
#app .userinfo-wrapper {
    border: 1px solid #184184;
    box-shadow: none
}
.userinfo-wrapper .userinfo-content {
    background: #184184;
    padding: 10px 20px
}
#app .userinfo-wrapper .bg {
    display: none;
    height: 0px
}

.userinfo-wrapper .userinfo-content .face {
    top: 15px;
    border: 2px solid #092951;
    left: 15px
}
.userinfo-wrapper .userinfo-content .info p {
    color: #bbc5d0
}
.userinfo-wrapper .userinfo-content .info {
    padding: 0px 5px 10px 65px
}
.userinfo-wrapper .userinfo-content .info .user .name {
    color: #dbe3e8
}
.userinfo-wrapper .userinfo-content .btn-box {
    padding: 0 0 5px 65px;
}
}

@-moz-document domain("t.bilibili.com") {
/* chrome 滚动条 */
::-webkit-scrollbar {
    width: 8px;
}
::-webkit-scrollbar-thumb {
    background: #4a7bca!important;
    border-bottom: 0px solid #092951!important;
    border-left: 2px solid #092951!important;
    border-right: 2px solid #092951!important;
    border-top: 0px solid #092951!important;
    border-radius: 100px;
}
::-webkit-scrollbar-track-piece {
    background: #092951!important
}
}

@-moz-document domain("t.bilibili.com") {
/* 左侧栏-正在直播 */
#app .left-panel .live-panel .title span,
#app .left-panel .live-panel .live-up .live-detail .up-name {
    color: #b9c2d5!important
}
#app .left-panel .live-panel .live-up .live-up-img-box {
    background: none!important
}

/* 中间栏-发送 */
/* 输入框 */
#app .publish-panel .editor .core-style {
    background-color: #121e3a!important;
    border: 1px solid #092951!important;
    color: #8c9ab6
}
/* 定时 */
.publish-panel .schedule-bar,
.schedule-bar .date-input {
    background-color: #0c1937
}
.schedule-bar .date-input > input,
.schedule-bar .date-input > input:focus,
.schedule-bar .scroll-input .s-input,
.schedule-bar .scroll-input .s-input:focus,
.schedule-bar .s-panel,
.schedule-bar .date-panel,
.schedule-bar .date-panel:hover {
    background-color: #3d568e;
    border: none
}
.schedule-bar .s-item {
    color: #a2b1c6;
}

/* 中间栏-动态 */
/* 追番 */
#app .feed-card .empty-content {
    background-color: #092951
}
/* 回复 */
.feed-card .textarea-container textarea {
    background-color: #121e3a!important;
    color: #8c9ab6
}
.feed-card .textarea-container textarea,
.feed-card .textarea-container .comm-nologin {
    border: 1px solid #71747d!important;
}
.feed-card .comment-send .comment-emoji {
    background-color: #121e3a!important;
    color: #8c9ab6;
    border: 1px solid #71747d!important;
}

/* 右侧栏-话题 */
#app .new-topic-panel {
    background-color: #092951
}
#app .new-topic-panel .title {
    color: #b9c2d5
}
#app .new-topic-panel .tag-item .label {
    background: none;
    color: #dbe3e8
}
}

@-moz-document domain("t.bilibili.com") {
/* 去除圆角 */
#app div,
#app .video-container .image-area img,
#app button,#app textarea {
    border-radius: 0px
}

/* 保留头像圆框 */
#app .live-up-img {
    border-radius: 21px!important
}
#app .new-topic-panel .tag-item .label .label-icon {
    border-radius: 9px!important
}
}
