css:
@-moz-document domain('www.dizajnzona.com') {
	 body {
		 background-color: #212529;
	}
	 body .ipsFollow {
		 display: none !important;
	}
	 body #ipsLayout_header #elMobileNav, body #ipsLayout_header header {
		 background-color: #F1621A !important;
		 box-shadow: 0 0 20px 5px rgba(241,98,26,0.2);
	}
	 @media screen and (max-width: 928px) {
		 body #ipsLayout_header #elMobileNav .site-logo, body #ipsLayout_header header .site-logo {
			 margin-top: 0 !important;
		}
	}
	 body #ipsLayout_header nav {
		 display: none !important;
	}
	 body .ipsAttach {
		 border: none !important;
	}
	 body .ipsType_pageTitle {
		 color: #F1621A;
	}
	 body .ipsPageHeader .ipsType_blendLinks a {
		 color: #F1621A;
	}
	 body .ipsPageHeader .ipsType_pageTitle {
		 color: #F1621A;
	}
	 body .ipsPageHeader p {
		 color: rgba(255,255,255,0.5);
	}
	 body .ipsBreadcrumb ul a, body .ipsBreadcrumb ul li {
		 color: #FFF !important;
	}
	 body .ipsButtonBar {
		 padding: 10px;
		 margin-bottom: 10px;
		 background-color: #3D434B;
		 border: none;
		 border-radius: 10px;
	}
	 body .ipsButtonBar .ipsPagination a {
		 transition: all 0.2s linear;
	}
	 body .ipsButtonBar .ipsPagination .ipsPagination_first a, body .ipsButtonBar .ipsPagination .ipsPagination_last a, body .ipsButtonBar .ipsPagination .ipsPagination_next a, body .ipsButtonBar .ipsPagination .ipsPagination_page a, body .ipsButtonBar .ipsPagination .ipsPagination_prev a {
		 background-color: rgba(255,255,255,0.25) !important;
		 color: #212529 !important;
		 border-radius: 15px;
	}
	 body .ipsButtonBar .ipsPagination .ipsPagination_first a:hover, body .ipsButtonBar .ipsPagination .ipsPagination_last a:hover, body .ipsButtonBar .ipsPagination .ipsPagination_next a:hover, body .ipsButtonBar .ipsPagination .ipsPagination_page a:hover, body .ipsButtonBar .ipsPagination .ipsPagination_prev a:hover {
		 background-color: rgba(255,255,255,0.5) !important;
	}
	 body .ipsButtonBar .ipsPagination .ipsPagination_active a {
		 pointer-events: none;
		 background-color: #3D434B !important;
		 color: #F1621A !important;
		 text-shadow: 0 0 4px #F1621A !important;
	}
	 body .ipsButtonBar .ipsPagination .ipsPagination_pageJump a {
		 color: #FFF;
	}
	 body .ipsButton:not(ipsButton_noHover) {
		 color: #FFF;
	}
	 body .ipsButton:not(ipsButton_noHover):hover {
		 background-color: rgba(255,255,255,0.1);
	}
	 body .ipsButton_important {
		 background-color: #F1621A !important;
		 box-shadow: 0 0 20px 10px rgba(241,98,26,0.25);
		 transition: all 0.2s linear;
		 border-radius: 10px !important;
	}
	 body .ipsButton_important:hover {
		 box-shadow: 0 0 30px 15px rgba(241,98,26,0.3);
	}
	 body .ipsBox {
		 background-color: transparent !important;
		 border: none;
		 box-shadow: none;
	}
	 body .ipsBox .ipsType_sectionTitle {
		 margin-bottom: 10px;
		 margin-top: 30px;
		 border-radius: 10px;
		 line-height: 30px;
		 background-color: #3D434B;
	}
	 body .ipsBox .ipsType_sectionTitle .cForumToggle {
		 opacity: 0.5;
	}
	 body .ipsBox .ipsType_sectionTitle .cForumToggle:hover {
		 opacity: 1;
	}
	 body .ipsBox .ipsDataList {
		 background-color: transparent !important;
	}
	 body .ipsBox .ipsDataList .ipsDataItem {
		 background-color: #272B2F;
		 margin-bottom: 10px;
		 border-radius: 10px;
		 transition: all 0.2s linear;
	}
	 body .ipsBox .ipsDataList .ipsDataItem:nth-child(even) {
		 background-color: #272B2F !important;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_icon .ipsItemStatus {
		 background-color: #3D434B;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_icon .ipsItemStatus i {
		 color: #FFF !important;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_main .ipsDataItem_title a {
		 color: #f27232 !important;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_main .ipsDataItem_title a:hover {
		 color: #F1621A !important;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_main .ipsPagination_mini {
		 opacity: 1 !important;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_main .ipsPagination_mini .ipsPagination_last a, body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_main .ipsPagination_mini .ipsPagination_page a {
		 padding: 0 6px;
		 background-color: rgba(255,255,255,0.05);
		 color: #FFF;
		 border-radius: 10px/2;
		 text-shadow: none !important;
		 transition: all 0.2s linear;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_main .ipsPagination_mini .ipsPagination_last a:hover, body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_main .ipsPagination_mini .ipsPagination_page a:hover {
		 background-color: rgba(255,255,255,0.1);
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_main .ipsDataItem_subList a {
		 color: #f27232 !important;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_main .ipsDataItem_subList a:hover {
		 color: #F1621A !important;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_main .ipsDataItem_meta {
		 color: rgba(255,255,255,0.5);
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_main .ipsDataItem_meta a {
		 color: rgba(255,255,255,0.5);
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_main .ipsDataItem_meta a:hover {
		 color: #F1621A;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_stats {
		 color: #FFF;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_stats .ipsType_warning {
		 color: #F1621A;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_stats .ipsType_warning a, body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_stats .ipsType_warning i {
		 color: #F1621A;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_lastPoster li {
		 color: rgba(255,255,255,0.5);
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_lastPoster li a {
		 color: rgba(255,255,255,0.75);
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_lastPoster li a:hover {
		 color: #FFF;
	}
	 body .ipsBox .ipsDataList .ipsDataItem .ipsDataItem_lastPoster li.ipsType_light a {
		 color: rgba(255,255,255,0.25);
	}
	 body .ipsBox .ipsDataList .ipsDataItem.ipsDataItem_unread {
		 background-color: #4f362b !important;
	}
	 body .ipsBox .ipsDataList .ipsDataItem.ipsDataItem_unread .ipsDataItem_icon .ipsItemStatus {
		 background-color: #F1621A;
	}
	 body .ipsBox header .ipsPageHead_barText {
		 background-color: transparent;
	}
	 body .ipsBox header #elProfileStats {
		 background-color: #272B2F !important;
	}
	 body .ipsBox #elProfileInfoColumn {
		 background-color: #3D434B !important;
	}
	 body .ipsBox #elProfileInfoColumn .cProfileRepScore {
		 background-color: #F1621A;
		 border-radius: 10px;
	}
	 body .ipsBox #elProfileInfoColumn .ipsWidget {
		 background-color: rgba(0,0,0,0.1) !important;
		 border-radius: 10px;
		 overflow: hidden;
	}
	 body .ipsBox #elProfileInfoColumn .ipsWidget .ipsWidget_title {
		 background-color: #212529;
	}
	 body .ipsBox #elProfileInfoColumn .ipsWidget .ipsWidget_inner {
		 padding: 10px;
	}
	 body .ipsBox #elProfileInfoColumn .ipsWidget .ipsWidget_inner .ipsDataList .ipsDataItem {
		 border: none !important;
		 padding: 5px;
		 color: rgba(255,255,255,0.75);
		 border-radius: 10px/2;
	}
	 body .ipsBox #elProfileInfoColumn .ipsWidget .ipsWidget_inner .ipsDataList .ipsDataItem:last-child {
		 margin-bottom: 0;
	}
	 body .ipsBox #elProfileInfoColumn .ipsWidget .ipsWidget_inner .ipsDataList .ipsDataItem a {
		 color: rgba(241,98,26,0.8);
	}
	 body .ipsBox #elProfileInfoColumn .ipsWidget .ipsWidget_inner .ipsDataList .ipsDataItem a:hover {
		 color: #F1621A;
	}
	 body .ipsBox .ipsTabs {
		 background-color: #3D434B;
	}
	 body .ipsBox .ipsTabs li a {
		 border-top-left-radius: 10px;
		 border-top-right-radius: 10px;
	}
	 body .ipsComment {
		 background-color: #272B2F !important;
		 border-radius: 10px;
	}
	 body .ipsComment .ipsComment_author h3 {
		 color: rgba(255,255,255,0.75);
	}
	 body .ipsComment .ipsComment_author .cAuthorPane_info li {
		 color: rgba(255,255,255,0.5);
	}
	 @media screen and (min-width: 10px) {
		 body .ipsComment .cAuthorPane {
			 background-color: transparent !important;
		}
		 body .ipsComment .cAuthorPane .cAuthorPane_author {
			 color: #FFF;
		}
	}
	 body .ipsComment .ipsColumn .ipsComment_meta {
		 color: rgba(255,255,255,0.25);
	}
	 body .ipsComment .ipsColumn .ipsComment_meta a {
		 color: rgba(255,255,255,0.25);
	}
	 body .ipsComment .ipsColumn .ipsComment_meta a:hover {
		 color: rgba(255,255,255,0.5);
	}
	 body .ipsComment .ipsColumn .ipsPad .ipsEmbeddedVideo {
		 background-color: #3D434B;
		 border-radius: 10px;
	}
	 body .ipsComment .ipsColumn .ipsPad .ipsEmbeddedVideo iframe {
		 border: none !important;
		 outline: none !important;
	}
	 body .ipsComment .ipsColumn .ipsPad .cke_contents {
		 color: #212529;
	}
	 body .ipsComment .ipsColumn .ipsPad .ipsType_richText {
		 color: rgba(255,255,255,0.8);
	}
	 body .ipsComment .ipsColumn .ipsPad .ipsType_richText a {
		 color: rgba(241,98,26,0.8);
	}
	 body .ipsComment .ipsColumn .ipsPad .ipsType_richText a:hover {
		 color: #F1621A;
	}
	 body .ipsComment .ipsColumn .ipsPad .ipsType_richText strong {
		 display: block;
		 width: 100%;
		 margin-top: 40px;
	}
	 body .ipsComment .ipsColumn .ipsPad pre {
		 border: 1px solid #3D434B !important;
		 background: #212529;
		 color: rgba(255,255,255,0.5);
		 border-radius: 10px;
		 overflow: hidden;
	}
	 body .ipsComment .ipsColumn .ipsPad blockquote {
		 border: 1px solid #3D434B;
		 background: #212529;
		 border-radius: 10px;
		 overflow: hidden;
	}
	 body .ipsComment .ipsColumn .ipsPad blockquote div {
		 color: rgba(255,255,255,0.5);
		 padding: 10px;
	}
	 body .ipsComment .ipsColumn .ipsPad blockquote div.ipsQuote_citation {
		 background-color: #3D434B;
		 line-height: normal;
	}
	 body .ipsComment .ipsColumn .ipsPad blockquote div.ipsQuote_citation.ipsQuote_closed a {
		 color: #FFF !important;
	}
	 body .ipsComment .ipsColumn .ipsPad blockquote div .fa-share {
		 color: #FFF;
	}
	 body .ipsComment .ipsColumn .ipsPad .ipsComment_controls a {
		 color: rgba(241,98,26,0.5);
	}
	 body .ipsComment .ipsColumn .ipsPad .ipsComment_controls a:hover {
		 color: #F1621A;
	}
	 body .ipsComment.ipsModerated {
		 background-color: #3b312d !important;
	}
	 body .ipsComment.ipsModerated .ipsComment_author, body .ipsComment.ipsModerated .ipsComment_content {
		 background-color: transparent;
	}
	 body .ipsComment.ipsModerated .ipsComment_controls a {
		 color: #FFF !important;
	}
	 body .cTopicPostArea {
		 background-color: #3D434B;
	}
	 body .cTopicPostArea .ipsToolList {
		 color: #FFF;
	}
	 body .cWidgetContainer .ipsWidget {
		 border-radius: 10px;
		 overflow: hidden;
	}
	 body .cWidgetContainer .ipsWidget .ipsWidget_title {
		 background-color: #3D434B;
	}
	 body .cWidgetContainer .ipsWidget .ipsWidget_inner {
		 background-color: rgba(0,0,0,0.1);
	}
	 body .cWidgetContainer .ipsWidget .ipsWidget_inner a {
		 color: rgba(241,98,26,0.8);
	}
	 body .cWidgetContainer .ipsWidget .ipsWidget_inner a:hover {
		 color: #F1621A;
	}
	 .ipsPager_next a, .ipsPager_prev a {
		 color: #F1621A !important;
	}
	 .ipsPager_next a span, .ipsPager_prev a span {
		 color: inherit;
	}
	 .ipsPager_next a:after, .ipsPager_prev a:after {
		 color: inherit !important;
	}
	 #ipsLayout_footer {
		 display: none;
	}
	 #ipsLayout_body {
		 padding-bottom: 100px;
	}
	 .ipsBox_alt #modcp_menu {
		 border-radius: 10px;
		 background-color: #3D434B;
		 overflow: hidden;
	}
	 .ipsBox_alt #modcp_menu .ipsSideMenu_title {
		 height: 40px;
		 line-height: 40px;
		 font-size: 16px;
		 background-color: #272B2F;
		 color: #3D434B;
	}
	 .ipsBox_alt #modcp_menu .ipsSideMenu_list .ipsSideMenu_item a {
		 color: rgba(255,255,255,0.8);
	}
	 .ipsBox_alt #modcp_menu .ipsSideMenu_list .ipsSideMenu_item a:hover {
		 color: #FFF;
		 background-color: rgba(255,255,255,0.1);
	}
	 .ipsBox_alt #elModCPContent {
		 padding: 0 10px !important;
	}
	 .ipsBox_alt #elModCPContent #elModCPMemberSearch {
		 background-color: #3D434B !important;
		 color: #FFF;
		 border-radius: 10px;
	}
	 .ipsBox_alt #elModCPContent #elmodCPTabs {
		 background-color: transparent;
		 padding: 0;
	}
	 .ipsBox_alt #elModCPContent #elModCPMemberTabContent {
		 padding: 0;
	}
	 .ipsBox_alt #elModCPContent #elModCPMemberTabContent #ipsTabs_elmodCPTabs_modcp_banned_panel {
		 padding: 10px 10px 1px;
		 background-color: #FFF;
		 border-bottom-left-radius: 10px;
		 border-bottom-right-radius: 10px;
	}
	 .ipsBox_alt #elModCPContent #elModCPMemberTabContent #ipsTabs_elmodCPTabs_modcp_banned_panel .ipsDataItem {
		 background-color: #CCC !important;
	}
	 .ipsDataItem {
		 border: none;
		 background-color: #272B2F !important;
	}
	 .ipsDataItem:nth-child(even) {
		 background-color: #272B2F !important;
	}
	 .ipsButton_light {
		 background-color: rgba(241,98,26,0.5) !important;
		 border: none !important;
	}
	 .ipsButton_light:hover {
		 background-color: #F1621A !important;
	}
	 .ipsCommentUnreadSeperator {
		 border-color: #F1621A;
		 box-shadow: 0 0 10px 5px rgba(241,98,26,0.25);
	}
	 .cTopicList .ipsItemStatus {
		 background: transparent !important;
	}
}
