css:
@-moz-document domain(skyscrapercity.com) {
/* common */
.california-block-outer-upper-nav {
    display: -webkit-box;
    display: flex;
    flex-wrap: wrap;
}

/* header */
html {
    background: #f5f5f5;
    font: 13px arial, sans-serif;
}

/* header */
header.is-sticky {
    display: none !important;
}

.p-header-banner-placeholder {
    width: 100%;
    height: 70px;
    overflow: hidden;
    display: block;
}

.p-header-banner-placeholder img {
    display: none;
}

.header-content-wrapper h1 {
    display: block;
    font-size: 2em;
    margin-block-start: 0em;
    margin-block-end: 0em;
    margin-inline-start: 0px;
    margin-inline-end: 0px;
    font-weight: bold;
}


/* sidebar */
div.sidebar {
    display: none !important;
}


/* nav */
.headerNav {
    padding: 10px 0px;
}

.california-header-nav {
    color: #383a3b !important;
    margin-bottom: 0px;
}

.pageNav-page > a {
    display: block;
    padding: 7px 8px;
    text-decoration: none;
    min-width: 36px;
    height: 32px;
}


/* section links */
.p-sectionLinks {
    font-size: 14px;
}

.p-sectionLinks-inner {
    max-width: 1200px;
    padding: 0px 10px;
    margin: 0 auto;
    padding-left: 2px;
    padding-right: 2px;
}

/* page nav */
.pageNav-page.pageNav-page--current {
    color: #fefefe;
    cursor: pointer;
    border: none;
}

/* body */
.p-body {
    display: -webkit-box;
    display: flex;
    -webkit-box-align: stretch;
    align-items: stretch;
    -webkit-box-flex: 1;
    flex-grow: 1;
    min-height: 1px;
}

.p-body-header {
    margin-bottom: 0px;
    margin-top: 0px;
}

.p-body-inner {
    display: -webkit-box;
    display: flex;
    flex-direction: column;
    width: 100%;
    max-width: 1200px;
    padding: 0 20px;
    margin: 0 auto;
    padding-left: 0px;
    padding-right: 0px;
    padding-top: 10px;
    padding-bottom: 0px;
}

.california-additional-header h1 {
    font-size: 18px;
    font-weight: 400;
    line-height: 45px;
    width: 100%;
}

/* message */

article.message--post {
    background: #ffffff !important;
    padding: 1.2em 1em 0.8em 1.2em ! important;
    margin: 1em !important;
}

div.message-cell {
    padding: 0px !important;
}

html.template-thread_view div.p-pageWrapper {
    background: #f0f3f5 !important;
}


div.actionBar-set.actionBar-set--internal {
    line-height: initial !important;    
}

div.block-outer,
form.block.js-quickReply,
div.message.js-newMessagesIndicator
{
    padding: 0em 1em !important;
}

