css:
@-moz-document domain(skyscrapercity.com) {
  .message {
    border: 1px solid #273657;
    background: #f5f5ff;
    font: 10pt verdana, geneva, lucida, "lucida grande", arial, helvetica,
      sans-serif;
  }

  .message-inner {
    border: 1px solid #cecddd;
    display: flex;
  }

  .message-cell--main {
    z-index: 1;
  }

  .california-options-button-inner {
    z-index: 2;
  }

  .message-attribution {
    background: rgb(2, 0, 36);
    background: linear-gradient(#34487f, #51608e);
    color: white;
    margin: -6px -6px -6px -166px;
    padding: 6px;
    z-index: 1;
  }

  .message-body {
    font: 10pt verdana, geneva, lucida, "lucida grande", arial, helvetica,
      sans-serif;
  }

  .block--messages .message {
    background: #f5f5ff;
  }

  .message-cell.message-cell--user {
    background: #e1e4f2;
    border-right: 1px solid #d1d1e1;
    padding: 35px 6px 6px 6px;
    width: 160px;
    flex: 0 0 160px;
  }

  .message + .message,
  .message.message--bordered {
    border-top: 1px solid #273657;
  }

  .sidebar {
    display: none;
  }

  .message-cell.california-message-cell {
    padding: 6px;
  }

  .avatar,
  .avatar img:not(.cropImage) {
    border-radius: 0;
  }

  a {
    color: #22229c;
  }

  a:hover {
    color: #060635;
  }

  .message-cell .message-cell--user .username {
    font-size: 14pt;
    text-decoration: underline;
  }

  .california-options-button {
    margin-top: -14px;
  }

  .message-footer {
    border-top: 1px solid #d1d1e1;
    margin-top: 0;
  }

  .structItemContainer {
    border: 1px solid #0b198c;
  }

  .structItemContainer-group {
    border: 1px solid #d1d1e1;
  }
  .california-thread-item {
    border-bottom: 1px solid #d1d1e1;
    margin-top: 0;
    background: #f5f5ff;
    padding: 0;
  }

  .structItem-cell {
    border-right: 1px solid #d1d1e1;
    padding: 6px;
    text-align: center;
  }

  .structItem-cell.structItem-cell--main {
    padding: 6px;
    text-align: left;
  }

  .california-thread-item .forum-view-stats {
    color: black;
    font-size: 13px;
  }

  .california-thread-item .structItem-title {
    font-size: 14px;
  }

  .california-thread-item .structItem-parts > li,
  .structItem-username > a.username {
    font-size: 12px;
  }

  .reply-count-cell,
  .last-post-cell {
    background: #e1e4f2;
  }

  .last-post-cell {
    border-right: 0;
  }

  .california-forum-view-thread-list-container
    .california-forum-view-thread-list
    > * {
    border-top: 0;
  }

  .structItem-pageJump > a {
    color: #0b198c;
    font-weight: bold;
  }

  .button.button--primary,
  a.button.button--primary {
    background: #738fbf;
    border: 0;
  }

  .button.button--primary:hover,
  a.button.button--primary:hover {
    background: #0b198c;
  }

  .button.button--cta,
  a.button.button--cta,
  .pageNav-page.pageNav-page--current,
  .node-icon .node--unread {
    background: #738fbf;
  }

  .button.button--cta:focus,
  .button.button--cta:hover,
  a.button.button--cta:focus,
  a.button.button--cta:hover,
  .pageNav-page.pageNav-page--current:active,
  .pageNav-page.pageNav-page--current:hover {
    background: #0b198c;
    color: white;
  }

  .pageNav-page--current > a:hover {
    color: white;
  }

  .bbCodeBlock {
    background: #e1e4f2;
    border-left-color: #738fbf;
  }

  .sticky-divider {
    background: #788cb7;
  }

  .last-poster {
    flex-direction: column;
  }

  .last-poster .username {
    font-size: inherit;
  }

  .message-attribution-main time::before {
    content: attr(title) ", ";
    display: inline;
  }

  .bbWrapper a {
    color: #22229c;
    text-decoration: underline;
  }

  .bbWrapper a:hover {
    color: #060635;
    text-decoration: underline;
  }

  .block--messages .message-cell:first-child {
    border-radius: 0;
  }

  .message-signature .signature-expand,
  .message-signature.signature-collapsed .signature-overlay {
    display: none;
  }
  .message-signature.signature-collapsed {
    overflow: visible;
    max-height: unset;
  }

  .message-attribution-main {
    display: flex;
    flex-direction: row-reverse;
    width: 100%;
    justify-content: space-between;
    padding-right: 30px;
  }

  .message-attribution-main .dot-separator {
    display: none;
  }

  .message-attribution-main a {
    display: block;
  }

  .message-attribution-main a:first-child {
    font-weight: bold;
  }

  .california-message .message-signature {
    border-top: 1px solid #d1d1e1;
    border-bottom: 1px solid #f5f5ff;
    margin-bottom: -1px;
  }

  @media (max-width: 900px) {
    .message-cell.message-cell--user {
      background: #e1e4f2;
      border-right: 0;
      border-bottom: 1px solid #d1d1e1;
      padding: 6px;
    }
    .message:not(.message--forceColumns) .message-cell.message-cell--main {
      padding: 6px;
    }

    .message-attribution {
      margin: -6px;
    }

    .message-attribution-main {
      padding: 0;
    }
  }

  @media (max-width: 650px) {
    .structItem-cell {
      border: 0;
    }

    div.reply-count-cell,
    div.last-post-cell {
      background: #f5f5ff;
    }

    .structItem--thread {
      padding: 6px;
    }

    .reply-count {
      padding-left: 6px;
    }
  }
}
