css:
@-moz-document domain("9tensu.com") {
/* BUGS 
* Pagination in event archive can't be styalized, but I don't use it
*/
:root {
    /* Variables */
    --imgTime: 0.10s;
    --imgAnim: cubic-bezier(0.5, 1, 0.89, 1);
    --brdrRad: 15px;

    --clrHighlight: #7edcff;
}

* {
    transition: all var(--imgTime) var(--imgAnim);
}

h1 {
    font-weight: 700 !important;
}

body {
    background: #121212;
}

.wrapper {
    width: auto;
}
#header {
    background: #1c1c1c;
    border-radius: var(--brdrRad);
}

/* Nav bar */
#header-section .PageList {
    margin-bottom: 30px;
    background: #1c1c1c;
    border-radius: var(--brdrRad);
}

/* Nav button */
.main-menu ul.menu > li > a {
    border-radius: var(--brdrRad) var(--brdrRad) 0px 0px;
}
.main-menu ul.menu > li:hover > a {
    color: #1c1c1c;
    background: var(--clrHighlight);
    transform: translatey(0px);

    transition: all var(--imgTime) var(--imgAnim);
}

/* Nav button arrow */
.main-menu ul.menu > li > a .arrow {
    transition: all var(--imgTime) var(--imgAnim);
}
.main-menu ul.menu > li:hover > a .arrow {
    transform: translatey(10px);
}

/* Nav button menu */
.sub-menu {
    border-radius: 0px var(--brdrRad) var(--brdrRad) var(--brdrRad);
}
.main-menu li > .menu-item-inner {

    transition: all var(--imgTime) var(--imgAnim);

    transform: translatey(-10px);
    border-radius: var(--brdrRad);
}
.main-menu li:hover > .menu-item-inner {
    transform: translatey(0px);
    visibility: visible;
}

/* Search toggle */
#search-toggle .inner:hover .color {
    /* transform: scale(1.2,1.2); */
}
/* Search icon */
#search-toggle .color {
    color: #c0c0c0;
}
/* Search text */
#search-toggle .inner:hover > span {
    color: #fff;
    transform: scale(1.2, 1.2);
}

/* Search overlay */
.search-form-wrapper {
    /* Feature is not available yet */
    /* backdrop-filter: blur(10px); */
}
/* Input */
.search-text {
    font-weight: 700 !important;
}
/*Input bar */
.search-text,
form input[type="text"].search-text {

    width: 80%;
    margin: 20px auto 0;
    border: none;
    font-size: 80px;
    outline: 0;
    min-width: 300px;
    background-color: #1c1c1c;
    height: 175px;
    border-radius: 15px;
}
/* Sidebar */
#main-sidebar .widget {
    background: #222222;
    padding: 20px;
    margin-top: 20px;
    border-radius: var(--brdrRad);
}
/* Sidebar item background*/
.feed.widget.one .item {
    /* Remove white background */
    background: #0000;
}
/* Sidebar item */
#main-sidebar .feed.widget.one .item-thumbnail {
    height: 144px !important;
}
/* 'Releases' item title */
#Label1 .item-content.gradient {
    background: #0000006e;
}
#Label1 .item-content.gradient h3 {
    padding-left: 10px;
    padding-right: 10px;
}
/* 'More' header */
#main-sidebar .widget > h2 {
    color: #eee;
}
/* 'More' item tooltip */
.related-post-style-4 .related-post-item-tooltip {
    background: #444;
    border-radius: var(--brdrRad);
    border: 0;
}
.related-post-style-4 .related-post-item-tooltip a {
    color: #fff;
}

#primary {
    width: 1300px;
    margin: auto;
    padding-left: 50px !important;
    padding-right: 50px !important;
}

.feed.widget.three .three-col .item-0,
.feed.widget.three .three-col .item-1,
.feed.widget.three .three-col .item-2,
.feed.widget.three.fix-height .item-0,
.feed.widget.three.fix-height .item-1,
.feed.widget.three.fix-height .item-2 {
    border-radius: var(--brdrRad);
    background: #1c1c1c;
}

.feed.widget .thumbnail {
    transform: scale(1, 1);
    transition: all var(--imgTime) var(--imgAnim);
}

.feed.widget .item:hover .thumbnail {
    transform: scale(1.10, 1.10);
}

/* Black overlay on images */
.feed.widget .item img {
    transition: all var(--imgTime) var(--imgAnim);
}

.feed.widget .item:hover img {
    opacity: .8 !important;
}

/* Home page slideshow */
.owl-stage-outer {
    border-radius: var(--brdrRad);
}
/* Slideshow content */
.owl-item {
    border-radius: var(--brdrRad);
}
/* Slideshow Title */
.slider-item-content {

    bottom: 25%;
    text-align: center;
    padding: 0 0;
    width: 100% !important;
}
.slider-item .item-title {

    font-weight: 700 !important;
    font-size: 40px
}
/* Slideshow controls */
a.slider-button,
a.carousel-button {
    padding: 11.8% 2%;
    border-radius: var(--brdrRad);

    transition: all var(--imgTime) var(--imgAnim);
}
a.slider-button:hover,
a.carousel-button:hover {
    padding: 11.8% 3%;
    transform: scale(1.5, 1.5);
}
a.carousel-button {
    padding: 7.8% 1.2%;
}
a.carousel-button:hover {
    padding: 7.8% 2%;
}
a.slider-button-left,
a.slider-button-right,
a.carousel-button-left,
a.carousel-button-right {
    color: #fff;
    text-shadow: 2px 2px 8px #000;

    background: #00000000 !important;
}

/* Home page albums */
.feed.widget .item-main {

    overflow: hidden;
    position: relative;
    border-radius: var(--brdrRad);
}
.feed.widget.three .item {
    /* Border fix for search results */
    border-radius: var(--brdrRad);
}

/* Archive top slideshow */
.carousel .item-thumbnail {
    height: 250px;
}

/* Archive header */
.archive-page-header h2 {
    height: 50px;

    padding: 12px;
    margin: 50px 0px 50px 0px;
    border-radius: var(--brdrRad);

    background: #222;
    border: 0;

    font-size: 30px;
    color: white;
    line-height: 50px;
}

/* Archive pagination */
a.archive-page-pagination-button,
a.archive-page-pagination-button.active,
span.archive-page-pagination-separator {
    --size: 80px;
    width: var(--size);
    height: var(--size);

    margin: 10px;
    padding: 0px;
    border-radius: var(--brdrRad);

    line-height: var(--size);
    text-align: center;

    background-color: #303030;

    border: 0;

    font-weight: 700 !important;
    font-size: 24px;

    transition: all var(--imgTime) var(--imgAnim);
}
a.archive-page-pagination-button.active {
    background-color: #404040;
    pointer-events: none;
}
span.archive-page-pagination-separator {
    background-color: #00000000;
}
a.archive-page-pagination-button:hover {
    transform: scale(1.1, 1.1);
}



/* Main Block */
#primary {
    background: #1c1c1c;
    border-radius: var(--brdrRad);

    padding: 25px;
}

#content {
    background: #1c1c1c;
    border-radius: var(--brdrRad);
}

.post-body {
    /* Remove white background behind posts */
    background: #0000;
}

/* General text */
li {
    color: rgba(255, 255, 255, .8);
}

/* Post breadcrumbs */
.post-breadcrumb {
    border-radius: var(--brdrRad);
    background: #222;
    height: 50px;
    line-height: 50px;

    margin-top: 20px;
    margin-bottom: 20px;
    padding-left: 20px;
    padding-right: 20px;
}

/* Post title */
h1.post-title {
    font-size: 25px;
    background: #222;
    color: white;
    font-weight: bold;
    padding: 10px 20px 0px 20px;
    border: 0;
    border-radius: var(--brdrRad) var(--brdrRad) 0px 0px;
}
/* Post title meta */
.post-meta-wrapper {
    background: #222;
    border: 0;
    padding: 0px 20px 10px 20px;
    border-radius: 0px 0px var(--brdrRad) var(--brdrRad);
}
.post-meta-wrapper a {
    color: #c0c0c0;
}

/* Post info header icon */
#postinfostyle {
    /* Icon has white backround, this value comes very close to making the backound invisible */
    filter: invert(87%);
}
/* post info header */
#postinfo {
    /* Black because icon has invert filter */
    color: #000;
}
/* Post info */
.widget .post-body ul {
    border: 0;
    background: #2a2a2a;
    color: #fff;
    height: 84px;
    padding: 20px;
    border-radius: var(--brdrRad);
}
/* Preview header icon */
#previewstyle {
    border: 0;
    background: 0;
}
/* Preview header */
#previewdemo {
    background: #333;
    width: 200px
}
/* Tracklist header */
#tracklist {
    background: #333;
    width: 200px;
    text-align: center;
}
/* Download bar */
#Download-bar {
    background: #333;
    border-radius: var(--brdrRad);
    border: 0;
    height: 50px;
    line-height: 50px;
    padding-left: 25px;
    padding-right: 25px;
}
/* Download link */
#Download-bar a:hover {
    background: #EBEBEB00;
    color: var(--clrHighlight);
}

/* Cover art */
.post-feature-image-wrapper img {
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
    border-radius: var(--brdrRad);
}

.post-body-inner {
    background: #222222;
    padding: 20px;
    border-radius: var(--brdrRad);
}

/* Back to top */
a.scroll-up {
    color: #fff;
    background: #222;
    border-radius: var(--brdrRad);
}
a.scroll-up:hover {
    background: var(--clrHighlight);
    transform: scale(1.1, 1.2);
}

/* Disqus */
#comments {
    background: #222;
    border-radius: var(--brdrRad);
    height: 0px;
    transform: scaley(0);
}
#comments > * {
    color: #fff !important;
}

/* Footer */
/* Replies header */
#footer .title {
    height: 50px;

    padding: 12px;
    margin: 20px 0px 20px 0px;
    border-radius: var(--brdrRad);

    background: #222;
    border: 0;

    text-align: center;
    font-size: 30px;
    color: white;
    line-height: 50px;
}
/* Replies content */
#footer .widget-content {
    background: #1c1c1c;
    color: #fff;
    border-radius: var(--brdrRad);
}
#RecentComments {
    padding: 40px;
}
/* Real footer */
.footer-inner {
    margin-top: 10px;
    padding: 12px 145px 12px 145px;
    background: #1c1c1c;
    border-radius: var(--brdrRad) var(--brdrRad) 0px 0px;
}
}
